hebsuperobject.biblemap.put("b10_10_1", new hebsuperobject(10,10,1,"ויהי אחרי כן וימת מלך בני עמון וימלך חנון בנו תחתיו׃"));
hebsuperobject.biblemap.put("b10_10_2", new hebsuperobject(10,10,2,"ויאמר דוד אעשה חסד עם חנון בן נחש כאשר עשה אביו עמדי חסד וישלח דוד לנחמו ביד עבדיו אל אביו ויבאו עבדי דוד ארץ בני עמון׃"));
hebsuperobject.biblemap.put("b10_10_3", new hebsuperobject(10,10,3,"ויאמרו שרי בני עמון אל חנון אדניהם המכבד דוד את אביך בעיניך כי שלח לך מנחמים הלוא בעבור חקור את העיר ולרגלה ולהפכה שלח דוד את עבדיו אליך׃"));
hebsuperobject.biblemap.put("b10_10_4", new hebsuperobject(10,10,4,"ויקח חנון את עבדי דוד ויגלח את חצי זקנם ויכרת את מדויהם בחצי עד שתותיהם וישלחם׃"));
hebsuperobject.biblemap.put("b10_10_5", new hebsuperobject(10,10,5,"ויגדו לדוד וישלח לקראתם כי היו האנשים נכלמים מאד ויאמר המלך שבו בירחו עד יצמח זקנכם ושבתם׃"));
hebsuperobject.biblemap.put("b10_10_6", new hebsuperobject(10,10,6,"ויראו בני עמון כי נבאשו בדוד וישלחו בני עמון וישכרו את ארם בית רחוב ואת ארם צובא עשרים אלף רגלי ואת מלך מעכה אלף איש ואיש טוב שנים עשר אלף איש׃"));
hebsuperobject.biblemap.put("b10_10_7", new hebsuperobject(10,10,7,"וישמע דוד וישלח את יואב ואת כל הצבא הגברים׃"));
hebsuperobject.biblemap.put("b10_10_8", new hebsuperobject(10,10,8,"ויצאו בני עמון ויערכו מלחמה פתח השער וארם צובא ורחוב ואיש טוב ומעכה לבדם בשדה׃"));
hebsuperobject.biblemap.put("b10_10_9", new hebsuperobject(10,10,9,"וירא יואב כי היתה אליו פני המלחמה מפנים ומאחור ויבחר מכל בחורי בישראל ויערך לקראת ארם׃"));
hebsuperobject.biblemap.put("b10_10_10", new hebsuperobject(10,10,10,"ואת יתר העם נתן ביד אבשי אחיו ויערך לקראת בני עמון׃"));
hebsuperobject.biblemap.put("b10_10_11", new hebsuperobject(10,10,11,"ויאמר אם תחזק ארם ממני והיתה לי לישועה ואם בני עמון יחזקו ממך והלכתי להושיע לך׃"));
hebsuperobject.biblemap.put("b10_10_12", new hebsuperobject(10,10,12,"חזק ונתחזק בעד עמנו ובעד ערי אלהינו ויהוה יעשה הטוב בעיניו׃"));
hebsuperobject.biblemap.put("b10_10_13", new hebsuperobject(10,10,13,"ויגש יואב והעם אשר עמו למלחמה בארם וינסו מפניו׃"));
