hebsuperobject.biblemap.put("b10_18_3", new hebsuperobject(10,18,3,"ויאמר העם לא תצא כי אם נס ננוס לא ישימו אלינו לב ואם ימתו חצינו לא ישימו אלינו לב כי עתה כמנו עשרה אלפים ועתה טוב כי תהיה לנו מעיר לעזיר׃"));
hebsuperobject.biblemap.put("b10_18_4", new hebsuperobject(10,18,4,"ויאמר אליהם המלך אשר ייטב בעיניכם אעשה ויעמד המלך אל יד השער וכל העם יצאו למאות ולאלפים׃"));
hebsuperobject.biblemap.put("b10_18_5", new hebsuperobject(10,18,5,"ויצו המלך את יואב ואת אבישי ואת אתי לאמר לאט לי לנער לאבשלום וכל העם שמעו בצות המלך את כל השרים על דבר אבשלום׃"));
hebsuperobject.biblemap.put("b10_18_6", new hebsuperobject(10,18,6,"ויצא העם השדה לקראת ישראל ותהי המלחמה ביער אפרים׃"));
hebsuperobject.biblemap.put("b10_18_7", new hebsuperobject(10,18,7,"וינגפו שם עם ישראל לפני עבדי דוד ותהי שם המגפה גדולה ביום ההוא עשרים אלף׃"));
hebsuperobject.biblemap.put("b10_18_8", new hebsuperobject(10,18,8,"ותהי שם המלחמה נפצות על פני כל הארץ וירב היער לאכל בעם מאשר אכלה החרב ביום ההוא׃"));
hebsuperobject.biblemap.put("b10_18_9", new hebsuperobject(10,18,9,"ויקרא אבשלום לפני עבדי דוד ואבשלום רכב על הפרד ויבא הפרד תחת שובך האלה הגדולה ויחזק ראשו באלה ויתן בין השמים ובין הארץ והפרד אשר תחתיו עבר׃"));
hebsuperobject.biblemap.put("b10_18_10", new hebsuperobject(10,18,10,"וירא איש אחד ויגד ליואב ויאמר הנה ראיתי את אבשלם תלוי באלה׃"));
hebsuperobject.biblemap.put("b10_18_11", new hebsuperobject(10,18,11,"ויאמר יואב לאיש המגיד לו והנה ראית ומדוע לא הכיתו שם ארצה ועלי לתת לך עשרה כסף וחגרה אחת׃"));
hebsuperobject.biblemap.put("b10_18_12", new hebsuperobject(10,18,12,"ויאמר האיש אל יואב ולא אנכי שקל על כפי אלף כסף לא אשלח ידי אל בן המלך כי באזנינו צוה המלך אתך ואת אבישי ואת אתי לאמר שמרו מי בנער באבשלום׃"));
hebsuperobject.biblemap.put("b10_18_13", new hebsuperobject(10,18,13,"או עשיתי בנפשו שקר וכל דבר לא יכחד מן המלך ואתה תתיצב מנגד׃"));
hebsuperobject.biblemap.put("b10_18_14", new hebsuperobject(10,18,14,"ויאמר יואב לא כן אחילה לפניך ויקח שלשה שבטים בכפו ויתקעם בלב אבשלום עודנו חי בלב האלה׃"));
hebsuperobject.biblemap.put("b10_18_15", new hebsuperobject(10,18,15,"ויסבו עשרה נערים נשאי כלי יואב ויכו את אבשלום וימיתהו׃"));
