hebsuperobject.biblemap.put("b10_18_17", new hebsuperobject(10,18,17,"ויקחו את אבשלום וישליכו אתו ביער אל הפחת הגדול ויצבו עליו גל אבנים גדול מאד וכל ישראל נסו איש לאהלו׃"));
hebsuperobject.biblemap.put("b10_18_18", new hebsuperobject(10,18,18,"ואבשלם לקח ויצב לו בחיו את מצבת אשר בעמק המלך כי אמר אין לי בן בעבור הזכיר שמי ויקרא למצבת על שמו ויקרא לה יד אבשלם עד היום הזה׃"));
hebsuperobject.biblemap.put("b10_18_19", new hebsuperobject(10,18,19,"ואחימעץ בן צדוק אמר ארוצה נא ואבשרה את המלך כי שפטו יהוה מיד איביו׃"));
hebsuperobject.biblemap.put("b10_18_20", new hebsuperobject(10,18,20,"ויאמר לו יואב לא איש בשרה אתה היום הזה ובשרת ביום אחר והיום הזה לא תבשר כי על בן המלך מת׃"));
hebsuperobject.biblemap.put("b10_18_21", new hebsuperobject(10,18,21,"ויאמר יואב לכושי לך הגד למלך אשר ראיתה וישתחו כושי ליואב וירץ׃"));
hebsuperobject.biblemap.put("b10_18_22", new hebsuperobject(10,18,22,"ויסף עוד אחימעץ בן צדוק ויאמר אל יואב ויהי מה ארצה נא גם אני אחרי הכושי ויאמר יואב למה זה אתה רץ בני ולכה אין בשורה מצאת׃"));
hebsuperobject.biblemap.put("b10_18_23", new hebsuperobject(10,18,23,"ויהי מה ארוץ ויאמר לו רוץ וירץ אחימעץ דרך הככר ויעבר את הכושי׃"));
hebsuperobject.biblemap.put("b10_18_24", new hebsuperobject(10,18,24,"ודוד יושב בין שני השערים וילך הצפה אל גג השער אל החומה וישא את עיניו וירא והנה איש רץ לבדו׃"));
hebsuperobject.biblemap.put("b10_18_25", new hebsuperobject(10,18,25,"ויקרא הצפה ויגד למלך ויאמר המלך אם לבדו בשורה בפיו וילך הלוך וקרב׃"));
hebsuperobject.biblemap.put("b10_18_26", new hebsuperobject(10,18,26,"וירא הצפה איש אחר רץ ויקרא הצפה אל השער ויאמר הנה איש רץ לבדו ויאמר המלך גם זה מבשר׃"));
hebsuperobject.biblemap.put("b10_18_27", new hebsuperobject(10,18,27,"ויאמר הצפה אני ראה את מרוצת הראשון כמרצת אחימעץ בן צדוק ויאמר המלך איש טוב זה ואל בשורה טובה יבוא׃"));
hebsuperobject.biblemap.put("b10_18_28", new hebsuperobject(10,18,28,"ויקרא אחימעץ ויאמר אל המלך שלום וישתחו למלך לאפיו ארצה ויאמר ברוך יהוה אלהיך אשר סגר את האנשים אשר נשאו את ידם באדני המלך׃"));
hebsuperobject.biblemap.put("b10_18_29", new hebsuperobject(10,18,29,"ויאמר המלך שלום לנער לאבשלום ויאמר אחימעץ ראיתי ההמון הגדול לשלח את עבד המלך יואב ואת עבדך ולא ידעתי מה׃"));
hebsuperobject.biblemap.put("b10_18_30", new hebsuperobject(10,18,30,"ויאמר המלך סב התיצב כה ויסב ויעמד׃"));
hebsuperobject.biblemap.put("b10_18_31", new hebsuperobject(10,18,31,"והנה הכושי בא ויאמר הכושי יתבשר אדני המלך כי שפטך יהוה היום מיד כל הקמים עליך׃"));
hebsuperobject.biblemap.put("b10_18_32", new hebsuperobject(10,18,32,"ויאמר המלך אל הכושי השלום לנער לאבשלום ויאמר הכושי יהיו כנער איבי אדני המלך וכל אשר קמו עליך לרעה׃"));
hebsuperobject.biblemap.put("b10_18_33", new hebsuperobject(10,18,33,"וירגז המלך ויעל על עלית השער ויבך וכה אמר בלכתו בני אבשלום בני בני אבשלום מי יתן מותי אני תחתיך אבשלום בני בני׃"));
hebsuperobject.biblemap.put("b10_19_1", new hebsuperobject(10,19,1,"ויגד ליואב הנה המלך בכה ויתאבל על אבשלם׃"));
