hebsuperobject.biblemap.put("b10_19_3", new hebsuperobject(10,19,3,"ויתגנב העם ביום ההוא לבוא העיר כאשר יתגנב העם הנכלמים בנוסם במלחמה׃"));
hebsuperobject.biblemap.put("b10_19_4", new hebsuperobject(10,19,4,"והמלך לאט את פניו ויזעק המלך קול גדול בני אבשלום אבשלום בני בני׃"));
hebsuperobject.biblemap.put("b10_19_5", new hebsuperobject(10,19,5,"ויבא יואב אל המלך הבית ויאמר הבשת היום את פני כל עבדיך הממלטים את נפשך היום ואת נפש בניך ובנתיך ונפש נשיך ונפש פלגשיך׃"));
hebsuperobject.biblemap.put("b10_19_6", new hebsuperobject(10,19,6,"לאהבה את שנאיך ולשנא את אהביך כי הגדת היום כי אין לך שרים ועבדים כי ידעתי היום כי לא אבשלום חי וכלנו היום מתים כי אז ישר בעיניך׃"));
