hebsuperobject.biblemap.put("b10_19_8", new hebsuperobject(10,19,8,"ויקם המלך וישב בשער ולכל העם הגידו לאמר הנה המלך יושב בשער ויבא כל העם לפני המלך וישראל נס איש לאהליו׃"));
hebsuperobject.biblemap.put("b10_19_9", new hebsuperobject(10,19,9,"ויהי כל העם נדון בכל שבטי ישראל לאמר המלך הצילנו מכף איבינו והוא מלטנו מכף פלשתים ועתה ברח מן הארץ מעל אבשלום׃"));
hebsuperobject.biblemap.put("b10_19_10", new hebsuperobject(10,19,10,"ואבשלום אשר משחנו עלינו מת במלחמה ועתה למה אתם מחרשים להשיב את המלך׃"));
