hebsuperobject.biblemap.put("b10_19_12", new hebsuperobject(10,19,12,"אחי אתם עצמי ובשרי אתם ולמה תהיו אחרנים להשיב את המלך׃"));
hebsuperobject.biblemap.put("b10_19_13", new hebsuperobject(10,19,13,"ולעמשא תמרו הלוא עצמי ובשרי אתה כה יעשה לי אלהים וכה יוסיף אם לא שר צבא תהיה לפני כל הימים תחת יואב׃"));
hebsuperobject.biblemap.put("b10_19_14", new hebsuperobject(10,19,14,"ויט את לבב כל איש יהודה כאיש אחד וישלחו אל המלך שוב אתה וכל עבדיך׃"));
hebsuperobject.biblemap.put("b10_19_15", new hebsuperobject(10,19,15,"וישב המלך ויבא עד הירדן ויהודה בא הגלגלה ללכת לקראת המלך להעביר את המלך את הירדן׃"));
hebsuperobject.biblemap.put("b10_19_16", new hebsuperobject(10,19,16,"וימהר שמעי בן גרא בן הימיני אשר מבחורים וירד עם איש יהודה לקראת המלך דוד׃"));
hebsuperobject.biblemap.put("b10_19_17", new hebsuperobject(10,19,17,"ואלף איש עמו מבנימן וציבא נער בית שאול וחמשת עשר בניו ועשרים עבדיו אתו וצלחו הירדן לפני המלך׃"));
hebsuperobject.biblemap.put("b10_19_18", new hebsuperobject(10,19,18,"ועברה העברה לעביר את בית המלך ולעשות הטוב בעינו ושמעי בן גרא נפל לפני המלך בעברו בירדן׃"));
hebsuperobject.biblemap.put("b10_19_19", new hebsuperobject(10,19,19,"ויאמר אל המלך אל יחשב לי אדני עון ואל תזכר את אשר העוה עבדך ביום אשר יצא אדני המלך מירושלם לשום המלך אל לבו׃"));
hebsuperobject.biblemap.put("b10_19_20", new hebsuperobject(10,19,20,"כי ידע עבדך כי אני חטאתי והנה באתי היום ראשון לכל בית יוסף לרדת לקראת אדני המלך׃"));
hebsuperobject.biblemap.put("b10_19_21", new hebsuperobject(10,19,21,"ויען אבישי בן צרויה ויאמר התחת זאת לא יומת שמעי כי קלל את משיח יהוה׃"));
hebsuperobject.biblemap.put("b10_19_22", new hebsuperobject(10,19,22,"ויאמר דוד מה לי ולכם בני צרויה כי תהיו לי היום לשטן היום יומת איש בישראל כי הלוא ידעתי כי היום אני מלך על ישראל׃"));
hebsuperobject.biblemap.put("b10_19_23", new hebsuperobject(10,19,23,"ויאמר המלך אל שמעי לא תמות וישבע לו המלך׃"));
hebsuperobject.biblemap.put("b10_19_24", new hebsuperobject(10,19,24,"ומפבשת בן שאול ירד לקראת המלך ולא עשה רגליו ולא עשה שפמו ואת בגדיו לא כבס למן היום לכת המלך עד היום אשר בא בשלום׃"));
hebsuperobject.biblemap.put("b10_19_25", new hebsuperobject(10,19,25,"ויהי כי בא ירושלם לקראת המלך ויאמר לו המלך למה לא הלכת עמי מפיבשת׃"));
hebsuperobject.biblemap.put("b10_19_26", new hebsuperobject(10,19,26,"ויאמר אדני המלך עבדי רמני כי אמר עבדך אחבשה לי החמור וארכב עליה ואלך את המלך כי פסח עבדך׃"));
hebsuperobject.biblemap.put("b10_19_27", new hebsuperobject(10,19,27,"וירגל בעבדך אל אדני המלך ואדני המלך כמלאך האלהים ועשה הטוב בעיניך׃"));
hebsuperobject.biblemap.put("b10_19_28", new hebsuperobject(10,19,28,"כי לא היה כל בית אבי כי אם אנשי מות לאדני המלך ותשת את עבדך באכלי שלחנך ומה יש לי עוד צדקה ולזעק עוד אל המלך׃"));
hebsuperobject.biblemap.put("b10_19_29", new hebsuperobject(10,19,29,"ויאמר לו המלך למה תדבר עוד דבריך אמרתי אתה וציבא תחלקו את השדה׃"));
hebsuperobject.biblemap.put("b10_19_30", new hebsuperobject(10,19,30,"ויאמר מפיבשת אל המלך גם את הכל יקח אחרי אשר בא אדני המלך בשלום אל ביתו׃"));
hebsuperobject.biblemap.put("b10_19_31", new hebsuperobject(10,19,31,"וברזלי הגלעדי ירד מרגלים ויעבר את המלך הירדן לשלחו את בירדן׃"));
hebsuperobject.biblemap.put("b10_19_32", new hebsuperobject(10,19,32,"וברזלי זקן מאד בן שמנים שנה והוא כלכל את המלך בשיבתו במחנים כי איש גדול הוא מאד׃"));
hebsuperobject.biblemap.put("b10_19_33", new hebsuperobject(10,19,33,"ויאמר המלך אל ברזלי אתה עבר אתי וכלכלתי אתך עמדי בירושלם׃"));
hebsuperobject.biblemap.put("b10_19_34", new hebsuperobject(10,19,34,"ויאמר ברזלי אל המלך כמה ימי שני חיי כי אעלה את המלך ירושלם׃"));
hebsuperobject.biblemap.put("b10_19_35", new hebsuperobject(10,19,35,"בן שמנים שנה אנכי היום האדע בין טוב לרע אם יטעם עבדך את אשר אכל ואת אשר אשתה אם אשמע עוד בקול שרים ושרות ולמה יהיה עבדך עוד למשא אל אדני המלך׃"));
hebsuperobject.biblemap.put("b10_19_36", new hebsuperobject(10,19,36,"כמעט יעבר עבדך את הירדן את המלך ולמה יגמלני המלך הגמולה הזאת׃"));
hebsuperobject.biblemap.put("b10_19_37", new hebsuperobject(10,19,37,"ישב נא עבדך ואמת בעירי עם קבר אבי ואמי והנה עבדך כמהם יעבר עם אדני המלך ועשה לו את אשר טוב בעיניך׃"));
hebsuperobject.biblemap.put("b10_19_38", new hebsuperobject(10,19,38,"ויאמר המלך אתי יעבר כמהם ואני אעשה לו את הטוב בעיניך וכל אשר תבחר עלי אעשה לך׃"));
hebsuperobject.biblemap.put("b10_19_39", new hebsuperobject(10,19,39,"ויעבר כל העם את הירדן והמלך עבר וישק המלך לברזלי ויברכהו וישב למקמו׃"));
