hebsuperobject.biblemap.put("b11_2_26", new hebsuperobject(11,2,26,"ולאביתר הכהן אמר המלך ענתת לך על שדיך כי איש מות אתה וביום הזה לא אמיתך כי נשאת את ארון אדני יהוה לפני דוד אבי וכי התענית בכל אשר התענה אבי׃"));
hebsuperobject.biblemap.put("b11_2_27", new hebsuperobject(11,2,27,"ויגרש שלמה את אביתר מהיות כהן ליהוה למלא את דבר יהוה אשר דבר על בית עלי בשלה׃"));
hebsuperobject.biblemap.put("b11_2_28", new hebsuperobject(11,2,28,"והשמעה באה עד יואב כי יואב נטה אחרי אדניה ואחרי אבשלום לא נטה וינס יואב אל אהל יהוה ויחזק בקרנות המזבח׃"));
hebsuperobject.biblemap.put("b11_2_29", new hebsuperobject(11,2,29,"ויגד למלך שלמה כי נס יואב אל אהל יהוה והנה אצל המזבח וישלח שלמה את בניהו בן יהוידע לאמר לך פגע בו׃"));
hebsuperobject.biblemap.put("b11_2_30", new hebsuperobject(11,2,30,"ויבא בניהו אל אהל יהוה ויאמר אליו כה אמר המלך צא ויאמר לא כי פה אמות וישב בניהו את המלך דבר לאמר כה דבר יואב וכה ענני׃"));
hebsuperobject.biblemap.put("b11_2_31", new hebsuperobject(11,2,31,"ויאמר לו המלך עשה כאשר דבר ופגע בו וקברתו והסירת דמי חנם אשר שפך יואב מעלי ומעל בית אבי׃"));
hebsuperobject.biblemap.put("b11_2_32", new hebsuperobject(11,2,32,"והשיב יהוה את דמו על ראשו אשר פגע בשני אנשים צדקים וטבים ממנו ויהרגם בחרב ואבי דוד לא ידע את אבנר בן נר שר צבא ישראל ואת עמשא בן יתר שר צבא יהודה׃"));
hebsuperobject.biblemap.put("b11_2_33", new hebsuperobject(11,2,33,"ושבו דמיהם בראש יואב ובראש זרעו לעלם ולדוד ולזרעו ולביתו ולכסאו יהיה שלום עד עולם מעם יהוה׃"));
hebsuperobject.biblemap.put("b11_2_34", new hebsuperobject(11,2,34,"ויעל בניהו בן יהוידע ויפגע בו וימתהו ויקבר בביתו במדבר׃"));
hebsuperobject.biblemap.put("b11_2_35", new hebsuperobject(11,2,35,"ויתן המלך את בניהו בן יהוידע תחתיו על הצבא ואת צדוק הכהן נתן המלך תחת אביתר׃"));
hebsuperobject.biblemap.put("b11_2_36", new hebsuperobject(11,2,36,"וישלח המלך ויקרא לשמעי ויאמר לו בנה לך בית בירושלם וישבת שם ולא תצא משם אנה ואנה׃"));
hebsuperobject.biblemap.put("b11_2_37", new hebsuperobject(11,2,37,"והיה ביום צאתך ועברת את נחל קדרון ידע תדע כי מות תמות דמך יהיה בראשך׃"));
hebsuperobject.biblemap.put("b11_2_38", new hebsuperobject(11,2,38,"ויאמר שמעי למלך טוב הדבר כאשר דבר אדני המלך כן יעשה עבדך וישב שמעי בירושלם ימים רבים׃"));
hebsuperobject.biblemap.put("b11_2_39", new hebsuperobject(11,2,39,"ויהי מקץ שלש שנים ויברחו שני עבדים לשמעי אל אכיש בן מעכה מלך גת ויגידו לשמעי לאמר הנה עבדיך בגת׃"));
hebsuperobject.biblemap.put("b11_2_40", new hebsuperobject(11,2,40,"ויקם שמעי ויחבש את חמרו וילך גתה אל אכיש לבקש את עבדיו וילך שמעי ויבא את עבדיו מגת׃"));
hebsuperobject.biblemap.put("b11_2_41", new hebsuperobject(11,2,41,"ויגד לשלמה כי הלך שמעי מירושלם גת וישב׃"));
hebsuperobject.biblemap.put("b11_2_42", new hebsuperobject(11,2,42,"וישלח המלך ויקרא לשמעי ויאמר אליו הלוא השבעתיך ביהוה ואעד בך לאמר ביום צאתך והלכת אנה ואנה ידע תדע כי מות תמות ותאמר אלי טוב הדבר שמעתי׃"));
hebsuperobject.biblemap.put("b11_2_43", new hebsuperobject(11,2,43,"ומדוע לא שמרת את שבעת יהוה ואת המצוה אשר צויתי עליך׃"));
hebsuperobject.biblemap.put("b11_2_44", new hebsuperobject(11,2,44,"ויאמר המלך אל שמעי אתה ידעת את כל הרעה אשר ידע לבבך אשר עשית לדוד אבי והשיב יהוה את רעתך בראשך׃"));
hebsuperobject.biblemap.put("b11_2_45", new hebsuperobject(11,2,45,"והמלך שלמה ברוך וכסא דוד יהיה נכון לפני יהוה עד עולם׃"));
hebsuperobject.biblemap.put("b11_2_46", new hebsuperobject(11,2,46,"ויצו המלך את בניהו בן יהוידע ויצא ויפגע בו וימת והממלכה נכונה ביד שלמה׃"));
hebsuperobject.biblemap.put("b11_3_1", new hebsuperobject(11,3,1,"ויתחתן שלמה את פרעה מלך מצרים ויקח את בת פרעה ויביאה אל עיר דוד עד כלתו לבנות את ביתו ואת בית יהוה ואת חומת ירושלם סביב׃"));
hebsuperobject.biblemap.put("b11_3_2", new hebsuperobject(11,3,2,"רק העם מזבחים בבמות כי לא נבנה בית לשם יהוה עד הימים ההם׃"));
hebsuperobject.biblemap.put("b11_3_3", new hebsuperobject(11,3,3,"ויאהב שלמה את יהוה ללכת בחקות דוד אביו רק בבמות הוא מזבח ומקטיר׃"));
hebsuperobject.biblemap.put("b11_3_4", new hebsuperobject(11,3,4,"וילך המלך גבענה לזבח שם כי היא הבמה הגדולה אלף עלות יעלה שלמה על המזבח ההוא׃"));
hebsuperobject.biblemap.put("b11_3_5", new hebsuperobject(11,3,5,"בגבעון נראה יהוה אל שלמה בחלום הלילה ויאמר אלהים שאל מה אתן לך׃"));
hebsuperobject.biblemap.put("b11_3_6", new hebsuperobject(11,3,6,"ויאמר שלמה אתה עשית עם עבדך דוד אבי חסד גדול כאשר הלך לפניך באמת ובצדקה ובישרת לבב עמך ותשמר לו את החסד הגדול הזה ותתן לו בן ישב על כסאו כיום הזה׃"));
hebsuperobject.biblemap.put("b11_3_7", new hebsuperobject(11,3,7,"ועתה יהוה אלהי אתה המלכת את עבדך תחת דוד אבי ואנכי נער קטן לא אדע צאת ובא׃"));
hebsuperobject.biblemap.put("b11_3_8", new hebsuperobject(11,3,8,"ועבדך בתוך עמך אשר בחרת עם רב אשר לא ימנה ולא יספר מרב׃"));
hebsuperobject.biblemap.put("b11_3_9", new hebsuperobject(11,3,9,"ונתת לעבדך לב שמע לשפט את עמך להבין בין טוב לרע כי מי יוכל לשפט את עמך הכבד הזה׃"));
hebsuperobject.biblemap.put("b11_3_10", new hebsuperobject(11,3,10,"וייטב הדבר בעיני אדני כי שאל שלמה את הדבר הזה׃"));
hebsuperobject.biblemap.put("b11_3_11", new hebsuperobject(11,3,11,"ויאמר אלהים אליו יען אשר שאלת את הדבר הזה ולא שאלת לך ימים רבים ולא שאלת לך עשר ולא שאלת נפש איביך ושאלת לך הבין לשמע משפט׃"));
hebsuperobject.biblemap.put("b11_3_12", new hebsuperobject(11,3,12,"הנה עשיתי כדבריך הנה נתתי לך לב חכם ונבון אשר כמוך לא היה לפניך ואחריך לא יקום כמוך׃"));
hebsuperobject.biblemap.put("b11_3_13", new hebsuperobject(11,3,13,"וגם אשר לא שאלת נתתי לך גם עשר גם כבוד אשר לא היה כמוך איש במלכים כל ימיך׃"));
hebsuperobject.biblemap.put("b11_3_14", new hebsuperobject(11,3,14,"ואם תלך בדרכי לשמר חקי ומצותי כאשר הלך דויד אביך והארכתי את ימיך׃"));
hebsuperobject.biblemap.put("b11_3_15", new hebsuperobject(11,3,15,"ויקץ שלמה והנה חלום ויבוא ירושלם ויעמד לפני ארון ברית אדני ויעל עלות ויעש שלמים ויעש משתה לכל עבדיו׃"));
hebsuperobject.biblemap.put("b11_3_16", new hebsuperobject(11,3,16,"אז תבאנה שתים נשים זנות אל המלך ותעמדנה לפניו׃"));
hebsuperobject.biblemap.put("b11_3_17", new hebsuperobject(11,3,17,"ותאמר האשה האחת בי אדני אני והאשה הזאת ישבת בבית אחד ואלד עמה בבית׃"));
hebsuperobject.biblemap.put("b11_3_18", new hebsuperobject(11,3,18,"ויהי ביום השלישי ללדתי ותלד גם האשה הזאת ואנחנו יחדו אין זר אתנו בבית זולתי שתים אנחנו בבית׃"));
hebsuperobject.biblemap.put("b11_3_19", new hebsuperobject(11,3,19,"וימת בן האשה הזאת לילה אשר שכבה עליו׃"));
hebsuperobject.biblemap.put("b11_3_20", new hebsuperobject(11,3,20,"ותקם בתוך הלילה ותקח את בני מאצלי ואמתך ישנה ותשכיבהו בחיקה ואת בנה המת השכיבה בחיקי׃"));
hebsuperobject.biblemap.put("b11_3_21", new hebsuperobject(11,3,21,"ואקם בבקר להיניק את בני והנה מת ואתבונן אליו בבקר והנה לא היה בני אשר ילדתי׃"));
hebsuperobject.biblemap.put("b11_3_22", new hebsuperobject(11,3,22,"ותאמר האשה האחרת לא כי בני החי ובנך המת וזאת אמרת לא כי בנך המת ובני החי ותדברנה לפני המלך׃"));
hebsuperobject.biblemap.put("b11_3_23", new hebsuperobject(11,3,23,"ויאמר המלך זאת אמרת זה בני החי ובנך המת וזאת אמרת לא כי בנך המת ובני החי׃"));
hebsuperobject.biblemap.put("b11_3_24", new hebsuperobject(11,3,24,"ויאמר המלך קחו לי חרב ויבאו החרב לפני המלך׃"));
hebsuperobject.biblemap.put("b11_3_25", new hebsuperobject(11,3,25,"ויאמר המלך גזרו את הילד החי לשנים ותנו את החצי לאחת ואת החצי לאחת׃"));
hebsuperobject.biblemap.put("b11_3_26", new hebsuperobject(11,3,26,"ותאמר האשה אשר בנה החי אל המלך כי נכמרו רחמיה על בנה ותאמר בי אדני תנו לה את הילוד החי והמת אל תמיתהו וזאת אמרת גם לי גם לך לא יהיה גזרו׃"));
hebsuperobject.biblemap.put("b11_3_27", new hebsuperobject(11,3,27,"ויען המלך ויאמר תנו לה את הילוד החי והמת לא תמיתהו היא אמו׃"));
hebsuperobject.biblemap.put("b11_3_28", new hebsuperobject(11,3,28,"וישמעו כל ישראל את המשפט אשר שפט המלך ויראו מפני המלך כי ראו כי חכמת אלהים בקרבו לעשות משפט׃"));
hebsuperobject.biblemap.put("b11_4_1", new hebsuperobject(11,4,1,"ויהי המלך שלמה מלך על כל ישראל׃"));
hebsuperobject.biblemap.put("b11_4_2", new hebsuperobject(11,4,2,"ואלה השרים אשר לו עזריהו בן צדוק הכהן׃"));
hebsuperobject.biblemap.put("b11_4_3", new hebsuperobject(11,4,3,"אליחרף ואחיה בני שישא ספרים יהושפט בן אחילוד המזכיר׃"));
hebsuperobject.biblemap.put("b11_4_4", new hebsuperobject(11,4,4,"ובניהו בן יהוידע על הצבא וצדוק ואביתר כהנים׃"));
hebsuperobject.biblemap.put("b11_4_5", new hebsuperobject(11,4,5,"ועזריהו בן נתן על הנצבים וזבוד בן נתן כהן רעה המלך׃"));
hebsuperobject.biblemap.put("b11_4_6", new hebsuperobject(11,4,6,"ואחישר על הבית ואדנירם בן עבדא על המס׃"));
hebsuperobject.biblemap.put("b11_4_7", new hebsuperobject(11,4,7,"ולשלמה שנים עשר נצבים על כל ישראל וכלכלו את המלך ואת ביתו חדש בשנה יהיה על אחד לכלכל׃"));
hebsuperobject.biblemap.put("b11_4_8", new hebsuperobject(11,4,8,"ואלה שמותם בן חור בהר אפרים׃"));
hebsuperobject.biblemap.put("b11_4_9", new hebsuperobject(11,4,9,"בן דקר במקץ ובשעלבים ובית שמש ואילון בית חנן׃"));
hebsuperobject.biblemap.put("b11_4_10", new hebsuperobject(11,4,10,"בן חסד בארבות לו שכה וכל ארץ חפר׃"));
hebsuperobject.biblemap.put("b11_4_11", new hebsuperobject(11,4,11,"בן אבינדב כל נפת דאר טפת בת שלמה היתה לו לאשה׃"));
hebsuperobject.biblemap.put("b11_4_12", new hebsuperobject(11,4,12,"בענא בן אחילוד תענך ומגדו וכל בית שאן אשר אצל צרתנה מתחת ליזרעאל מבית שאן עד אבל מחולה עד מעבר ליקמעם׃"));
hebsuperobject.biblemap.put("b11_4_13", new hebsuperobject(11,4,13,"בן גבר ברמת גלעד לו חות יאיר בן מנשה אשר בגלעד לו חבל ארגב אשר בבשן ששים ערים גדלות חומה ובריח נחשת׃"));
hebsuperobject.biblemap.put("b11_4_14", new hebsuperobject(11,4,14,"אחינדב בן עדא מחנימה׃"));
hebsuperobject.biblemap.put("b11_4_15", new hebsuperobject(11,4,15,"אחימעץ בנפתלי גם הוא לקח את בשמת בת שלמה לאשה׃"));
hebsuperobject.biblemap.put("b11_4_16", new hebsuperobject(11,4,16,"בענא בן חושי באשר ובעלות׃"));
hebsuperobject.biblemap.put("b11_4_17", new hebsuperobject(11,4,17,"יהושפט בן פרוח ביששכר׃"));
hebsuperobject.biblemap.put("b11_4_18", new hebsuperobject(11,4,18,"שמעי בן אלא בבנימן׃"));
hebsuperobject.biblemap.put("b11_4_19", new hebsuperobject(11,4,19,"גבר בן ארי בארץ גלעד ארץ סיחון מלך האמרי ועג מלך הבשן ונציב אחד אשר בארץ׃"));
hebsuperobject.biblemap.put("b11_4_20", new hebsuperobject(11,4,20,"יהודה וישראל רבים כחול אשר על הים לרב אכלים ושתים ושמחים׃"));
hebsuperobject.biblemap.put("b11_4_21", new hebsuperobject(11,4,21,"ושלמה היה מושל בכל הממלכות מן הנהר ארץ פלשתים ועד גבול מצרים מגשים מנחה ועבדים את שלמה כל ימי חייו׃"));
hebsuperobject.biblemap.put("b11_4_22", new hebsuperobject(11,4,22,"ויהי לחם שלמה ליום אחד שלשים כר סלת וששים כר קמח׃"));
hebsuperobject.biblemap.put("b11_4_23", new hebsuperobject(11,4,23,"עשרה בקר בראים ועשרים בקר רעי ומאה צאן לבד מאיל וצבי ויחמור וברברים אבוסים׃"));
hebsuperobject.biblemap.put("b11_4_24", new hebsuperobject(11,4,24,"כי הוא רדה בכל עבר הנהר מתפסח ועד עזה בכל מלכי עבר הנהר ושלום היה לו מכל עבריו מסביב׃"));
hebsuperobject.biblemap.put("b11_4_25", new hebsuperobject(11,4,25,"וישב יהודה וישראל לבטח איש תחת גפנו ותחת תאנתו מדן ועד באר שבע כל ימי שלמה׃"));
hebsuperobject.biblemap.put("b11_4_26", new hebsuperobject(11,4,26,"ויהי לשלמה ארבעים אלף ארות סוסים למרכבו ושנים עשר אלף פרשים׃"));
hebsuperobject.biblemap.put("b11_4_27", new hebsuperobject(11,4,27,"וכלכלו הנצבים האלה את המלך שלמה ואת כל הקרב אל שלחן המלך שלמה איש חדשו לא יעדרו דבר׃"));
hebsuperobject.biblemap.put("b11_4_28", new hebsuperobject(11,4,28,"והשערים והתבן לסוסים ולרכש יבאו אל המקום אשר יהיה שם איש כמשפטו׃"));
hebsuperobject.biblemap.put("b11_4_29", new hebsuperobject(11,4,29,"ויתן אלהים חכמה לשלמה ותבונה הרבה מאד ורחב לב כחול אשר על שפת הים׃"));
hebsuperobject.biblemap.put("b11_4_30", new hebsuperobject(11,4,30,"ותרב חכמת שלמה מחכמת כל בני קדם ומכל חכמת מצרים׃"));
