hebsuperobject.biblemap.put("b11_7_26", new hebsuperobject(11,7,26,"ועביו טפח ושפתו כמעשה שפת כוס פרח שושן אלפים בת יכיל׃"));
hebsuperobject.biblemap.put("b11_7_27", new hebsuperobject(11,7,27,"ויעש את המכנות עשר נחשת ארבע באמה ארך המכונה האחת וארבע באמה רחבה ושלש באמה קומתה׃"));
hebsuperobject.biblemap.put("b11_7_28", new hebsuperobject(11,7,28,"וזה מעשה המכונה מסגרת להם ומסגרת בין השלבים׃"));
