hebsuperobject.biblemap.put("b11_7_30", new hebsuperobject(11,7,30,"וארבעה אופני נחשת למכונה האחת וסרני נחשת וארבעה פעמתיו כתפת להם מתחת לכיר הכתפת יצקות מעבר איש ליות׃"));
hebsuperobject.biblemap.put("b11_7_31", new hebsuperobject(11,7,31,"ופיהו מבית לכתרת ומעלה באמה ופיה עגל מעשה כן אמה וחצי האמה וגם על פיה מקלעות ומסגרתיהם מרבעות לא עגלות׃"));
hebsuperobject.biblemap.put("b11_7_32", new hebsuperobject(11,7,32,"וארבעת האופנים למתחת למסגרות וידות האופנים במכונה וקומת האופן האחד אמה וחצי האמה׃"));
hebsuperobject.biblemap.put("b11_7_33", new hebsuperobject(11,7,33,"ומעשה האופנים כמעשה אופן המרכבה ידותם וגביהם וחשקיהם וחשריהם הכל מוצק׃"));
hebsuperobject.biblemap.put("b11_7_34", new hebsuperobject(11,7,34,"וארבע כתפות אל ארבע פנות המכנה האחת מן המכנה כתפיה׃"));
hebsuperobject.biblemap.put("b11_7_35", new hebsuperobject(11,7,35,"ובראש המכונה חצי האמה קומה עגל סביב ועל ראש המכנה ידתיה ומסגרתיה ממנה׃"));
hebsuperobject.biblemap.put("b11_7_36", new hebsuperobject(11,7,36,"ויפתח על הלחת ידתיה ועל ומסגרתיה כרובים אריות ותמרת כמער איש וליות סביב׃"));
hebsuperobject.biblemap.put("b11_7_37", new hebsuperobject(11,7,37,"כזאת עשה את עשר המכנות מוצק אחד מדה אחת קצב אחד לכלהנה׃"));
hebsuperobject.biblemap.put("b11_7_38", new hebsuperobject(11,7,38,"ויעש עשרה כירות נחשת ארבעים בת יכיל הכיור האחד ארבע באמה הכיור האחד כיור אחד על המכנה האחת לעשר המכונות׃"));
hebsuperobject.biblemap.put("b11_7_39", new hebsuperobject(11,7,39,"ויתן את המכנות חמש על כתף הבית מימין וחמש על כתף הבית משמאלו ואת הים נתן מכתף הבית הימנית קדמה ממול נגב׃"));
hebsuperobject.biblemap.put("b11_7_40", new hebsuperobject(11,7,40,"ויעש חירום את הכירות ואת היעים ואת המזרקות ויכל חירם לעשות את כל המלאכה אשר עשה למלך שלמה בית יהוה׃"));
hebsuperobject.biblemap.put("b11_7_41", new hebsuperobject(11,7,41,"עמדים שנים וגלת הכתרת אשר על ראש העמדים שתים והשבכות שתים לכסות את שתי גלת הכתרת אשר על ראש העמודים׃"));
hebsuperobject.biblemap.put("b11_7_42", new hebsuperobject(11,7,42,"ואת הרמנים ארבע מאות לשתי השבכות שני טורים רמנים לשבכה האחת לכסות את שתי גלת הכתרת אשר על פני העמודים׃"));
hebsuperobject.biblemap.put("b11_7_43", new hebsuperobject(11,7,43,"ואת המכנות עשר ואת הכירת עשרה על המכנות׃"));
