hebsuperobject.biblemap.put("b11_7_45", new hebsuperobject(11,7,45,"ואת הסירות ואת היעים ואת המזרקות ואת כל הכלים האהל אשר עשה חירם למלך שלמה בית יהוה נחשת ממרט׃"));
hebsuperobject.biblemap.put("b11_7_46", new hebsuperobject(11,7,46,"בככר הירדן יצקם המלך במעבה האדמה בין סכות ובין צרתן׃"));
hebsuperobject.biblemap.put("b11_7_47", new hebsuperobject(11,7,47,"וינח שלמה את כל הכלים מרב מאד מאד לא נחקר משקל הנחשת׃"));
hebsuperobject.biblemap.put("b11_7_48", new hebsuperobject(11,7,48,"ויעש שלמה את כל הכלים אשר בית יהוה את מזבח הזהב ואת השלחן אשר עליו לחם הפנים זהב׃"));
hebsuperobject.biblemap.put("b11_7_49", new hebsuperobject(11,7,49,"ואת המנרות חמש מימין וחמש משמאול לפני הדביר זהב סגור והפרח והנרת והמלקחים זהב׃"));
hebsuperobject.biblemap.put("b11_7_50", new hebsuperobject(11,7,50,"והספות והמזמרות והמזרקות והכפות והמחתות זהב סגור והפתות לדלתות הבית הפנימי לקדש הקדשים לדלתי הבית להיכל זהב׃"));
hebsuperobject.biblemap.put("b11_7_51", new hebsuperobject(11,7,51,"ותשלם כל המלאכה אשר עשה המלך שלמה בית יהוה ויבא שלמה את קדשי דוד אביו את הכסף ואת הזהב ואת הכלים נתן באצרות בית יהוה׃"));
hebsuperobject.biblemap.put("b11_8_1", new hebsuperobject(11,8,1,"אז יקהל שלמה את זקני ישראל את כל ראשי המטות נשיאי האבות לבני ישראל אל המלך שלמה ירושלם להעלות את ארון ברית יהוה מעיר דוד היא ציון׃"));
hebsuperobject.biblemap.put("b11_8_2", new hebsuperobject(11,8,2,"ויקהלו אל המלך שלמה כל איש ישראל בירח האתנים בחג הוא החדש השביעי׃"));
hebsuperobject.biblemap.put("b11_8_3", new hebsuperobject(11,8,3,"ויבאו כל זקני ישראל וישאו הכהנים את הארון׃"));
