hebsuperobject.biblemap.put("b11_8_5", new hebsuperobject(11,8,5,"והמלך שלמה וכל עדת ישראל הנועדים עליו אתו לפני הארון מזבחים צאן ובקר אשר לא יספרו ולא ימנו מרב׃"));
hebsuperobject.biblemap.put("b11_8_6", new hebsuperobject(11,8,6,"ויבאו הכהנים את ארון ברית יהוה אל מקומו אל דביר הבית אל קדש הקדשים אל תחת כנפי הכרובים׃"));
hebsuperobject.biblemap.put("b11_8_7", new hebsuperobject(11,8,7,"כי הכרובים פרשים כנפים אל מקום הארון ויסכו הכרבים על הארון ועל בדיו מלמעלה׃"));
hebsuperobject.biblemap.put("b11_8_8", new hebsuperobject(11,8,8,"ויארכו הבדים ויראו ראשי הבדים מן הקדש על פני הדביר ולא יראו החוצה ויהיו שם עד היום הזה׃"));
hebsuperobject.biblemap.put("b11_8_9", new hebsuperobject(11,8,9,"אין בארון רק שני לחות האבנים אשר הנח שם משה בחרב אשר כרת יהוה עם בני ישראל בצאתם מארץ מצרים׃"));
hebsuperobject.biblemap.put("b11_8_10", new hebsuperobject(11,8,10,"ויהי בצאת הכהנים מן הקדש והענן מלא את בית יהוה׃"));
