hebsuperobject.biblemap.put("b11_8_20", new hebsuperobject(11,8,20,"ויקם יהוה את דברו אשר דבר ואקם תחת דוד אבי ואשב על כסא ישראל כאשר דבר יהוה ואבנה הבית לשם יהוה אלהי ישראל׃"));
hebsuperobject.biblemap.put("b11_8_21", new hebsuperobject(11,8,21,"ואשם שם מקום לארון אשר שם ברית יהוה אשר כרת עם אבתינו בהוציאו אתם מארץ מצרים׃"));
hebsuperobject.biblemap.put("b11_8_22", new hebsuperobject(11,8,22,"ויעמד שלמה לפני מזבח יהוה נגד כל קהל ישראל ויפרש כפיו השמים׃"));
hebsuperobject.biblemap.put("b11_8_23", new hebsuperobject(11,8,23,"ויאמר יהוה אלהי ישראל אין כמוך אלהים בשמים ממעל ועל הארץ מתחת שמר הברית והחסד לעבדיך ההלכים לפניך בכל לבם׃"));
hebsuperobject.biblemap.put("b11_8_24", new hebsuperobject(11,8,24,"אשר שמרת לעבדך דוד אבי את אשר דברת לו ותדבר בפיך ובידך מלאת כיום הזה׃"));
hebsuperobject.biblemap.put("b11_8_25", new hebsuperobject(11,8,25,"ועתה יהוה אלהי ישראל שמר לעבדך דוד אבי את אשר דברת לו לאמר לא יכרת לך איש מלפני ישב על כסא ישראל רק אם ישמרו בניך את דרכם ללכת לפני כאשר הלכת לפני׃"));
hebsuperobject.biblemap.put("b11_8_26", new hebsuperobject(11,8,26,"ועתה אלהי ישראל יאמן נא דבריך אשר דברת לעבדך דוד אבי׃"));
