hebsuperobject.biblemap.put("b11_8_28", new hebsuperobject(11,8,28,"ופנית אל תפלת עבדך ואל תחנתו יהוה אלהי לשמע אל הרנה ואל התפלה אשר עבדך מתפלל לפניך היום׃"));
hebsuperobject.biblemap.put("b11_8_29", new hebsuperobject(11,8,29,"להיות עינך פתחות אל הבית הזה לילה ויום אל המקום אשר אמרת יהיה שמי שם לשמע אל התפלה אשר יתפלל עבדך אל המקום הזה׃"));
hebsuperobject.biblemap.put("b11_8_30", new hebsuperobject(11,8,30,"ושמעת אל תחנת עבדך ועמך ישראל אשר יתפללו אל המקום הזה ואתה תשמע אל מקום שבתך אל השמים ושמעת וסלחת׃"));
hebsuperobject.biblemap.put("b11_8_31", new hebsuperobject(11,8,31,"את אשר יחטא איש לרעהו ונשא בו אלה להאלתו ובא אלה לפני מזבחך בבית הזה׃"));
hebsuperobject.biblemap.put("b11_8_32", new hebsuperobject(11,8,32,"ואתה תשמע השמים ועשית ושפטת את עבדיך להרשיע רשע לתת דרכו בראשו ולהצדיק צדיק לתת לו כצדקתו׃"));
hebsuperobject.biblemap.put("b11_8_33", new hebsuperobject(11,8,33,"בהנגף עמך ישראל לפני אויב אשר יחטאו לך ושבו אליך והודו את שמך והתפללו והתחננו אליך בבית הזה׃"));
hebsuperobject.biblemap.put("b11_8_34", new hebsuperobject(11,8,34,"ואתה תשמע השמים וסלחת לחטאת עמך ישראל והשבתם אל האדמה אשר נתת לאבותם׃"));
hebsuperobject.biblemap.put("b11_8_35", new hebsuperobject(11,8,35,"בהעצר שמים ולא יהיה מטר כי יחטאו לך והתפללו אל המקום הזה והודו את שמך ומחטאתם ישובון כי תענם׃"));
hebsuperobject.biblemap.put("b11_8_36", new hebsuperobject(11,8,36,"ואתה תשמע השמים וסלחת לחטאת עבדיך ועמך ישראל כי תורם את הדרך הטובה אשר ילכו בה ונתתה מטר על ארצך אשר נתתה לעמך לנחלה׃"));
hebsuperobject.biblemap.put("b11_8_37", new hebsuperobject(11,8,37,"רעב כי יהיה בארץ דבר כי יהיה שדפון ירקון ארבה חסיל כי יהיה כי יצר לו איבו בארץ שעריו כל נגע כל מחלה׃"));
hebsuperobject.biblemap.put("b11_8_38", new hebsuperobject(11,8,38,"כל תפלה כל תחנה אשר תהיה לכל האדם לכל עמך ישראל אשר ידעון איש נגע לבבו ופרש כפיו אל הבית הזה׃"));
hebsuperobject.biblemap.put("b11_8_39", new hebsuperobject(11,8,39,"ואתה תשמע השמים מכון שבתך וסלחת ועשית ונתת לאיש ככל דרכיו אשר תדע את לבבו כי אתה ידעת לבדך את לבב כל בני האדם׃"));
hebsuperobject.biblemap.put("b11_8_40", new hebsuperobject(11,8,40,"למען יראוך כל הימים אשר הם חיים על פני האדמה אשר נתתה לאבתינו׃"));
hebsuperobject.biblemap.put("b11_8_41", new hebsuperobject(11,8,41,"וגם אל הנכרי אשר לא מעמך ישראל הוא ובא מארץ רחוקה למען שמך׃"));
hebsuperobject.biblemap.put("b11_8_42", new hebsuperobject(11,8,42,"כי ישמעון את שמך הגדול ואת ידך החזקה וזרעך הנטויה ובא והתפלל אל הבית הזה׃"));
hebsuperobject.biblemap.put("b11_8_43", new hebsuperobject(11,8,43,"אתה תשמע השמים מכון שבתך ועשית ככל אשר יקרא אליך הנכרי למען ידעון כל עמי הארץ את שמך ליראה אתך כעמך ישראל ולדעת כי שמך נקרא על הבית הזה אשר בניתי׃"));
hebsuperobject.biblemap.put("b11_8_44", new hebsuperobject(11,8,44,"כי יצא עמך למלחמה על איבו בדרך אשר תשלחם והתפללו אל יהוה דרך העיר אשר בחרת בה והבית אשר בנתי לשמך׃"));
hebsuperobject.biblemap.put("b11_8_45", new hebsuperobject(11,8,45,"ושמעת השמים את תפלתם ואת תחנתם ועשית משפטם׃"));
hebsuperobject.biblemap.put("b11_8_46", new hebsuperobject(11,8,46,"כי יחטאו לך כי אין אדם אשר לא יחטא ואנפת בם ונתתם לפני אויב ושבום שביהם אל ארץ האויב רחוקה או קרובה׃"));
hebsuperobject.biblemap.put("b11_8_47", new hebsuperobject(11,8,47,"והשיבו אל לבם בארץ אשר נשבו שם ושבו והתחננו אליך בארץ שביהם לאמר חטאנו והעוינו רשענו׃"));
hebsuperobject.biblemap.put("b11_8_48", new hebsuperobject(11,8,48,"ושבו אליך בכל לבבם ובכל נפשם בארץ איביהם אשר שבו אתם והתפללו אליך דרך ארצם אשר נתתה לאבותם העיר אשר בחרת והבית אשר בנית לשמך׃"));
hebsuperobject.biblemap.put("b11_8_49", new hebsuperobject(11,8,49,"ושמעת השמים מכון שבתך את תפלתם ואת תחנתם ועשית משפטם׃"));
hebsuperobject.biblemap.put("b11_8_50", new hebsuperobject(11,8,50,"וסלחת לעמך אשר חטאו לך ולכל פשעיהם אשר פשעו בך ונתתם לרחמים לפני שביהם ורחמום׃"));
hebsuperobject.biblemap.put("b11_8_51", new hebsuperobject(11,8,51,"כי עמך ונחלתך הם אשר הוצאת ממצרים מתוך כור הברזל׃"));
hebsuperobject.biblemap.put("b11_8_52", new hebsuperobject(11,8,52,"להיות עיניך פתחות אל תחנת עבדך ואל תחנת עמך ישראל לשמע אליהם בכל קראם אליך׃"));
hebsuperobject.biblemap.put("b11_8_53", new hebsuperobject(11,8,53,"כי אתה הבדלתם לך לנחלה מכל עמי הארץ כאשר דברת ביד משה עבדך בהוציאך את אבתינו ממצרים אדני יהוה׃"));
hebsuperobject.biblemap.put("b11_8_54", new hebsuperobject(11,8,54,"ויהי ככלות שלמה להתפלל אל יהוה את כל התפלה והתחנה הזאת קם מלפני מזבח יהוה מכרע על ברכיו וכפיו פרשות השמים׃"));
hebsuperobject.biblemap.put("b11_8_55", new hebsuperobject(11,8,55,"ויעמד ויברך את כל קהל ישראל קול גדול לאמר׃"));
hebsuperobject.biblemap.put("b11_8_56", new hebsuperobject(11,8,56,"ברוך יהוה אשר נתן מנוחה לעמו ישראל ככל אשר דבר לא נפל דבר אחד מכל דברו הטוב אשר דבר ביד משה עבדו׃"));
hebsuperobject.biblemap.put("b11_8_57", new hebsuperobject(11,8,57,"יהי יהוה אלהינו עמנו כאשר היה עם אבתינו אל יעזבנו ואל יטשנו׃"));
hebsuperobject.biblemap.put("b11_8_58", new hebsuperobject(11,8,58,"להטות לבבנו אליו ללכת בכל דרכיו ולשמר מצותיו וחקיו ומשפטיו אשר צוה את אבתינו׃"));
hebsuperobject.biblemap.put("b11_8_59", new hebsuperobject(11,8,59,"ויהיו דברי אלה אשר התחננתי לפני יהוה קרבים אל יהוה אלהינו יומם ולילה לעשות משפט עבדו ומשפט עמו ישראל דבר יום ביומו׃"));
hebsuperobject.biblemap.put("b11_8_60", new hebsuperobject(11,8,60,"למען דעת כל עמי הארץ כי יהוה הוא האלהים אין עוד׃"));
hebsuperobject.biblemap.put("b11_8_61", new hebsuperobject(11,8,61,"והיה לבבכם שלם עם יהוה אלהינו ללכת בחקיו ולשמר מצותיו כיום הזה׃"));
hebsuperobject.biblemap.put("b11_8_62", new hebsuperobject(11,8,62,"והמלך וכל ישראל עמו זבחים זבח לפני יהוה׃"));
hebsuperobject.biblemap.put("b11_8_63", new hebsuperobject(11,8,63,"ויזבח שלמה את זבח השלמים אשר זבח ליהוה בקר עשרים ושנים אלף וצאן מאה ועשרים אלף ויחנכו את בית יהוה המלך וכל בני ישראל׃"));
hebsuperobject.biblemap.put("b11_8_64", new hebsuperobject(11,8,64,"ביום ההוא קדש המלך את תוך החצר אשר לפני בית יהוה כי עשה שם את העלה ואת המנחה ואת חלבי השלמים כי מזבח הנחשת אשר לפני יהוה קטן מהכיל את העלה ואת המנחה ואת חלבי השלמים׃"));
hebsuperobject.biblemap.put("b11_8_65", new hebsuperobject(11,8,65,"ויעש שלמה בעת ההיא את החג וכל ישראל עמו קהל גדול מלבוא חמת עד נחל מצרים לפני יהוה אלהינו שבעת ימים ושבעת ימים ארבעה עשר יום׃"));
hebsuperobject.biblemap.put("b11_8_66", new hebsuperobject(11,8,66,"ביום השמיני שלח את העם ויברכו את המלך וילכו לאהליהם שמחים וטובי לב על כל הטובה אשר עשה יהוה לדוד עבדו ולישראל עמו׃"));
hebsuperobject.biblemap.put("b11_9_1", new hebsuperobject(11,9,1,"ויהי ככלות שלמה לבנות את בית יהוה ואת בית המלך ואת כל חשק שלמה אשר חפץ לעשות׃"));
hebsuperobject.biblemap.put("b11_9_2", new hebsuperobject(11,9,2,"וירא יהוה אל שלמה שנית כאשר נראה אליו בגבעון׃"));
hebsuperobject.biblemap.put("b11_9_3", new hebsuperobject(11,9,3,"ויאמר יהוה אליו שמעתי את תפלתך ואת תחנתך אשר התחננתה לפני הקדשתי את הבית הזה אשר בנתה לשום שמי שם עד עולם והיו עיני ולבי שם כל הימים׃"));
hebsuperobject.biblemap.put("b11_9_4", new hebsuperobject(11,9,4,"ואתה אם תלך לפני כאשר הלך דוד אביך בתם לבב ובישר לעשות ככל אשר צויתיך חקי ומשפטי תשמר׃"));
hebsuperobject.biblemap.put("b11_9_5", new hebsuperobject(11,9,5,"והקמתי את כסא ממלכתך על ישראל לעלם כאשר דברתי על דוד אביך לאמר לא יכרת לך איש מעל כסא ישראל׃"));
hebsuperobject.biblemap.put("b11_9_6", new hebsuperobject(11,9,6,"אם שוב תשבון אתם ובניכם מאחרי ולא תשמרו מצותי חקתי אשר נתתי לפניכם והלכתם ועבדתם אלהים אחרים והשתחויתם להם׃"));
hebsuperobject.biblemap.put("b11_9_7", new hebsuperobject(11,9,7,"והכרתי את ישראל מעל פני האדמה אשר נתתי להם ואת הבית אשר הקדשתי לשמי אשלח מעל פני והיה ישראל למשל ולשנינה בכל העמים׃"));
hebsuperobject.biblemap.put("b11_9_8", new hebsuperobject(11,9,8,"והבית הזה יהיה עליון כל עבר עליו ישם ושרק ואמרו על מה עשה יהוה ככה לארץ הזאת ולבית הזה׃"));
hebsuperobject.biblemap.put("b11_9_9", new hebsuperobject(11,9,9,"ואמרו על אשר עזבו את יהוה אלהיהם אשר הוציא את אבתם מארץ מצרים ויחזקו באלהים אחרים וישתחו להם ויעבדם על כן הביא יהוה עליהם את כל הרעה הזאת׃"));
hebsuperobject.biblemap.put("b11_9_10", new hebsuperobject(11,9,10,"ויהי מקצה עשרים שנה אשר בנה שלמה את שני הבתים את בית יהוה ואת בית המלך׃"));
