hebsuperobject.biblemap.put("b11_9_12", new hebsuperobject(11,9,12,"ויצא חירם מצר לראות את הערים אשר נתן לו שלמה ולא ישרו בעיניו׃"));
hebsuperobject.biblemap.put("b11_9_13", new hebsuperobject(11,9,13,"ויאמר מה הערים האלה אשר נתתה לי אחי ויקרא להם ארץ כבול עד היום הזה׃"));
