hebsuperobject.biblemap.put("b11_9_22", new hebsuperobject(11,9,22,"ומבני ישראל לא נתן שלמה עבד כי הם אנשי המלחמה ועבדיו ושריו ושלשיו ושרי רכבו ופרשיו׃"));
hebsuperobject.biblemap.put("b11_9_23", new hebsuperobject(11,9,23,"אלה שרי הנצבים אשר על המלאכה לשלמה חמשים וחמש מאות הרדים בעם העשים במלאכה׃"));
