hebsuperobject.biblemap.put("b11_9_26", new hebsuperobject(11,9,26,"ואני עשה המלך שלמה בעציון גבר אשר את אלות על שפת ים סוף בארץ אדום׃"));
hebsuperobject.biblemap.put("b11_9_27", new hebsuperobject(11,9,27,"וישלח חירם באני את עבדיו אנשי אניות ידעי הים עם עבדי שלמה׃"));
hebsuperobject.biblemap.put("b11_9_28", new hebsuperobject(11,9,28,"ויבאו אופירה ויקחו משם זהב ארבע מאות ועשרים ככר ויבאו אל המלך שלמה׃"));
