hebsuperobject.biblemap.put("b11_10_6", new hebsuperobject(11,10,6,"ותאמר אל המלך אמת היה הדבר אשר שמעתי בארצי על דבריך ועל חכמתך׃"));
hebsuperobject.biblemap.put("b11_10_7", new hebsuperobject(11,10,7,"ולא האמנתי לדברים עד אשר באתי ותראינה עיני והנה לא הגד לי החצי הוספת חכמה וטוב אל השמועה אשר שמעתי׃"));
hebsuperobject.biblemap.put("b11_10_8", new hebsuperobject(11,10,8,"אשרי אנשיך אשרי עבדיך אלה העמדים לפניך תמיד השמעים את חכמתך׃"));
hebsuperobject.biblemap.put("b11_10_9", new hebsuperobject(11,10,9,"יהי יהוה אלהיך ברוך אשר חפץ בך לתתך על כסא ישראל באהבת יהוה את ישראל לעלם וישימך למלך לעשות משפט וצדקה׃"));
hebsuperobject.biblemap.put("b11_10_10", new hebsuperobject(11,10,10,"ותתן למלך מאה ועשרים ככר זהב ובשמים הרבה מאד ואבן יקרה לא בא כבשם ההוא עוד לרב אשר נתנה מלכת שבא למלך שלמה׃"));
hebsuperobject.biblemap.put("b11_10_11", new hebsuperobject(11,10,11,"וגם אני חירם אשר נשא זהב מאופיר הביא מאפיר עצי אלמגים הרבה מאד ואבן יקרה׃"));
hebsuperobject.biblemap.put("b11_10_12", new hebsuperobject(11,10,12,"ויעש המלך את עצי האלמגים מסעד לבית יהוה ולבית המלך וכנרות ונבלים לשרים לא בא כן עצי אלמגים ולא נראה עד היום הזה׃"));
hebsuperobject.biblemap.put("b11_10_13", new hebsuperobject(11,10,13,"והמלך שלמה נתן למלכת שבא את כל חפצה אשר שאלה מלבד אשר נתן לה כיד המלך שלמה ותפן ותלך לארצה היא ועבדיה׃"));
hebsuperobject.biblemap.put("b11_10_14", new hebsuperobject(11,10,14,"ויהי משקל הזהב אשר בא לשלמה בשנה אחת שש מאות ששים ושש ככר זהב׃"));
