hebsuperobject.biblemap.put("b11_10_20", new hebsuperobject(11,10,20,"ושנים עשר אריים עמדים שם על שש המעלות מזה ומזה לא נעשה כן לכל ממלכות׃"));
hebsuperobject.biblemap.put("b11_10_21", new hebsuperobject(11,10,21,"וכל כלי משקה המלך שלמה זהב וכל כלי בית יער הלבנון זהב סגור אין כסף לא נחשב בימי שלמה למאומה׃"));
hebsuperobject.biblemap.put("b11_10_22", new hebsuperobject(11,10,22,"כי אני תרשיש למלך בים עם אני חירם אחת לשלש שנים תבוא אני תרשיש נשאת זהב וכסף שנהבים וקפים ותכיים׃"));
hebsuperobject.biblemap.put("b11_10_23", new hebsuperobject(11,10,23,"ויגדל המלך שלמה מכל מלכי הארץ לעשר ולחכמה׃"));
hebsuperobject.biblemap.put("b11_10_24", new hebsuperobject(11,10,24,"וכל הארץ מבקשים את פני שלמה לשמע את חכמתו אשר נתן אלהים בלבו׃"));
