hebsuperobject.biblemap.put("b11_10_27", new hebsuperobject(11,10,27,"ויתן המלך את הכסף בירושלם כאבנים ואת הארזים נתן כשקמים אשר בשפלה לרב׃"));
hebsuperobject.biblemap.put("b11_10_28", new hebsuperobject(11,10,28,"ומוצא הסוסים אשר לשלמה ממצרים ומקוה סחרי המלך יקחו מקוה במחיר׃"));
hebsuperobject.biblemap.put("b11_10_29", new hebsuperobject(11,10,29,"ותעלה ותצא מרכבה ממצרים בשש מאות כסף וסוס בחמשים ומאה וכן לכל מלכי החתים ולמלכי ארם בידם יצאו׃"));
hebsuperobject.biblemap.put("b11_11_1", new hebsuperobject(11,11,1,"והמלך שלמה אהב נשים נכריות רבות ואת בת פרעה מואביות עמניות אדמית צדנית חתית׃"));
hebsuperobject.biblemap.put("b11_11_2", new hebsuperobject(11,11,2,"מן הגוים אשר אמר יהוה אל בני ישראל לא תבאו בהם והם לא יבאו בכם אכן יטו את לבבכם אחרי אלהיהם בהם דבק שלמה לאהבה׃"));
hebsuperobject.biblemap.put("b11_11_3", new hebsuperobject(11,11,3,"ויהי לו נשים שרות שבע מאות ופלגשים שלש מאות ויטו נשיו את לבו׃"));
hebsuperobject.biblemap.put("b11_11_4", new hebsuperobject(11,11,4,"ויהי לעת זקנת שלמה נשיו הטו את לבבו אחרי אלהים אחרים ולא היה לבבו שלם עם יהוה אלהיו כלבב דויד אביו׃"));
hebsuperobject.biblemap.put("b11_11_5", new hebsuperobject(11,11,5,"וילך שלמה אחרי עשתרת אלהי צדנים ואחרי מלכם שקץ עמנים׃"));
hebsuperobject.biblemap.put("b11_11_6", new hebsuperobject(11,11,6,"ויעש שלמה הרע בעיני יהוה ולא מלא אחרי יהוה כדוד אביו׃"));
hebsuperobject.biblemap.put("b11_11_7", new hebsuperobject(11,11,7,"אז יבנה שלמה במה לכמוש שקץ מואב בהר אשר על פני ירושלם ולמלך שקץ בני עמון׃"));
hebsuperobject.biblemap.put("b11_11_8", new hebsuperobject(11,11,8,"וכן עשה לכל נשיו הנכריות מקטירות ומזבחות לאלהיהן׃"));
hebsuperobject.biblemap.put("b11_11_9", new hebsuperobject(11,11,9,"ויתאנף יהוה בשלמה כי נטה לבבו מעם יהוה אלהי ישראל הנראה אליו פעמים׃"));
hebsuperobject.biblemap.put("b11_11_10", new hebsuperobject(11,11,10,"וצוה אליו על הדבר הזה לבלתי לכת אחרי אלהים אחרים ולא שמר את אשר צוה יהוה׃"));
hebsuperobject.biblemap.put("b11_11_11", new hebsuperobject(11,11,11,"ויאמר יהוה לשלמה יען אשר היתה זאת עמך ולא שמרת בריתי וחקתי אשר צויתי עליך קרע אקרע את הממלכה מעליך ונתתיה לעבדך׃"));
hebsuperobject.biblemap.put("b11_11_12", new hebsuperobject(11,11,12,"אך בימיך לא אעשנה למען דוד אביך מיד בנך אקרענה׃"));
hebsuperobject.biblemap.put("b11_11_13", new hebsuperobject(11,11,13,"רק את כל הממלכה לא אקרע שבט אחד אתן לבנך למען דוד עבדי ולמען ירושלם אשר בחרתי׃"));
hebsuperobject.biblemap.put("b11_11_14", new hebsuperobject(11,11,14,"ויקם יהוה שטן לשלמה את הדד האדמי מזרע המלך הוא באדום׃"));
hebsuperobject.biblemap.put("b11_11_15", new hebsuperobject(11,11,15,"ויהי בהיות דוד את אדום בעלות יואב שר הצבא לקבר את החללים ויך כל זכר באדום׃"));
hebsuperobject.biblemap.put("b11_11_16", new hebsuperobject(11,11,16,"כי ששת חדשים ישב שם יואב וכל ישראל עד הכרית כל זכר באדום׃"));
hebsuperobject.biblemap.put("b11_11_17", new hebsuperobject(11,11,17,"ויברח אדד הוא ואנשים אדמיים מעבדי אביו אתו לבוא מצרים והדד נער קטן׃"));
hebsuperobject.biblemap.put("b11_11_18", new hebsuperobject(11,11,18,"ויקמו ממדין ויבאו פארן ויקחו אנשים עמם מפארן ויבאו מצרים אל פרעה מלך מצרים ויתן לו בית ולחם אמר לו וארץ נתן לו׃"));
hebsuperobject.biblemap.put("b11_11_19", new hebsuperobject(11,11,19,"וימצא הדד חן בעיני פרעה מאד ויתן לו אשה את אחות אשתו אחות תחפניס הגבירה׃"));
hebsuperobject.biblemap.put("b11_11_20", new hebsuperobject(11,11,20,"ותלד לו אחות תחפניס את גנבת בנו ותגמלהו תחפנס בתוך בית פרעה ויהי גנבת בית פרעה בתוך בני פרעה׃"));
hebsuperobject.biblemap.put("b11_11_21", new hebsuperobject(11,11,21,"והדד שמע במצרים כי שכב דוד עם אבתיו וכי מת יואב שר הצבא ויאמר הדד אל פרעה שלחני ואלך אל ארצי׃"));
hebsuperobject.biblemap.put("b11_11_22", new hebsuperobject(11,11,22,"ויאמר לו פרעה כי מה אתה חסר עמי והנך מבקש ללכת אל ארצך ויאמר לא כי שלח תשלחני׃"));
hebsuperobject.biblemap.put("b11_11_23", new hebsuperobject(11,11,23,"ויקם אלהים לו שטן את רזון בן אלידע אשר ברח מאת הדדעזר מלך צובה אדניו׃"));
hebsuperobject.biblemap.put("b11_11_24", new hebsuperobject(11,11,24,"ויקבץ עליו אנשים ויהי שר גדוד בהרג דוד אתם וילכו דמשק וישבו בה וימלכו בדמשק׃"));
hebsuperobject.biblemap.put("b11_11_25", new hebsuperobject(11,11,25,"ויהי שטן לישראל כל ימי שלמה ואת הרעה אשר הדד ויקץ בישראל וימלך על ארם׃"));
hebsuperobject.biblemap.put("b11_11_26", new hebsuperobject(11,11,26,"וירבעם בן נבט אפרתי מן הצרדה ושם אמו צרועה אשה אלמנה עבד לשלמה וירם יד במלך׃"));
hebsuperobject.biblemap.put("b11_11_27", new hebsuperobject(11,11,27,"וזה הדבר אשר הרים יד במלך שלמה בנה את המלוא סגר את פרץ עיר דוד אביו׃"));
hebsuperobject.biblemap.put("b11_11_28", new hebsuperobject(11,11,28,"והאיש ירבעם גבור חיל וירא שלמה את הנער כי עשה מלאכה הוא ויפקד אתו לכל סבל בית יוסף׃"));
hebsuperobject.biblemap.put("b11_11_29", new hebsuperobject(11,11,29,"ויהי בעת ההיא וירבעם יצא מירושלם וימצא אתו אחיה השילני הנביא בדרך והוא מתכסה בשלמה חדשה ושניהם לבדם בשדה׃"));
hebsuperobject.biblemap.put("b11_11_30", new hebsuperobject(11,11,30,"ויתפש אחיה בשלמה החדשה אשר עליו ויקרעה שנים עשר קרעים׃"));
hebsuperobject.biblemap.put("b11_11_31", new hebsuperobject(11,11,31,"ויאמר לירבעם קח לך עשרה קרעים כי כה אמר יהוה אלהי ישראל הנני קרע את הממלכה מיד שלמה ונתתי לך את עשרה השבטים׃"));
hebsuperobject.biblemap.put("b11_11_32", new hebsuperobject(11,11,32,"והשבט האחד יהיה לו למען עבדי דוד ולמען ירושלם העיר אשר בחרתי בה מכל שבטי ישראל׃"));
hebsuperobject.biblemap.put("b11_11_33", new hebsuperobject(11,11,33,"יען אשר עזבוני וישתחוו לעשתרת אלהי צדנין לכמוש אלהי מואב ולמלכם אלהי בני עמון ולא הלכו בדרכי לעשות הישר בעיני וחקתי ומשפטי כדוד אביו׃"));
hebsuperobject.biblemap.put("b11_11_34", new hebsuperobject(11,11,34,"ולא אקח את כל הממלכה מידו כי נשיא אשתנו כל ימי חייו למען דוד עבדי אשר בחרתי אתו אשר שמר מצותי וחקתי׃"));
hebsuperobject.biblemap.put("b11_11_35", new hebsuperobject(11,11,35,"ולקחתי המלוכה מיד בנו ונתתיה לך את עשרת השבטים׃"));
hebsuperobject.biblemap.put("b11_11_36", new hebsuperobject(11,11,36,"ולבנו אתן שבט אחד למען היות ניר לדויד עבדי כל הימים לפני בירושלם העיר אשר בחרתי לי לשום שמי שם׃"));
hebsuperobject.biblemap.put("b11_11_37", new hebsuperobject(11,11,37,"ואתך אקח ומלכת בכל אשר תאוה נפשך והיית מלך על ישראל׃"));
hebsuperobject.biblemap.put("b11_11_38", new hebsuperobject(11,11,38,"והיה אם תשמע את כל אשר אצוך והלכת בדרכי ועשית הישר בעיני לשמור חקותי ומצותי כאשר עשה דוד עבדי והייתי עמך ובניתי לך בית נאמן כאשר בניתי לדוד ונתתי לך את ישראל׃"));
hebsuperobject.biblemap.put("b11_11_39", new hebsuperobject(11,11,39,"ואענה את זרע דוד למען זאת אך לא כל הימים׃"));
hebsuperobject.biblemap.put("b11_11_40", new hebsuperobject(11,11,40,"ויבקש שלמה להמית את ירבעם ויקם ירבעם ויברח מצרים אל שישק מלך מצרים ויהי במצרים עד מות שלמה׃"));
hebsuperobject.biblemap.put("b11_11_41", new hebsuperobject(11,11,41,"ויתר דברי שלמה וכל אשר עשה וחכמתו הלוא הם כתבים על ספר דברי שלמה׃"));
hebsuperobject.biblemap.put("b11_11_42", new hebsuperobject(11,11,42,"והימים אשר מלך שלמה בירושלם על כל ישראל ארבעים שנה׃"));
hebsuperobject.biblemap.put("b11_11_43", new hebsuperobject(11,11,43,"וישכב שלמה עם אבתיו ויקבר בעיר דוד אביו וימלך רחבעם בנו תחתיו׃"));
hebsuperobject.biblemap.put("b11_12_1", new hebsuperobject(11,12,1,"וילך רחבעם שכם כי שכם בא כל ישראל להמליך אתו׃"));
hebsuperobject.biblemap.put("b11_12_2", new hebsuperobject(11,12,2,"ויהי כשמע ירבעם בן נבט והוא עודנו במצרים אשר ברח מפני המלך שלמה וישב ירבעם במצרים׃"));
hebsuperobject.biblemap.put("b11_12_3", new hebsuperobject(11,12,3,"וישלחו ויקראו לו ויבאו ירבעם וכל קהל ישראל וידברו אל רחבעם לאמר׃"));
hebsuperobject.biblemap.put("b11_12_4", new hebsuperobject(11,12,4,"אביך הקשה את עלנו ואתה עתה הקל מעבדת אביך הקשה ומעלו הכבד אשר נתן עלינו ונעבדך׃"));
hebsuperobject.biblemap.put("b11_12_5", new hebsuperobject(11,12,5,"ויאמר אליהם לכו עד שלשה ימים ושובו אלי וילכו העם׃"));
hebsuperobject.biblemap.put("b11_12_6", new hebsuperobject(11,12,6,"ויועץ המלך רחבעם את הזקנים אשר היו עמדים את פני שלמה אביו בהיתו חי לאמר איך אתם נועצים להשיב את העם הזה דבר׃"));
hebsuperobject.biblemap.put("b11_12_7", new hebsuperobject(11,12,7,"וידבר אליו לאמר אם היום תהיה עבד לעם הזה ועבדתם ועניתם ודברת אליהם דברים טובים והיו לך עבדים כל הימים׃"));
hebsuperobject.biblemap.put("b11_12_8", new hebsuperobject(11,12,8,"ויעזב את עצת הזקנים אשר יעצהו ויועץ את הילדים אשר גדלו אתו אשר העמדים לפניו׃"));
hebsuperobject.biblemap.put("b11_12_9", new hebsuperobject(11,12,9,"ויאמר אליהם מה אתם נועצים ונשיב דבר את העם הזה אשר דברו אלי לאמר הקל מן העל אשר נתן אביך עלינו׃"));
hebsuperobject.biblemap.put("b11_12_10", new hebsuperobject(11,12,10,"וידברו אליו הילדים אשר גדלו אתו לאמר כה תאמר לעם הזה אשר דברו אליך לאמר אביך הכביד את עלנו ואתה הקל מעלינו כה תדבר אליהם קטני עבה ממתני אבי׃"));
hebsuperobject.biblemap.put("b11_12_11", new hebsuperobject(11,12,11,"ועתה אבי העמיס עליכם על כבד ואני אוסיף על עלכם אבי יסר אתכם בשוטים ואני איסר אתכם בעקרבים׃"));
hebsuperobject.biblemap.put("b11_12_12", new hebsuperobject(11,12,12,"ויבו ירבעם וכל העם אל רחבעם ביום השלישי כאשר דבר המלך לאמר שובו אלי ביום השלישי׃"));
hebsuperobject.biblemap.put("b11_12_13", new hebsuperobject(11,12,13,"ויען המלך את העם קשה ויעזב את עצת הזקנים אשר יעצהו׃"));
hebsuperobject.biblemap.put("b11_12_14", new hebsuperobject(11,12,14,"וידבר אליהם כעצת הילדים לאמר אבי הכביד את עלכם ואני אסיף על עלכם אבי יסר אתכם בשוטים ואני איסר אתכם בעקרבים׃"));
hebsuperobject.biblemap.put("b11_12_15", new hebsuperobject(11,12,15,"ולא שמע המלך אל העם כי היתה סבה מעם יהוה למען הקים את דברו אשר דבר יהוה ביד אחיה השילני אל ירבעם בן נבט׃"));
hebsuperobject.biblemap.put("b11_12_16", new hebsuperobject(11,12,16,"וירא כל ישראל כי לא שמע המלך אליהם וישבו העם את המלך דבר לאמר מה לנו חלק בדוד ולא נחלה בבן ישי לאהליך ישראל עתה ראה ביתך דוד וילך ישראל לאהליו׃"));
hebsuperobject.biblemap.put("b11_12_17", new hebsuperobject(11,12,17,"ובני ישראל הישבים בערי יהודה וימלך עליהם רחבעם׃"));
hebsuperobject.biblemap.put("b11_12_18", new hebsuperobject(11,12,18,"וישלח המלך רחבעם את אדרם אשר על המס וירגמו כל ישראל בו אבן וימת והמלך רחבעם התאמץ לעלות במרכבה לנוס ירושלם׃"));
hebsuperobject.biblemap.put("b11_12_19", new hebsuperobject(11,12,19,"ויפשעו ישראל בבית דוד עד היום הזה׃"));
hebsuperobject.biblemap.put("b11_12_20", new hebsuperobject(11,12,20,"ויהי כשמע כל ישראל כי שב ירבעם וישלחו ויקראו אתו אל העדה וימליכו אתו על כל ישראל לא היה אחרי בית דוד זולתי שבט יהודה לבדו׃"));
hebsuperobject.biblemap.put("b11_12_21", new hebsuperobject(11,12,21,"ויבאו רחבעם ירושלם ויקהל את כל בית יהודה ואת שבט בנימן מאה ושמנים אלף בחור עשה מלחמה להלחם עם בית ישראל להשיב את המלוכה לרחבעם בן שלמה׃"));
hebsuperobject.biblemap.put("b11_12_22", new hebsuperobject(11,12,22,"ויהי דבר האלהים אל שמעיה איש האלהים לאמר׃"));
hebsuperobject.biblemap.put("b11_12_23", new hebsuperobject(11,12,23,"אמר אל רחבעם בן שלמה מלך יהודה ואל כל בית יהודה ובנימין ויתר העם לאמר׃"));
hebsuperobject.biblemap.put("b11_12_24", new hebsuperobject(11,12,24,"כה אמר יהוה לא תעלו ולא תלחמון עם אחיכם בני ישראל שובו איש לביתו כי מאתי נהיה הדבר הזה וישמעו את דבר יהוה וישבו ללכת כדבר יהוה׃"));
hebsuperobject.biblemap.put("b11_12_25", new hebsuperobject(11,12,25,"ויבן ירבעם את שכם בהר אפרים וישב בה ויצא משם ויבן את פנואל׃"));
hebsuperobject.biblemap.put("b11_12_26", new hebsuperobject(11,12,26,"ויאמר ירבעם בלבו עתה תשוב הממלכה לבית דוד׃"));
hebsuperobject.biblemap.put("b11_12_27", new hebsuperobject(11,12,27,"אם יעלה העם הזה לעשות זבחים בבית יהוה בירושלם ושב לב העם הזה אל אדניהם אל רחבעם מלך יהודה והרגני ושבו אל רחבעם מלך יהודה׃"));
hebsuperobject.biblemap.put("b11_12_28", new hebsuperobject(11,12,28,"ויועץ המלך ויעש שני עגלי זהב ויאמר אלהם רב לכם מעלות ירושלם הנה אלהיך ישראל אשר העלוך מארץ מצרים׃"));
hebsuperobject.biblemap.put("b11_12_29", new hebsuperobject(11,12,29,"וישם את האחד בבית אל ואת האחד נתן בדן׃"));
hebsuperobject.biblemap.put("b11_12_30", new hebsuperobject(11,12,30,"ויהי הדבר הזה לחטאת וילכו העם לפני האחד עד דן׃"));
hebsuperobject.biblemap.put("b11_12_31", new hebsuperobject(11,12,31,"ויעש את בית במות ויעש כהנים מקצות העם אשר לא היו מבני לוי׃"));
hebsuperobject.biblemap.put("b11_12_32", new hebsuperobject(11,12,32,"ויעש ירבעם חג בחדש השמיני בחמשה עשר יום לחדש כחג אשר ביהודה ויעל על המזבח כן עשה בבית אל לזבח לעגלים אשר עשה והעמיד בבית אל את כהני הבמות אשר עשה׃"));
hebsuperobject.biblemap.put("b11_12_33", new hebsuperobject(11,12,33,"ויעל על המזבח אשר עשה בבית אל בחמשה עשר יום בחדש השמיני בחדש אשר בדא מלבד ויעש חג לבני ישראל ויעל על המזבח להקטיר׃"));
hebsuperobject.biblemap.put("b11_13_1", new hebsuperobject(11,13,1,"והנה איש אלהים בא מיהודה בדבר יהוה אל בית אל וירבעם עמד על המזבח להקטיר׃"));
hebsuperobject.biblemap.put("b11_13_2", new hebsuperobject(11,13,2,"ויקרא על המזבח בדבר יהוה ויאמר מזבח מזבח כה אמר יהוה הנה בן נולד לבית דוד יאשיהו שמו וזבח עליך את כהני הבמות המקטרים עליך ועצמות אדם ישרפו עליך׃"));
hebsuperobject.biblemap.put("b11_13_3", new hebsuperobject(11,13,3,"ונתן ביום ההוא מופת לאמר זה המופת אשר דבר יהוה הנה המזבח נקרע ונשפך הדשן אשר עליו׃"));
hebsuperobject.biblemap.put("b11_13_4", new hebsuperobject(11,13,4,"ויהי כשמע המלך את דבר איש האלהים אשר קרא על המזבח בבית אל וישלח ירבעם את ידו מעל המזבח לאמר תפשהו ותיבש ידו אשר שלח עליו ולא יכל להשיבה אליו׃"));
hebsuperobject.biblemap.put("b11_13_5", new hebsuperobject(11,13,5,"והמזבח נקרע וישפך הדשן מן המזבח כמופת אשר נתן איש האלהים בדבר יהוה׃"));
hebsuperobject.biblemap.put("b11_13_6", new hebsuperobject(11,13,6,"ויען המלך ויאמר אל איש האלהים חל נא את פני יהוה אלהיך והתפלל בעדי ותשב ידי אלי ויחל איש האלהים את פני יהוה ותשב יד המלך אליו ותהי כבראשנה׃"));
hebsuperobject.biblemap.put("b11_13_7", new hebsuperobject(11,13,7,"וידבר המלך אל איש האלהים באה אתי הביתה וסעדה ואתנה לך מתת׃"));
hebsuperobject.biblemap.put("b11_13_8", new hebsuperobject(11,13,8,"ויאמר איש האלהים אל המלך אם תתן לי את חצי ביתך לא אבא עמך ולא אכל לחם ולא אשתה מים במקום הזה׃"));
hebsuperobject.biblemap.put("b11_13_9", new hebsuperobject(11,13,9,"כי כן צוה אתי בדבר יהוה לאמר לא תאכל לחם ולא תשתה מים ולא תשוב בדרך אשר הלכת׃"));
hebsuperobject.biblemap.put("b11_13_10", new hebsuperobject(11,13,10,"וילך בדרך אחר ולא שב בדרך אשר בא בה אל בית אל׃"));
hebsuperobject.biblemap.put("b11_13_11", new hebsuperobject(11,13,11,"ונביא אחד זקן ישב בבית אל ויבוא בנו ויספר לו את כל המעשה אשר עשה איש האלהים היום בבית אל את הדברים אשר דבר אל המלך ויספרום לאביהם׃"));
hebsuperobject.biblemap.put("b11_13_12", new hebsuperobject(11,13,12,"וידבר אלהם אביהם אי זה הדרך הלך ויראו בניו את הדרך אשר הלך איש האלהים אשר בא מיהודה׃"));
hebsuperobject.biblemap.put("b11_13_13", new hebsuperobject(11,13,13,"ויאמר אל בניו חבשו לי החמור ויחבשו לו החמור וירכב עליו׃"));
hebsuperobject.biblemap.put("b11_13_14", new hebsuperobject(11,13,14,"וילך אחרי איש האלהים וימצאהו ישב תחת האלה ויאמר אליו האתה איש האלהים אשר באת מיהודה ויאמר אני׃"));
hebsuperobject.biblemap.put("b11_13_15", new hebsuperobject(11,13,15,"ויאמר אליו לך אתי הביתה ואכל לחם׃"));
hebsuperobject.biblemap.put("b11_13_16", new hebsuperobject(11,13,16,"ויאמר לא אוכל לשוב אתך ולבוא אתך ולא אכל לחם ולא אשתה אתך מים במקום הזה׃"));
hebsuperobject.biblemap.put("b11_13_17", new hebsuperobject(11,13,17,"כי דבר אלי בדבר יהוה לא תאכל לחם ולא תשתה שם מים לא תשוב ללכת בדרך אשר הלכת בה׃"));
hebsuperobject.biblemap.put("b11_13_18", new hebsuperobject(11,13,18,"ויאמר לו גם אני נביא כמוך ומלאך דבר אלי בדבר יהוה לאמר השבהו אתך אל ביתך ויאכל לחם וישת מים כחש לו׃"));
hebsuperobject.biblemap.put("b11_13_19", new hebsuperobject(11,13,19,"וישב אתו ויאכל לחם בביתו וישת מים׃"));
hebsuperobject.biblemap.put("b11_13_20", new hebsuperobject(11,13,20,"ויהי הם ישבים אל השלחן ויהי דבר יהוה אל הנביא אשר השיבו׃"));
hebsuperobject.biblemap.put("b11_13_21", new hebsuperobject(11,13,21,"ויקרא אל איש האלהים אשר בא מיהודה לאמר כה אמר יהוה יען כי מרית פי יהוה ולא שמרת את המצוה אשר צוך יהוה אלהיך׃"));
hebsuperobject.biblemap.put("b11_13_22", new hebsuperobject(11,13,22,"ותשב ותאכל לחם ותשת מים במקום אשר דבר אליך אל תאכל לחם ואל תשת מים לא תבוא נבלתך אל קבר אבתיך׃"));
hebsuperobject.biblemap.put("b11_13_23", new hebsuperobject(11,13,23,"ויהי אחרי אכלו לחם ואחרי שתותו ויחבש לו החמור לנביא אשר השיבו׃"));
hebsuperobject.biblemap.put("b11_13_24", new hebsuperobject(11,13,24,"וילך וימצאהו אריה בדרך וימיתהו ותהי נבלתו משלכת בדרך והחמור עמד אצלה והאריה עמד אצל הנבלה׃"));
hebsuperobject.biblemap.put("b11_13_25", new hebsuperobject(11,13,25,"והנה אנשים עברים ויראו את הנבלה משלכת בדרך ואת האריה עמד אצל הנבלה ויבאו וידברו בעיר אשר הנביא הזקן ישב בה׃"));
hebsuperobject.biblemap.put("b11_13_26", new hebsuperobject(11,13,26,"וישמע הנביא אשר השיבו מן הדרך ויאמר איש האלהים הוא אשר מרה את פי יהוה ויתנהו יהוה לאריה וישברהו וימתהו כדבר יהוה אשר דבר לו׃"));
hebsuperobject.biblemap.put("b11_13_27", new hebsuperobject(11,13,27,"וידבר אל בניו לאמר חבשו לי את החמור ויחבשו׃"));
hebsuperobject.biblemap.put("b11_13_28", new hebsuperobject(11,13,28,"וילך וימצא את נבלתו משלכת בדרך וחמור והאריה עמדים אצל הנבלה לא אכל האריה את הנבלה ולא שבר את החמור׃"));
hebsuperobject.biblemap.put("b11_13_29", new hebsuperobject(11,13,29,"וישא הנביא את נבלת איש האלהים וינחהו אל החמור וישיבהו ויבא אל עיר הנביא הזקן לספד ולקברו׃"));
hebsuperobject.biblemap.put("b11_13_30", new hebsuperobject(11,13,30,"וינח את נבלתו בקברו ויספדו עליו הוי אחי׃"));
hebsuperobject.biblemap.put("b11_13_31", new hebsuperobject(11,13,31,"ויהי אחרי קברו אתו ויאמר אל בניו לאמר במותי וקברתם אתי בקבר אשר איש האלהים קבור בו אצל עצמתיו הניחו את עצמתי׃"));
hebsuperobject.biblemap.put("b11_13_32", new hebsuperobject(11,13,32,"כי היה יהיה הדבר אשר קרא בדבר יהוה על המזבח אשר בבית אל ועל כל בתי הבמות אשר בערי שמרון׃"));
hebsuperobject.biblemap.put("b11_13_33", new hebsuperobject(11,13,33,"אחר הדבר הזה לא שב ירבעם מדרכו הרעה וישב ויעש מקצות העם כהני במות החפץ ימלא את ידו ויהי כהני במות׃"));
hebsuperobject.biblemap.put("b11_13_34", new hebsuperobject(11,13,34,"ויהי בדבר הזה לחטאת בית ירבעם ולהכחיד ולהשמיד מעל פני האדמה׃"));
hebsuperobject.biblemap.put("b11_14_1", new hebsuperobject(11,14,1,"בעת ההיא חלה אביה בן ירבעם׃"));
hebsuperobject.biblemap.put("b11_14_2", new hebsuperobject(11,14,2,"ויאמר ירבעם לאשתו קומי נא והשתנית ולא ידעו כי אתי אשת ירבעם והלכת שלה הנה שם אחיה הנביא הוא דבר עלי למלך על העם הזה׃"));
hebsuperobject.biblemap.put("b11_14_3", new hebsuperobject(11,14,3,"ולקחת בידך עשרה לחם ונקדים ובקבק דבש ובאת אליו הוא יגיד לך מה יהיה לנער׃"));
hebsuperobject.biblemap.put("b11_14_4", new hebsuperobject(11,14,4,"ותעש כן אשת ירבעם ותקם ותלך שלה ותבא בית אחיה ואחיהו לא יכל לראות כי קמו עיניו משיבו׃"));
hebsuperobject.biblemap.put("b11_14_5", new hebsuperobject(11,14,5,"ויהוה אמר אל אחיהו הנה אשת ירבעם באה לדרש דבר מעמך אל בנה כי חלה הוא כזה וכזה תדבר אליה ויהי כבאה והיא מתנכרה׃"));
hebsuperobject.biblemap.put("b11_14_6", new hebsuperobject(11,14,6,"ויהי כשמע אחיהו את קול רגליה באה בפתח ויאמר באי אשת ירבעם למה זה את מתנכרה ואנכי שלוח אליך קשה׃"));
hebsuperobject.biblemap.put("b11_14_7", new hebsuperobject(11,14,7,"לכי אמרי לירבעם כה אמר יהוה אלהי ישראל יען אשר הרימתיך מתוך העם ואתנך נגיד על עמי ישראל׃"));
hebsuperobject.biblemap.put("b11_14_8", new hebsuperobject(11,14,8,"ואקרע את הממלכה מבית דוד ואתנה לך ולא היית כעבדי דוד אשר שמר מצותי ואשר הלך אחרי בכל לבבו לעשות רק הישר בעיני׃"));
hebsuperobject.biblemap.put("b11_14_9", new hebsuperobject(11,14,9,"ותרע לעשות מכל אשר היו לפניך ותלך ותעשה לך אלהים אחרים ומסכות להכעיסני ואתי השלכת אחרי גוך׃"));
hebsuperobject.biblemap.put("b11_14_10", new hebsuperobject(11,14,10,"לכן הנני מביא רעה אל בית ירבעם והכרתי לירבעם משתין בקיר עצור ועזוב בישראל ובערתי אחרי בית ירבעם כאשר יבער הגלל עד תמו׃"));
hebsuperobject.biblemap.put("b11_14_11", new hebsuperobject(11,14,11,"המת לירבעם בעיר יאכלו הכלבים והמת בשדה יאכלו עוף השמים כי יהוה דבר׃"));
hebsuperobject.biblemap.put("b11_14_12", new hebsuperobject(11,14,12,"ואת קומי לכי לביתך בבאה רגליך העירה ומת הילד׃"));
hebsuperobject.biblemap.put("b11_14_13", new hebsuperobject(11,14,13,"וספדו לו כל ישראל וקברו אתו כי זה לבדו יבא לירבעם אל קבר יען נמצא בו דבר טוב אל יהוה אלהי ישראל בבית ירבעם׃"));
hebsuperobject.biblemap.put("b11_14_14", new hebsuperobject(11,14,14,"והקים יהוה לו מלך על ישראל אשר יכרית את בית ירבעם זה היום ומה גם עתה׃"));
hebsuperobject.biblemap.put("b11_14_15", new hebsuperobject(11,14,15,"והכה יהוה את ישראל כאשר ינוד הקנה במים ונתש את ישראל מעל האדמה הטובה הזאת אשר נתן לאבותיהם וזרם מעבר לנהר יען אשר עשו את אשריהם מכעיסים את יהוה׃"));
hebsuperobject.biblemap.put("b11_14_16", new hebsuperobject(11,14,16,"ויתן את ישראל בגלל חטאות ירבעם אשר חטא ואשר החטיא את ישראל׃"));
hebsuperobject.biblemap.put("b11_14_17", new hebsuperobject(11,14,17,"ותקם אשת ירבעם ותלך ותבא תרצתה היא באה בסף הבית והנער מת׃"));
hebsuperobject.biblemap.put("b11_14_18", new hebsuperobject(11,14,18,"ויקברו אתו ויספדו לו כל ישראל כדבר יהוה אשר דבר ביד עבדו אחיהו הנביא׃"));
hebsuperobject.biblemap.put("b11_14_19", new hebsuperobject(11,14,19,"ויתר דברי ירבעם אשר נלחם ואשר מלך הנם כתובים על ספר דברי הימים למלכי ישראל׃"));
hebsuperobject.biblemap.put("b11_14_20", new hebsuperobject(11,14,20,"והימים אשר מלך ירבעם עשרים ושתים שנה וישכב עם אבתיו וימלך נדב בנו תחתיו׃"));
hebsuperobject.biblemap.put("b11_14_21", new hebsuperobject(11,14,21,"ורחבעם בן שלמה מלך ביהודה בן ארבעים ואחת שנה רחבעם במלכו ושבע עשרה שנה מלך בירושלם העיר אשר בחר יהוה לשום את שמו שם מכל שבטי ישראל ושם אמו נעמה העמנית׃"));
hebsuperobject.biblemap.put("b11_14_22", new hebsuperobject(11,14,22,"ויעש יהודה הרע בעיני יהוה ויקנאו אתו מכל אשר עשו אבתם בחטאתם אשר חטאו׃"));
hebsuperobject.biblemap.put("b11_14_23", new hebsuperobject(11,14,23,"ויבנו גם המה להם במות ומצבות ואשרים על כל גבעה גבהה ותחת כל עץ רענן׃"));
hebsuperobject.biblemap.put("b11_14_24", new hebsuperobject(11,14,24,"וגם קדש היה בארץ עשו ככל התועבת הגוים אשר הוריש יהוה מפני בני ישראל׃"));
hebsuperobject.biblemap.put("b11_14_25", new hebsuperobject(11,14,25,"ויהי בשנה החמישית למלך רחבעם עלה שושק מלך מצרים על ירושלם׃"));
hebsuperobject.biblemap.put("b11_14_26", new hebsuperobject(11,14,26,"ויקח את אצרות בית יהוה ואת אוצרות בית המלך ואת הכל לקח ויקח את כל מגני הזהב אשר עשה שלמה׃"));
hebsuperobject.biblemap.put("b11_14_27", new hebsuperobject(11,14,27,"ויעש המלך רחבעם תחתם מגני נחשת והפקיד על יד שרי הרצים השמרים פתח בית המלך׃"));
hebsuperobject.biblemap.put("b11_14_28", new hebsuperobject(11,14,28,"ויהי מדי בא המלך בית יהוה ישאום הרצים והשיבום אל תא הרצים׃"));
hebsuperobject.biblemap.put("b11_14_29", new hebsuperobject(11,14,29,"ויתר דברי רחבעם וכל אשר עשה הלא המה כתובים על ספר דברי הימים למלכי יהודה׃"));
hebsuperobject.biblemap.put("b11_14_30", new hebsuperobject(11,14,30,"ומלחמה היתה בין רחבעם ובין ירבעם כל הימים׃"));
hebsuperobject.biblemap.put("b11_14_31", new hebsuperobject(11,14,31,"וישכב רחבעם עם אבתיו ויקבר עם אבתיו בעיר דוד ושם אמו נעמה העמנית וימלך אבים בנו תחתיו׃"));
hebsuperobject.biblemap.put("b11_15_1", new hebsuperobject(11,15,1,"ובשנת שמנה עשרה למלך ירבעם בן נבט מלך אבים על יהודה׃"));
hebsuperobject.biblemap.put("b11_15_2", new hebsuperobject(11,15,2,"שלש שנים מלך בירושלם ושם אמו מעכה בת אבישלום׃"));
hebsuperobject.biblemap.put("b11_15_3", new hebsuperobject(11,15,3,"וילך בכל חטאות אביו אשר עשה לפניו ולא היה לבבו שלם עם יהוה אלהיו כלבב דוד אביו׃"));
hebsuperobject.biblemap.put("b11_15_4", new hebsuperobject(11,15,4,"כי למען דוד נתן יהוה אלהיו לו ניר בירושלם להקים את בנו אחריו ולהעמיד את ירושלם׃"));
hebsuperobject.biblemap.put("b11_15_5", new hebsuperobject(11,15,5,"אשר עשה דוד את הישר בעיני יהוה ולא סר מכל אשר צוהו כל ימי חייו רק בדבר אוריה החתי׃"));
hebsuperobject.biblemap.put("b11_15_6", new hebsuperobject(11,15,6,"ומלחמה היתה בין רחבעם ובין ירבעם כל ימי חייו׃"));
hebsuperobject.biblemap.put("b11_15_7", new hebsuperobject(11,15,7,"ויתר דברי אבים וכל אשר עשה הלוא הם כתובים על ספר דברי הימים למלכי יהודה ומלחמה היתה בין אבים ובין ירבעם׃"));
