hebsuperobject.biblemap.put("b11_22_7", new hebsuperobject(11,22,7,"ויאמר יהושפט האין פה נביא ליהוה עוד ונדרשה מאותו׃"));
hebsuperobject.biblemap.put("b11_22_8", new hebsuperobject(11,22,8,"ויאמר מלך ישראל אל יהושפט עוד איש אחד לדרש את יהוה מאתו ואני שנאתיו כי לא יתנבא עלי טוב כי אם רע מיכיהו בן ימלה ויאמר יהושפט אל יאמר המלך כן׃"));
hebsuperobject.biblemap.put("b11_22_9", new hebsuperobject(11,22,9,"ויקרא מלך ישראל אל סריס אחד ויאמר מהרה מיכיהו בן ימלה׃"));
hebsuperobject.biblemap.put("b11_22_10", new hebsuperobject(11,22,10,"ומלך ישראל ויהושפט מלך יהודה ישבים איש על כסאו מלבשים בגדים בגרן פתח שער שמרון וכל הנביאים מתנבאים לפניהם׃"));
hebsuperobject.biblemap.put("b11_22_11", new hebsuperobject(11,22,11,"ויעש לו צדקיה בן כנענה קרני ברזל ויאמר כה אמר יהוה באלה תנגח את ארם עד כלתם׃"));
hebsuperobject.biblemap.put("b11_22_12", new hebsuperobject(11,22,12,"וכל הנבאים נבאים כן לאמר עלה רמת גלעד והצלח ונתן יהוה ביד המלך׃"));
hebsuperobject.biblemap.put("b11_22_13", new hebsuperobject(11,22,13,"והמלאך אשר הלך לקרא מיכיהו דבר אליו לאמר הנה נא דברי הנביאים פה אחד טוב אל המלך יהי נא דבריך כדבר אחד מהם ודברת טוב׃"));
hebsuperobject.biblemap.put("b11_22_14", new hebsuperobject(11,22,14,"ויאמר מיכיהו חי יהוה כי את אשר יאמר יהוה אלי אתו אדבר׃"));
hebsuperobject.biblemap.put("b11_22_15", new hebsuperobject(11,22,15,"ויבוא אל המלך ויאמר המלך אליו מיכיהו הנלך אל רמת גלעד למלחמה אם נחדל ויאמר אליו עלה והצלח ונתן יהוה ביד המלך׃"));
hebsuperobject.biblemap.put("b11_22_16", new hebsuperobject(11,22,16,"ויאמר אליו המלך עד כמה פעמים אני משבעך אשר לא תדבר אלי רק אמת בשם יהוה׃"));
hebsuperobject.biblemap.put("b11_22_17", new hebsuperobject(11,22,17,"ויאמר ראיתי את כל ישראל נפצים אל ההרים כצאן אשר אין להם רעה ויאמר יהוה לא אדנים לאלה ישובו איש לביתו בשלום׃"));
hebsuperobject.biblemap.put("b11_22_18", new hebsuperobject(11,22,18,"ויאמר מלך ישראל אל יהושפט הלוא אמרתי אליך לוא יתנבא עלי טוב כי אם רע׃"));
hebsuperobject.biblemap.put("b11_22_19", new hebsuperobject(11,22,19,"ויאמר לכן שמע דבר יהוה ראיתי את יהוה ישב על כסאו וכל צבא השמים עמד עליו מימינו ומשמאלו׃"));
hebsuperobject.biblemap.put("b11_22_20", new hebsuperobject(11,22,20,"ויאמר יהוה מי יפתה את אחאב ויעל ויפל ברמת גלעד ויאמר זה בכה וזה אמר בכה׃"));
hebsuperobject.biblemap.put("b11_22_21", new hebsuperobject(11,22,21,"ויצא הרוח ויעמד לפני יהוה ויאמר אני אפתנו ויאמר יהוה אליו במה׃"));
hebsuperobject.biblemap.put("b11_22_22", new hebsuperobject(11,22,22,"ויאמר אצא והייתי רוח שקר בפי כל נביאיו ויאמר תפתה וגם תוכל צא ועשה כן׃"));
hebsuperobject.biblemap.put("b11_22_23", new hebsuperobject(11,22,23,"ועתה הנה נתן יהוה רוח שקר בפי כל נביאיך אלה ויהוה דבר עליך רעה׃"));
hebsuperobject.biblemap.put("b11_22_24", new hebsuperobject(11,22,24,"ויגש צדקיהו בן כנענה ויכה את מיכיהו על הלחי ויאמר אי זה עבר רוח יהוה מאתי לדבר אותך׃"));
hebsuperobject.biblemap.put("b11_22_25", new hebsuperobject(11,22,25,"ויאמר מיכיהו הנך ראה ביום ההוא אשר תבא חדר בחדר להחבה׃"));
hebsuperobject.biblemap.put("b11_22_26", new hebsuperobject(11,22,26,"ויאמר מלך ישראל קח את מיכיהו והשיבהו אל אמן שר העיר ואל יואש בן המלך׃"));
hebsuperobject.biblemap.put("b11_22_27", new hebsuperobject(11,22,27,"ואמרת כה אמר המלך שימו את זה בית הכלא והאכילהו לחם לחץ ומים לחץ עד באי בשלום׃"));
hebsuperobject.biblemap.put("b11_22_28", new hebsuperobject(11,22,28,"ויאמר מיכיהו אם שוב תשוב בשלום לא דבר יהוה בי ויאמר שמעו עמים כלם׃"));
hebsuperobject.biblemap.put("b11_22_29", new hebsuperobject(11,22,29,"ויעל מלך ישראל ויהושפט מלך יהודה רמת גלעד׃"));
hebsuperobject.biblemap.put("b11_22_30", new hebsuperobject(11,22,30,"ויאמר מלך ישראל אל יהושפט התחפש ובא במלחמה ואתה לבש בגדיך ויתחפש מלך ישראל ויבוא במלחמה׃"));
hebsuperobject.biblemap.put("b11_22_31", new hebsuperobject(11,22,31,"ומלך ארם צוה את שרי הרכב אשר לו שלשים ושנים לאמר לא תלחמו את קטן ואת גדול כי אם את מלך ישראל לבדו׃"));
hebsuperobject.biblemap.put("b11_22_32", new hebsuperobject(11,22,32,"ויהי כראות שרי הרכב את יהושפט והמה אמרו אך מלך ישראל הוא ויסרו עליו להלחם ויזעק יהושפט׃"));
hebsuperobject.biblemap.put("b11_22_33", new hebsuperobject(11,22,33,"ויהי כראות שרי הרכב כי לא מלך ישראל הוא וישובו מאחריו׃"));
hebsuperobject.biblemap.put("b11_22_34", new hebsuperobject(11,22,34,"ואיש משך בקשת לתמו ויכה את מלך ישראל בין הדבקים ובין השרין ויאמר לרכבו הפך ידך והוציאני מן המחנה כי החליתי׃"));
hebsuperobject.biblemap.put("b11_22_35", new hebsuperobject(11,22,35,"ותעלה המלחמה ביום ההוא והמלך היה מעמד במרכבה נכח ארם וימת בערב ויצק דם המכה אל חיק הרכב׃"));
hebsuperobject.biblemap.put("b11_22_36", new hebsuperobject(11,22,36,"ויעבר הרנה במחנה כבא השמש לאמר איש אל עירו ואיש אל ארצו׃"));
hebsuperobject.biblemap.put("b11_22_37", new hebsuperobject(11,22,37,"וימת המלך ויבוא שמרון ויקברו את המלך בשמרון׃"));
hebsuperobject.biblemap.put("b11_22_38", new hebsuperobject(11,22,38,"וישטף את הרכב על ברכת שמרון וילקו הכלבים את דמו והזנות רחצו כדבר יהוה אשר דבר׃"));
hebsuperobject.biblemap.put("b11_22_39", new hebsuperobject(11,22,39,"ויתר דברי אחאב וכל אשר עשה ובית השן אשר בנה וכל הערים אשר בנה הלוא הם כתובים על ספר דברי הימים למלכי ישראל׃"));
hebsuperobject.biblemap.put("b11_22_40", new hebsuperobject(11,22,40,"וישכב אחאב עם אבתיו וימלך אחזיהו בנו תחתיו׃"));
hebsuperobject.biblemap.put("b11_22_41", new hebsuperobject(11,22,41,"ויהושפט בן אסא מלך על יהודה בשנת ארבע לאחאב מלך ישראל׃"));
hebsuperobject.biblemap.put("b11_22_42", new hebsuperobject(11,22,42,"יהושפט בן שלשים וחמש שנה במלכו ועשרים וחמש שנה מלך בירושלם ושם אמו עזובה בת שלחי׃"));
hebsuperobject.biblemap.put("b11_22_43", new hebsuperobject(11,22,43,"וילך בכל דרך אסא אביו לא סר ממנו לעשות הישר בעיני יהוה אך הבמות לא סרו עוד העם מזבחים ומקטרים בבמות׃"));
hebsuperobject.biblemap.put("b11_22_44", new hebsuperobject(11,22,44,"וישלם יהושפט עם מלך ישראל׃"));
hebsuperobject.biblemap.put("b11_22_45", new hebsuperobject(11,22,45,"ויתר דברי יהושפט וגבורתו אשר עשה ואשר נלחם הלא הם כתובים על ספר דברי הימים למלכי יהודה׃"));
hebsuperobject.biblemap.put("b11_22_46", new hebsuperobject(11,22,46,"ויתר הקדש אשר נשאר בימי אסא אביו בער מן הארץ׃"));
hebsuperobject.biblemap.put("b11_22_47", new hebsuperobject(11,22,47,"ומלך אין באדום נצב מלך׃"));
hebsuperobject.biblemap.put("b11_22_48", new hebsuperobject(11,22,48,"יהושפט עשר אניות תרשיש ללכת אופירה לזהב ולא הלך כי נשברה אניות בעציון גבר׃"));
hebsuperobject.biblemap.put("b11_22_49", new hebsuperobject(11,22,49,"אז אמר אחזיהו בן אחאב אל יהושפט ילכו עבדי עם עבדיך באניות ולא אבה יהושפט׃"));
hebsuperobject.biblemap.put("b11_22_50", new hebsuperobject(11,22,50,"וישכב יהושפט עם אבתיו ויקבר עם אבתיו בעיר דוד אביו וימלך יהורם בנו תחתיו׃"));
hebsuperobject.biblemap.put("b11_22_51", new hebsuperobject(11,22,51,"אחזיהו בן אחאב מלך על ישראל בשמרון בשנת שבע עשרה ליהושפט מלך יהודה וימלך על ישראל שנתים׃"));
hebsuperobject.biblemap.put("b11_22_52", new hebsuperobject(11,22,52,"ויעש הרע בעיני יהוה וילך בדרך אביו ובדרך אמו ובדרך ירבעם בן נבט אשר החטיא את ישראל׃"));
hebsuperobject.biblemap.put("b11_22_53", new hebsuperobject(11,22,53,"ויעבד את הבעל וישתחוה לו ויכעס את יהוה אלהי ישראל ככל אשר עשה אביו׃"));

