hebsuperobject.biblemap.put("b12_21_7", new hebsuperobject(12,21,7,"וישם את פסל האשרה אשר עשה בבית אשר אמר יהוה אל דוד ואל שלמה בנו בבית הזה ובירושלם אשר בחרתי מכל שבטי ישראל אשים את שמי לעולם׃"));
hebsuperobject.biblemap.put("b12_21_8", new hebsuperobject(12,21,8,"ולא אסיף להניד רגל ישראל מן האדמה אשר נתתי לאבותם רק אם ישמרו לעשות ככל אשר צויתים ולכל התורה אשר צוה אתם עבדי משה׃"));
hebsuperobject.biblemap.put("b12_21_9", new hebsuperobject(12,21,9,"ולא שמעו ויתעם מנשה לעשות את הרע מן הגוים אשר השמיד יהוה מפני בני ישראל׃"));
hebsuperobject.biblemap.put("b12_21_10", new hebsuperobject(12,21,10,"וידבר יהוה ביד עבדיו הנביאים לאמר׃"));
hebsuperobject.biblemap.put("b12_21_11", new hebsuperobject(12,21,11,"יען אשר עשה מנשה מלך יהודה התעבות האלה הרע מכל אשר עשו האמרי אשר לפניו ויחטא גם את יהודה בגלוליו׃"));
hebsuperobject.biblemap.put("b12_21_12", new hebsuperobject(12,21,12,"לכן כה אמר יהוה אלהי ישראל הנני מביא רעה על ירושלם ויהודה אשר כל שמעיו תצלנה שתי אזניו׃"));
hebsuperobject.biblemap.put("b12_21_13", new hebsuperobject(12,21,13,"ונטיתי על ירושלם את קו שמרון ואת משקלת בית אחאב ומחיתי את ירושלם כאשר ימחה את הצלחת מחה והפך על פניה׃"));
hebsuperobject.biblemap.put("b12_21_14", new hebsuperobject(12,21,14,"ונטשתי את שארית נחלתי ונתתים ביד איביהם והיו לבז ולמשסה לכל איביהם׃"));
hebsuperobject.biblemap.put("b12_21_15", new hebsuperobject(12,21,15,"יען אשר עשו את הרע בעיני ויהיו מכעסים אתי מן היום אשר יצאו אבותם ממצרים ועד היום הזה׃"));
hebsuperobject.biblemap.put("b12_21_16", new hebsuperobject(12,21,16,"וגם דם נקי שפך מנשה הרבה מאד עד אשר מלא את ירושלם פה לפה לבד מחטאתו אשר החטיא את יהודה לעשות הרע בעיני יהוה׃"));
hebsuperobject.biblemap.put("b12_21_17", new hebsuperobject(12,21,17,"ויתר דברי מנשה וכל אשר עשה וחטאתו אשר חטא הלא הם כתובים על ספר דברי הימים למלכי יהודה׃"));
hebsuperobject.biblemap.put("b12_21_18", new hebsuperobject(12,21,18,"וישכב מנשה עם אבתיו ויקבר בגן ביתו בגן עזא וימלך אמון בנו תחתיו׃"));
hebsuperobject.biblemap.put("b12_21_19", new hebsuperobject(12,21,19,"בן עשרים ושתים שנה אמון במלכו ושתים שנים מלך בירושלם ושם אמו משלמת בת חרוץ מן יטבה׃"));
hebsuperobject.biblemap.put("b12_21_20", new hebsuperobject(12,21,20,"ויעש הרע בעיני יהוה כאשר עשה מנשה אביו׃"));
hebsuperobject.biblemap.put("b12_21_21", new hebsuperobject(12,21,21,"וילך בכל הדרך אשר הלך אביו ויעבד את הגללים אשר עבד אביו וישתחו להם׃"));
hebsuperobject.biblemap.put("b12_21_22", new hebsuperobject(12,21,22,"ויעזב את יהוה אלהי אבתיו ולא הלך בדרך יהוה׃"));
hebsuperobject.biblemap.put("b12_21_23", new hebsuperobject(12,21,23,"ויקשרו עבדי אמון עליו וימיתו את המלך בביתו׃"));
hebsuperobject.biblemap.put("b12_21_24", new hebsuperobject(12,21,24,"ויך עם הארץ את כל הקשרים על המלך אמון וימליכו עם הארץ את יאשיהו בנו תחתיו׃"));
hebsuperobject.biblemap.put("b12_21_25", new hebsuperobject(12,21,25,"ויתר דברי אמון אשר עשה הלא הם כתובים על ספר דברי הימים למלכי יהודה׃"));
hebsuperobject.biblemap.put("b12_21_26", new hebsuperobject(12,21,26,"ויקבר אתו בקברתו בגן עזא וימלך יאשיהו בנו תחתיו׃"));
hebsuperobject.biblemap.put("b12_22_1", new hebsuperobject(12,22,1,"בן שמנה שנה יאשיהו במלכו ושלשים ואחת שנה מלך בירושלם ושם אמו ידידה בת עדיה מבצקת׃"));
hebsuperobject.biblemap.put("b12_22_2", new hebsuperobject(12,22,2,"ויעש הישר בעיני יהוה וילך בכל דרך דוד אביו ולא סר ימין ושמאול׃"));
hebsuperobject.biblemap.put("b12_22_3", new hebsuperobject(12,22,3,"ויהי בשמנה עשרה שנה למלך יאשיהו שלח המלך את שפן בן אצליהו בן משלם הספר בית יהוה לאמר׃"));
hebsuperobject.biblemap.put("b12_22_4", new hebsuperobject(12,22,4,"עלה אל חלקיהו הכהן הגדול ויתם את הכסף המובא בית יהוה אשר אספו שמרי הסף מאת העם׃"));
hebsuperobject.biblemap.put("b12_22_5", new hebsuperobject(12,22,5,"ויתנה על יד עשי המלאכה המפקדים בבית יהוה ויתנו אתו לעשי המלאכה אשר בבית יהוה לחזק בדק הבית׃"));
hebsuperobject.biblemap.put("b12_22_6", new hebsuperobject(12,22,6,"לחרשים ולבנים ולגדרים ולקנות עצים ואבני מחצב לחזק את הבית׃"));
hebsuperobject.biblemap.put("b12_22_7", new hebsuperobject(12,22,7,"אך לא יחשב אתם הכסף הנתן על ידם כי באמונה הם עשים׃"));
hebsuperobject.biblemap.put("b12_22_8", new hebsuperobject(12,22,8,"ויאמר חלקיהו הכהן הגדול על שפן הספר ספר התורה מצאתי בבית יהוה ויתן חלקיה את הספר אל שפן ויקראהו׃"));
hebsuperobject.biblemap.put("b12_22_9", new hebsuperobject(12,22,9,"ויבא שפן הספר אל המלך וישב את המלך דבר ויאמר התיכו עבדיך את הכסף הנמצא בבית ויתנהו על יד עשי המלאכה המפקדים בית יהוה׃"));
hebsuperobject.biblemap.put("b12_22_10", new hebsuperobject(12,22,10,"ויגד שפן הספר למלך לאמר ספר נתן לי חלקיה הכהן ויקראהו שפן לפני המלך׃"));
hebsuperobject.biblemap.put("b12_22_11", new hebsuperobject(12,22,11,"ויהי כשמע המלך את דברי ספר התורה ויקרע את בגדיו׃"));
hebsuperobject.biblemap.put("b12_22_12", new hebsuperobject(12,22,12,"ויצו המלך את חלקיה הכהן ואת אחיקם בן שפן ואת עכבור בן מיכיה ואת שפן הספר ואת עשיה עבד המלך לאמר׃"));
hebsuperobject.biblemap.put("b12_22_13", new hebsuperobject(12,22,13,"לכו דרשו את יהוה בעדי ובעד העם ובעד כל יהודה על דברי הספר הנמצא הזה כי גדולה חמת יהוה אשר היא נצתה בנו על אשר לא שמעו אבתינו על דברי הספר הזה לעשות ככל הכתוב עלינו׃"));
hebsuperobject.biblemap.put("b12_22_14", new hebsuperobject(12,22,14,"וילך חלקיהו הכהן ואחיקם ועכבור ושפן ועשיה אל חלדה הנביאה אשת שלם בן תקוה בן חרחס שמר הבגדים והיא ישבת בירושלם במשנה וידברו אליה׃"));
hebsuperobject.biblemap.put("b12_22_15", new hebsuperobject(12,22,15,"ותאמר אליהם כה אמר יהוה אלהי ישראל אמרו לאיש אשר שלח אתכם אלי׃"));
hebsuperobject.biblemap.put("b12_22_16", new hebsuperobject(12,22,16,"כה אמר יהוה הנני מביא רעה אל המקום הזה ועל ישביו את כל דברי הספר אשר קרא מלך יהודה׃"));
hebsuperobject.biblemap.put("b12_22_17", new hebsuperobject(12,22,17,"תחת אשר עזבוני ויקטרו לאלהים אחרים למען הכעיסני בכל מעשה ידיהם ונצתה חמתי במקום הזה ולא תכבה׃"));
hebsuperobject.biblemap.put("b12_22_18", new hebsuperobject(12,22,18,"ואל מלך יהודה השלח אתכם לדרש את יהוה כה תאמרו אליו כה אמר יהוה אלהי ישראל הדברים אשר שמעת׃"));
hebsuperobject.biblemap.put("b12_22_19", new hebsuperobject(12,22,19,"יען רך לבבך ותכנע מפני יהוה בשמעך אשר דברתי על המקום הזה ועל ישביו להיות לשמה ולקללה ותקרע את בגדיך ותבכה לפני וגם אנכי שמעתי נאם יהוה׃"));
hebsuperobject.biblemap.put("b12_22_20", new hebsuperobject(12,22,20,"לכן הנני אספך על אבתיך ונאספת אל קברתיך בשלום ולא תראינה עיניך בכל הרעה אשר אני מביא על המקום הזה וישיבו את המלך דבר׃"));
hebsuperobject.biblemap.put("b12_23_1", new hebsuperobject(12,23,1,"וישלח המלך ויאספו אליו כל זקני יהודה וירושלם׃"));
hebsuperobject.biblemap.put("b12_23_2", new hebsuperobject(12,23,2,"ויעל המלך בית יהוה וכל איש יהודה וכל ישבי ירושלם אתו והכהנים והנביאים וכל העם למקטן ועד גדול ויקרא באזניהם את כל דברי ספר הברית הנמצא בבית יהוה׃"));
hebsuperobject.biblemap.put("b12_23_3", new hebsuperobject(12,23,3,"ויעמד המלך על העמוד ויכרת את הברית לפני יהוה ללכת אחר יהוה ולשמר מצותיו ואת עדותיו ואת חקתיו בכל לב ובכל נפש להקים את דברי הברית הזאת הכתבים על הספר הזה ויעמד כל העם בברית׃"));
hebsuperobject.biblemap.put("b12_23_4", new hebsuperobject(12,23,4,"ויצו המלך את חלקיהו הכהן הגדול ואת כהני המשנה ואת שמרי הסף להוציא מהיכל יהוה את כל הכלים העשוים לבעל ולאשרה ולכל צבא השמים וישרפם מחוץ לירושלם בשדמות קדרון ונשא את עפרם בית אל׃"));
hebsuperobject.biblemap.put("b12_23_5", new hebsuperobject(12,23,5,"והשבית את הכמרים אשר נתנו מלכי יהודה ויקטר בבמות בערי יהודה ומסבי ירושלם ואת המקטרים לבעל לשמש ולירח ולמזלות ולכל צבא השמים׃"));
hebsuperobject.biblemap.put("b12_23_6", new hebsuperobject(12,23,6,"ויצא את האשרה מבית יהוה מחוץ לירושלם אל נחל קדרון וישרף אתה בנחל קדרון וידק לעפר וישלך את עפרה על קבר בני העם׃"));
hebsuperobject.biblemap.put("b12_23_7", new hebsuperobject(12,23,7,"ויתץ את בתי הקדשים אשר בבית יהוה אשר הנשים ארגות שם בתים לאשרה׃"));
hebsuperobject.biblemap.put("b12_23_8", new hebsuperobject(12,23,8,"ויבא את כל הכהנים מערי יהודה ויטמא את הבמות אשר קטרו שמה הכהנים מגבע עד באר שבע ונתץ את במות השערים אשר פתח שער יהושע שר העיר אשר על שמאול איש בשער העיר׃"));
hebsuperobject.biblemap.put("b12_23_9", new hebsuperobject(12,23,9,"אך לא יעלו כהני הבמות אל מזבח יהוה בירושלם כי אם אכלו מצות בתוך אחיהם׃"));
hebsuperobject.biblemap.put("b12_23_10", new hebsuperobject(12,23,10,"וטמא את התפת אשר בגי בני הנם לבלתי להעביר איש את בנו ואת בתו באש למלך׃"));
hebsuperobject.biblemap.put("b12_23_11", new hebsuperobject(12,23,11,"וישבת את הסוסים אשר נתנו מלכי יהודה לשמש מבא בית יהוה אל לשכת נתן מלך הסריס אשר בפרורים ואת מרכבות השמש שרף באש׃"));
hebsuperobject.biblemap.put("b12_23_12", new hebsuperobject(12,23,12,"ואת המזבחות אשר על הגג עלית אחז אשר עשו מלכי יהודה ואת המזבחות אשר עשה מנשה בשתי חצרות בית יהוה נתץ המלך וירץ משם והשליך את עפרם אל נחל קדרון׃"));
hebsuperobject.biblemap.put("b12_23_13", new hebsuperobject(12,23,13,"ואת הבמות אשר על פני ירושלם אשר מימין להר המשחית אשר בנה שלמה מלך ישראל לעשתרת שקץ צידנים ולכמוש שקץ מואב ולמלכם תועבת בני עמון טמא המלך׃"));
hebsuperobject.biblemap.put("b12_23_14", new hebsuperobject(12,23,14,"ושבר את המצבות ויכרת את האשרים וימלא את מקומם עצמות אדם׃"));
hebsuperobject.biblemap.put("b12_23_15", new hebsuperobject(12,23,15,"וגם את המזבח אשר בבית אל הבמה אשר עשה ירבעם בן נבט אשר החטיא את ישראל גם את המזבח ההוא ואת הבמה נתץ וישרף את הבמה הדק לעפר ושרף אשרה׃"));
hebsuperobject.biblemap.put("b12_23_16", new hebsuperobject(12,23,16,"ויפן יאשיהו וירא את הקברים אשר שם בהר וישלח ויקח את העצמות מן הקברים וישרף על המזבח ויטמאהו כדבר יהוה אשר קרא איש האלהים אשר קרא את הדברים האלה׃"));
hebsuperobject.biblemap.put("b12_23_17", new hebsuperobject(12,23,17,"ויאמר מה הציון הלז אשר אני ראה ויאמרו אליו אנשי העיר הקבר איש האלהים אשר בא מיהודה ויקרא את הדברים האלה אשר עשית על המזבח בית אל׃"));
hebsuperobject.biblemap.put("b12_23_18", new hebsuperobject(12,23,18,"ויאמר הניחו לו איש אל ינע עצמתיו וימלטו עצמתיו את עצמות הנביא אשר בא משמרון׃"));
hebsuperobject.biblemap.put("b12_23_19", new hebsuperobject(12,23,19,"וגם את כל בתי הבמות אשר בערי שמרון אשר עשו מלכי ישראל להכעיס הסיר יאשיהו ויעש להם ככל המעשים אשר עשה בבית אל׃"));
hebsuperobject.biblemap.put("b12_23_20", new hebsuperobject(12,23,20,"ויזבח את כל כהני הבמות אשר שם על המזבחות וישרף את עצמות אדם עליהם וישב ירושלם׃"));
hebsuperobject.biblemap.put("b12_23_21", new hebsuperobject(12,23,21,"ויצו המלך את כל העם לאמר עשו פסח ליהוה אלהיכם ככתוב על ספר הברית הזה׃"));
hebsuperobject.biblemap.put("b12_23_22", new hebsuperobject(12,23,22,"כי לא נעשה כפסח הזה מימי השפטים אשר שפטו את ישראל וכל ימי מלכי ישראל ומלכי יהודה׃"));
hebsuperobject.biblemap.put("b12_23_23", new hebsuperobject(12,23,23,"כי אם בשמנה עשרה שנה למלך יאשיהו נעשה הפסח הזה ליהוה בירושלם׃"));
hebsuperobject.biblemap.put("b12_23_24", new hebsuperobject(12,23,24,"וגם את האבות ואת הידענים ואת התרפים ואת הגללים ואת כל השקצים אשר נראו בארץ יהודה ובירושלם בער יאשיהו למען הקים את דברי התורה הכתבים על הספר אשר מצא חלקיהו הכהן בית יהוה׃"));
hebsuperobject.biblemap.put("b12_23_25", new hebsuperobject(12,23,25,"וכמהו לא היה לפניו מלך אשר שב אל יהוה בכל לבבו ובכל נפשו ובכל מאדו ככל תורת משה ואחריו לא קם כמהו׃"));
hebsuperobject.biblemap.put("b12_23_26", new hebsuperobject(12,23,26,"אך לא שב יהוה מחרון אפו הגדול אשר חרה אפו ביהודה על כל הכעסים אשר הכעיסו מנשה׃"));
hebsuperobject.biblemap.put("b12_23_27", new hebsuperobject(12,23,27,"ויאמר יהוה גם את יהודה אסיר מעל פני כאשר הסרתי את ישראל ומאסתי את העיר הזאת אשר בחרתי את ירושלם ואת הבית אשר אמרתי יהיה שמי שם׃"));
