hebsuperobject.biblemap.put("b13_19_19", new hebsuperobject(13,19,19,"ויראו עבדי הדדעזר כי נגפו לפני ישראל וישלימו עם דויד ויעבדהו ולא אבה ארם להושיע את בני עמון עוד׃"));
hebsuperobject.biblemap.put("b13_20_1", new hebsuperobject(13,20,1,"ויהי לעת תשובת השנה לעת צאת המלכים וינהג יואב את חיל הצבא וישחת את ארץ בני עמון ויבא ויצר את רבה ודויד ישב בירושלם ויך יואב את רבה ויהרסה׃"));
hebsuperobject.biblemap.put("b13_20_2", new hebsuperobject(13,20,2,"ויקח דויד את עטרת מלכם מעל ראשו וימצאה משקל ככר זהב ובה אבן יקרה ותהי על ראש דויד ושלל העיר הוציא הרבה מאד׃"));
hebsuperobject.biblemap.put("b13_20_3", new hebsuperobject(13,20,3,"ואת העם אשר בה הוציא וישר במגרה ובחריצי הברזל ובמגרות וכן יעשה דויד לכל ערי בני עמון וישב דויד וכל העם ירושלם׃"));
hebsuperobject.biblemap.put("b13_20_4", new hebsuperobject(13,20,4,"ויהי אחריכן ותעמד מלחמה בגזר עם פלשתים אז הכה סבכי החשתי את ספי מילדי הרפאים ויכנעו׃"));
hebsuperobject.biblemap.put("b13_20_5", new hebsuperobject(13,20,5,"ותהי עוד מלחמה את פלשתים ויך אלחנן בן יעור את לחמי אחי גלית הגתי ועץ חניתו כמנור ארגים׃"));
hebsuperobject.biblemap.put("b13_20_6", new hebsuperobject(13,20,6,"ותהי עוד מלחמה בגת ויהי איש מדה ואצבעתיו שש ושש עשרים וארבע וגם הוא נולד להרפא׃"));
hebsuperobject.biblemap.put("b13_20_7", new hebsuperobject(13,20,7,"ויחרף את ישראל ויכהו יהונתן בן שמעא אחי דויד׃"));
