hebsuperobject.biblemap.put("b13_21_4", new hebsuperobject(13,21,4,"ודבר המלך חזק על יואב ויצא יואב ויתהלך בכל ישראל ויבא ירושלם׃"));
hebsuperobject.biblemap.put("b13_21_5", new hebsuperobject(13,21,5,"ויתן יואב את מספר מפקד העם אל דויד ויהי כל ישראל אלף אלפים ומאה אלף איש שלף חרב ויהודה ארבע מאות ושבעים אלף איש שלף חרב׃"));
