hebsuperobject.biblemap.put("b14_5_5", new hebsuperobject(14,5,5,"ויעלו את הארון ואת אהל מועד ואת כל כלי הקדש אשר באהל העלו אתם הכהנים הלוים׃"));
hebsuperobject.biblemap.put("b14_5_6", new hebsuperobject(14,5,6,"והמלך שלמה וכל עדת ישראל הנועדים עליו לפני הארון מזבחים צאן ובקר אשר לא יספרו ולא ימנו מרב׃"));
hebsuperobject.biblemap.put("b14_5_7", new hebsuperobject(14,5,7,"ויביאו הכהנים את ארון ברית יהוה אל מקומו אל דביר הבית אל קדש הקדשים אל תחת כנפי הכרובים׃"));
hebsuperobject.biblemap.put("b14_5_8", new hebsuperobject(14,5,8,"ויהיו הכרובים פרשים כנפים על מקום הארון ויכסו הכרובים על הארון ועל בדיו מלמעלה׃"));
hebsuperobject.biblemap.put("b14_5_9", new hebsuperobject(14,5,9,"ויאריכו הבדים ויראו ראשי הבדים מן הארון על פני הדביר ולא יראו החוצה ויהי שם עד היום הזה׃"));
hebsuperobject.biblemap.put("b14_5_10", new hebsuperobject(14,5,10,"אין בארון רק שני הלחות אשר נתן משה בחרב אשר כרת יהוה עם בני ישראל בצאתם ממצרים׃"));
hebsuperobject.biblemap.put("b14_5_11", new hebsuperobject(14,5,11,"ויהי בצאת הכהנים מן הקדש כי כל הכהנים הנמצאים התקדשו אין לשמור למחלקות׃"));
hebsuperobject.biblemap.put("b14_5_12", new hebsuperobject(14,5,12,"והלוים המשררים לכלם לאסף להימן לידתון ולבניהם ולאחיהם מלבשים בוץ במצלתים ובנבלים וכנרות עמדים מזרח למזבח ועמהם כהנים למאה ועשרים מחצררים בחצצרות׃"));
hebsuperobject.biblemap.put("b14_5_13", new hebsuperobject(14,5,13,"ויהי כאחד למחצצרים ולמשררים להשמיע קול אחד להלל ולהדות ליהוה וכהרים קול בחצצרות ובמצלתים ובכלי השיר ובהלל ליהוה כי טוב כי לעולם חסדו והבית מלא ענן בית יהוה׃"));
hebsuperobject.biblemap.put("b14_5_14", new hebsuperobject(14,5,14,"ולא יכלו הכהנים לעמוד לשרת מפני הענן כי מלא כבוד יהוה את בית האלהים׃"));
hebsuperobject.biblemap.put("b14_6_1", new hebsuperobject(14,6,1,"אז אמר שלמה יהוה אמר לשכון בערפל׃"));
hebsuperobject.biblemap.put("b14_6_2", new hebsuperobject(14,6,2,"ואני בניתי בית זבל לך ומכון לשבתך עולמים׃"));
hebsuperobject.biblemap.put("b14_6_3", new hebsuperobject(14,6,3,"ויסב המלך את פניו ויברך את כל קהל ישראל וכל קהל ישראל עומד׃"));
hebsuperobject.biblemap.put("b14_6_4", new hebsuperobject(14,6,4,"ויאמר ברוך יהוה אלהי ישראל אשר דבר בפיו את דויד אבי ובידיו מלא לאמר׃"));
hebsuperobject.biblemap.put("b14_6_5", new hebsuperobject(14,6,5,"מן היום אשר הוצאתי את עמי מארץ מצרים לא בחרתי בעיר מכל שבטי ישראל לבנות בית להיות שמי שם ולא בחרתי באיש להיות נגיד על עמי ישראל׃"));
hebsuperobject.biblemap.put("b14_6_6", new hebsuperobject(14,6,6,"ואבחר בירושלם להיות שמי שם ואבחר בדויד להיות על עמי ישראל׃"));
hebsuperobject.biblemap.put("b14_6_7", new hebsuperobject(14,6,7,"ויהי עם לבב דויד אבי לבנות בית לשם יהוה אלהי ישראל׃"));
hebsuperobject.biblemap.put("b14_6_8", new hebsuperobject(14,6,8,"ויאמר יהוה אל דויד אבי יען אשר היה עם לבבך לבנות בית לשמי הטיבות כי היה עם לבבך׃"));
hebsuperobject.biblemap.put("b14_6_9", new hebsuperobject(14,6,9,"רק אתה לא תבנה הבית כי בנך היוצא מחלציך הוא יבנה הבית לשמי׃"));
hebsuperobject.biblemap.put("b14_6_10", new hebsuperobject(14,6,10,"ויקם יהוה את דברו אשר דבר ואקום תחת דויד אבי ואשב על כסא ישראל כאשר דבר יהוה ואבנה הבית לשם יהוה אלהי ישראל׃"));
hebsuperobject.biblemap.put("b14_6_11", new hebsuperobject(14,6,11,"ואשים שם את הארון אשר שם ברית יהוה אשר כרת עם בני ישראל׃"));
hebsuperobject.biblemap.put("b14_6_12", new hebsuperobject(14,6,12,"ויעמד לפני מזבח יהוה נגד כל קהל ישראל ויפרש כפיו׃"));
hebsuperobject.biblemap.put("b14_6_13", new hebsuperobject(14,6,13,"כי עשה שלמה כיור נחשת ויתנהו בתוך העזרה חמש אמות ארכו וחמש אמות רחבו ואמות שלוש קומתו ויעמד עליו ויברך על ברכיו נגד כל קהל ישראל ויפרש כפיו השמימה׃"));
hebsuperobject.biblemap.put("b14_6_14", new hebsuperobject(14,6,14,"ויאמר יהוה אלהי ישראל אין כמוך אלהים בשמים ובארץ שמר הברית והחסד לעבדיך ההלכים לפניך בכל לבם׃"));
hebsuperobject.biblemap.put("b14_6_15", new hebsuperobject(14,6,15,"אשר שמרת לעבדך דויד אבי את אשר דברת לו ותדבר בפיך ובידך מלאת כיום הזה׃"));
hebsuperobject.biblemap.put("b14_6_16", new hebsuperobject(14,6,16,"ועתה יהוה אלהי ישראל שמר לעבדך דויד אבי את אשר דברת לו לאמר לא יכרת לך איש מלפני יושב על כסא ישראל רק אם ישמרו בניך את דרכם ללכת בתורתי כאשר הלכת לפני׃"));
hebsuperobject.biblemap.put("b14_6_17", new hebsuperobject(14,6,17,"ועתה יהוה אלהי ישראל יאמן דברך אשר דברת לעבדך לדויד׃"));
hebsuperobject.biblemap.put("b14_6_18", new hebsuperobject(14,6,18,"כי האמנם ישב אלהים את האדם על הארץ הנה שמים ושמי השמים לא יכלכלוך אף כי הבית הזה אשר בניתי׃"));
hebsuperobject.biblemap.put("b14_6_19", new hebsuperobject(14,6,19,"ופנית אל תפלת עבדך ואל תחנתו יהוה אלהי לשמע אל הרנה ואל התפלה אשר עבדך מתפלל לפניך׃"));
hebsuperobject.biblemap.put("b14_6_20", new hebsuperobject(14,6,20,"להיות עיניך פתחות אל הבית הזה יומם ולילה אל המקום אשר אמרת לשום שמך שם לשמוע אל התפלה אשר יתפלל עבדך אל המקום הזה׃"));
hebsuperobject.biblemap.put("b14_6_21", new hebsuperobject(14,6,21,"ושמעת אל תחנוני עבדך ועמך ישראל אשר יתפללו אל המקום הזה ואתה תשמע ממקום שבתך מן השמים ושמעת וסלחת׃"));
hebsuperobject.biblemap.put("b14_6_22", new hebsuperobject(14,6,22,"אם יחטא איש לרעהו ונשא בו אלה להאלתו ובא אלה לפני מזבחך בבית הזה׃"));
hebsuperobject.biblemap.put("b14_6_23", new hebsuperobject(14,6,23,"ואתה תשמע מן השמים ועשית ושפטת את עבדיך להשיב לרשע לתת דרכו בראשו ולהצדיק צדיק לתת לו כצדקתו׃"));
hebsuperobject.biblemap.put("b14_6_24", new hebsuperobject(14,6,24,"ואם ינגף עמך ישראל לפני אויב כי יחטאו לך ושבו והודו את שמך והתפללו והתחננו לפניך בבית הזה׃"));
hebsuperobject.biblemap.put("b14_6_25", new hebsuperobject(14,6,25,"ואתה תשמע מן השמים וסלחת לחטאת עמך ישראל והשיבותם אל האדמה אשר נתתה להם ולאבתיהם׃"));
hebsuperobject.biblemap.put("b14_6_26", new hebsuperobject(14,6,26,"בהעצר השמים ולא יהיה מטר כי יחטאו לך והתפללו אל המקום הזה והודו את שמך מחטאתם ישובון כי תענם׃"));
hebsuperobject.biblemap.put("b14_6_27", new hebsuperobject(14,6,27,"ואתה תשמע השמים וסלחת לחטאת עבדיך ועמך ישראל כי תורם אל הדרך הטובה אשר ילכו בה ונתתה מטר על ארצך אשר נתתה לעמך לנחלה׃"));
hebsuperobject.biblemap.put("b14_6_28", new hebsuperobject(14,6,28,"רעב כי יהיה בארץ דבר כי יהיה שדפון וירקון ארבה וחסיל כי יהיה כי יצר לו אויביו בארץ שעריו כל נגע וכל מחלה׃"));
hebsuperobject.biblemap.put("b14_6_29", new hebsuperobject(14,6,29,"כל תפלה כל תחנה אשר יהיה לכל האדם ולכל עמך ישראל אשר ידעו איש נגעו ומכאבו ופרש כפיו אל הבית הזה׃"));
hebsuperobject.biblemap.put("b14_6_30", new hebsuperobject(14,6,30,"ואתה תשמע מן השמים מכון שבתך וסלחת ונתתה לאיש ככל דרכיו אשר תדע את לבבו כי אתה לבדך ידעת את לבב בני האדם׃"));
hebsuperobject.biblemap.put("b14_6_31", new hebsuperobject(14,6,31,"למען ייראוך ללכת בדרכיך כל הימים אשר הם חיים על פני האדמה אשר נתתה לאבתינו׃"));
hebsuperobject.biblemap.put("b14_6_32", new hebsuperobject(14,6,32,"וגם אל הנכרי אשר לא מעמך ישראל הוא ובא מארץ רחוקה למען שמך הגדול וידך החזקה וזרועך הנטויה ובאו והתפללו אל הבית הזה׃"));
hebsuperobject.biblemap.put("b14_6_33", new hebsuperobject(14,6,33,"ואתה תשמע מן השמים ממכון שבתך ועשית ככל אשר יקרא אליך הנכרי למען ידעו כל עמי הארץ את שמך וליראה אתך כעמך ישראל ולדעת כי שמך נקרא על הבית הזה אשר בניתי׃"));
hebsuperobject.biblemap.put("b14_6_34", new hebsuperobject(14,6,34,"כי יצא עמך למלחמה על אויביו בדרך אשר תשלחם והתפללו אליך דרך העיר הזאת אשר בחרת בה והבית אשר בניתי לשמך׃"));
hebsuperobject.biblemap.put("b14_6_35", new hebsuperobject(14,6,35,"ושמעת מן השמים את תפלתם ואת תחנתם ועשית משפטם׃"));
hebsuperobject.biblemap.put("b14_6_36", new hebsuperobject(14,6,36,"כי יחטאו לך כי אין אדם אשר לא יחטא ואנפת בם ונתתם לפני אויב ושבום שוביהם אל ארץ רחוקה או קרובה׃"));
hebsuperobject.biblemap.put("b14_6_37", new hebsuperobject(14,6,37,"והשיבו אל לבבם בארץ אשר נשבו שם ושבו והתחננו אליך בארץ שבים לאמר חטאנו העוינו ורשענו׃"));
hebsuperobject.biblemap.put("b14_6_38", new hebsuperobject(14,6,38,"ושבו אליך בכל לבם ובכל נפשם בארץ שבים אשר שבו אתם והתפללו דרך ארצם אשר נתתה לאבותם והעיר אשר בחרת ולבית אשר בניתי לשמך׃"));
hebsuperobject.biblemap.put("b14_6_39", new hebsuperobject(14,6,39,"ושמעת מן השמים ממכון שבתך את תפלתם ואת תחנתיהם ועשית משפטם וסלחת לעמך אשר חטאו לך׃"));
hebsuperobject.biblemap.put("b14_6_40", new hebsuperobject(14,6,40,"עתה אלהי יהיו נא עיניך פתחות ואזניך קשבות לתפלת המקום הזה׃"));
hebsuperobject.biblemap.put("b14_6_41", new hebsuperobject(14,6,41,"ועתה קומה יהוה אלהים לנוחך אתה וארון עזך כהניך יהוה אלהים ילבשו תשועה וחסידיך ישמחו בטוב׃"));
hebsuperobject.biblemap.put("b14_6_42", new hebsuperobject(14,6,42,"יהוה אלהים אל תשב פני משיחיך זכרה לחסדי דויד עבדך׃"));
hebsuperobject.biblemap.put("b14_7_1", new hebsuperobject(14,7,1,"וככלות שלמה להתפלל והאש ירדה מהשמים ותאכל העלה והזבחים וכבוד יהוה מלא את הבית׃"));
hebsuperobject.biblemap.put("b14_7_2", new hebsuperobject(14,7,2,"ולא יכלו הכהנים לבוא אל בית יהוה כי מלא כבוד יהוה את בית יהוה׃"));
hebsuperobject.biblemap.put("b14_7_3", new hebsuperobject(14,7,3,"וכל בני ישראל ראים ברדת האש וכבוד יהוה על הבית ויכרעו אפים ארצה על הרצפה וישתחוו והודות ליהוה כי טוב כי לעולם חסדו׃"));
hebsuperobject.biblemap.put("b14_7_4", new hebsuperobject(14,7,4,"והמלך וכל העם זבחים זבח לפני יהוה׃"));
hebsuperobject.biblemap.put("b14_7_5", new hebsuperobject(14,7,5,"ויזבח המלך שלמה את זבח הבקר עשרים ושנים אלף וצאן מאה ועשרים אלף ויחנכו את בית האלהים המלך וכל העם׃"));
hebsuperobject.biblemap.put("b14_7_6", new hebsuperobject(14,7,6,"והכהנים על משמרותם עמדים והלוים בכלי שיר יהוה אשר עשה דויד המלך להדות ליהוה כי לעולם חסדו בהלל דויד בידם והכהנים מחצצרים נגדם וכל ישראל עמדים׃"));
hebsuperobject.biblemap.put("b14_7_7", new hebsuperobject(14,7,7,"ויקדש שלמה את תוך החצר אשר לפני בית יהוה כי עשה שם העלות ואת חלבי השלמים כי מזבח הנחשת אשר עשה שלמה לא יכול להכיל את העלה ואת המנחה ואת החלבים׃"));
hebsuperobject.biblemap.put("b14_7_8", new hebsuperobject(14,7,8,"ויעש שלמה את החג בעת ההיא שבעת ימים וכל ישראל עמו קהל גדול מאד מלבוא חמת עד נחל מצרים׃"));
hebsuperobject.biblemap.put("b14_7_9", new hebsuperobject(14,7,9,"ויעשו ביום השמיני עצרת כי חנכת המזבח עשו שבעת ימים והחג שבעת ימים׃"));
hebsuperobject.biblemap.put("b14_7_10", new hebsuperobject(14,7,10,"וביום עשרים ושלשה לחדש השביעי שלח את העם לאהליהם שמחים וטובי לב על הטובה אשר עשה יהוה לדויד ולשלמה ולישראל עמו׃"));
hebsuperobject.biblemap.put("b14_7_11", new hebsuperobject(14,7,11,"ויכל שלמה את בית יהוה ואת בית המלך ואת כל הבא על לב שלמה לעשות בבית יהוה ובביתו הצליח׃"));
hebsuperobject.biblemap.put("b14_7_12", new hebsuperobject(14,7,12,"וירא יהוה אל שלמה בלילה ויאמר לו שמעתי את תפלתך ובחרתי במקום הזה לי לבית זבח׃"));
hebsuperobject.biblemap.put("b14_7_13", new hebsuperobject(14,7,13,"הן אעצר השמים ולא יהיה מטר והן אצוה על חגב לאכול הארץ ואם אשלח דבר בעמי׃"));
hebsuperobject.biblemap.put("b14_7_14", new hebsuperobject(14,7,14,"ויכנעו עמי אשר נקרא שמי עליהם ויתפללו ויבקשו פני וישבו מדרכיהם הרעים ואני אשמע מן השמים ואסלח לחטאתם וארפא את ארצם׃"));
hebsuperobject.biblemap.put("b14_7_15", new hebsuperobject(14,7,15,"עתה עיני יהיו פתחות ואזני קשבות לתפלת המקום הזה׃"));
hebsuperobject.biblemap.put("b14_7_16", new hebsuperobject(14,7,16,"ועתה בחרתי והקדשתי את הבית הזה להיות שמי שם עד עולם והיו עיני ולבי שם כל הימים׃"));
hebsuperobject.biblemap.put("b14_7_17", new hebsuperobject(14,7,17,"ואתה אם תלך לפני כאשר הלך דויד אביך ולעשות ככל אשר צויתיך וחקי ומשפטי תשמור׃"));
hebsuperobject.biblemap.put("b14_7_18", new hebsuperobject(14,7,18,"והקימותי את כסא מלכותך כאשר כרתי לדויד אביך לאמר לא יכרת לך איש מושל בישראל׃"));
hebsuperobject.biblemap.put("b14_7_19", new hebsuperobject(14,7,19,"ואם תשובון אתם ועזבתם חקותי ומצותי אשר נתתי לפניכם והלכתם ועבדתם אלהים אחרים והשתחויתם להם׃"));
hebsuperobject.biblemap.put("b14_7_20", new hebsuperobject(14,7,20,"ונתשתים מעל אדמתי אשר נתתי להם ואת הבית הזה אשר הקדשתי לשמי אשליך מעל פני ואתננו למשל ולשנינה בכל העמים׃"));
hebsuperobject.biblemap.put("b14_7_21", new hebsuperobject(14,7,21,"והבית הזה אשר היה עליון לכל עבר עליו ישם ואמר במה עשה יהוה ככה לארץ הזאת ולבית הזה׃"));
hebsuperobject.biblemap.put("b14_7_22", new hebsuperobject(14,7,22,"ואמרו על אשר עזבו את יהוה אלהי אבתיהם אשר הוציאם מארץ מצרים ויחזיקו באלהים אחרים וישתחוו להם ויעבדום על כן הביא עליהם את כל הרעה הזאת׃"));
hebsuperobject.biblemap.put("b14_8_1", new hebsuperobject(14,8,1,"ויהי מקץ עשרים שנה אשר בנה שלמה את בית יהוה ואת ביתו׃"));
hebsuperobject.biblemap.put("b14_8_2", new hebsuperobject(14,8,2,"והערים אשר נתן חורם לשלמה בנה שלמה אתם ויושב שם את בני ישראל׃"));
hebsuperobject.biblemap.put("b14_8_3", new hebsuperobject(14,8,3,"וילך שלמה חמת צובה ויחזק עליה׃"));
hebsuperobject.biblemap.put("b14_8_4", new hebsuperobject(14,8,4,"ויבן את תדמר במדבר ואת כל ערי המסכנות אשר בנה בחמת׃"));
hebsuperobject.biblemap.put("b14_8_5", new hebsuperobject(14,8,5,"ויבן את בית חורון העליון ואת בית חורון התחתון ערי מצור חומות דלתים ובריח׃"));
hebsuperobject.biblemap.put("b14_8_6", new hebsuperobject(14,8,6,"ואת בעלת ואת כל ערי המסכנות אשר היו לשלמה ואת כל ערי הרכב ואת ערי הפרשים ואת כל חשק שלמה אשר חשק לבנות בירושלם ובלבנון ובכל ארץ ממשלתו׃"));
hebsuperobject.biblemap.put("b14_8_7", new hebsuperobject(14,8,7,"כל העם הנותר מן החתי והאמרי והפרזי והחוי והיבוסי אשר לא מישראל המה׃"));
hebsuperobject.biblemap.put("b14_8_8", new hebsuperobject(14,8,8,"מן בניהם אשר נותרו אחריהם בארץ אשר לא כלום בני ישראל ויעלם שלמה למס עד היום הזה׃"));
hebsuperobject.biblemap.put("b14_8_9", new hebsuperobject(14,8,9,"ומן בני ישראל אשר לא נתן שלמה לעבדים למלאכתו כי המה אנשי מלחמה ושרי שלישיו ושרי רכבו ופרשיו׃"));
hebsuperobject.biblemap.put("b14_8_10", new hebsuperobject(14,8,10,"ואלה שרי הנציבים אשר למלך שלמה חמשים ומאתים הרדים בעם׃"));
hebsuperobject.biblemap.put("b14_8_11", new hebsuperobject(14,8,11,"ואת בת פרעה העלה שלמה מעיר דויד לבית אשר בנה לה כי אמר לא תשב אשה לי בבית דויד מלך ישראל כי קדש המה אשר באה אליהם ארון יהוה׃"));
hebsuperobject.biblemap.put("b14_8_12", new hebsuperobject(14,8,12,"אז העלה שלמה עלות ליהוה על מזבח יהוה אשר בנה לפני האולם׃"));
hebsuperobject.biblemap.put("b14_8_13", new hebsuperobject(14,8,13,"ובדבר יום ביום להעלות כמצות משה לשבתות ולחדשים ולמועדות שלוש פעמים בשנה בחג המצות ובחג השבעות ובחג הסכות׃"));
hebsuperobject.biblemap.put("b14_8_14", new hebsuperobject(14,8,14,"ויעמד כמשפט דויד אביו את מחלקות הכהנים על עבדתם והלוים על משמרותם להלל ולשרת נגד הכהנים לדבר יום ביומו והשוערים במחלקותם לשער ושער כי כן מצות דויד איש האלהים׃"));
hebsuperobject.biblemap.put("b14_8_15", new hebsuperobject(14,8,15,"ולא סרו מצות המלך על הכהנים והלוים לכל דבר ולאצרות׃"));
