hebsuperobject.biblemap.put("b14_9_2", new hebsuperobject(14,9,2,"ויגד לה שלמה את כל דבריה ולא נעלם דבר משלמה אשר לא הגיד לה׃"));
hebsuperobject.biblemap.put("b14_9_3", new hebsuperobject(14,9,3,"ותרא מלכת שבא את חכמת שלמה והבית אשר בנה׃"));
hebsuperobject.biblemap.put("b14_9_4", new hebsuperobject(14,9,4,"ומאכל שלחנו ומושב עבדיו ומעמד משרתיו ומלבושיהם ומשקיו ומלבושיהם ועליתו אשר יעלה בית יהוה ולא היה עוד בה רוח׃"));
hebsuperobject.biblemap.put("b14_9_5", new hebsuperobject(14,9,5,"ותאמר אל המלך אמת הדבר אשר שמעתי בארצי על דבריך ועל חכמתך׃"));
