hebsuperobject.biblemap.put("b14_9_25", new hebsuperobject(14,9,25,"ויהי לשלמה ארבעת אלפים אריות סוסים ומרכבות ושנים עשר אלף פרשים ויניחם בערי הרכב ועם המלך בירושלם׃"));
hebsuperobject.biblemap.put("b14_9_26", new hebsuperobject(14,9,26,"ויהי מושל בכל המלכים מן הנהר ועד ארץ פלשתים ועד גבול מצרים׃"));
