hebsuperobject.biblemap.put("b14_9_30", new hebsuperobject(14,9,30,"וימלך שלמה בירושלם על כל ישראל ארבעים שנה׃"));
hebsuperobject.biblemap.put("b14_9_31", new hebsuperobject(14,9,31,"וישכב שלמה עם אבתיו ויקברהו בעיר דויד אביו וימלך רחבעם בנו תחתיו׃"));
