hebsuperobject.biblemap.put("b14_10_2", new hebsuperobject(14,10,2,"ויהי כשמע ירבעם בן נבט והוא במצרים אשר ברח מפני שלמה המלך וישב ירבעם ממצרים׃"));
hebsuperobject.biblemap.put("b14_10_3", new hebsuperobject(14,10,3,"וישלחו ויקראו לו ויבא ירבעם וכל ישראל וידברו אל רחבעם לאמר׃"));
