hebsuperobject.biblemap.put("b14_10_5", new hebsuperobject(14,10,5,"ויאמר אלהם עוד שלשת ימים ושובו אלי וילך העם׃"));
hebsuperobject.biblemap.put("b14_10_6", new hebsuperobject(14,10,6,"ויועץ המלך רחבעם את הזקנים אשר היו עמדים לפני שלמה אביו בהיתו חי לאמר איך אתם נועצים להשיב לעם הזה דבר׃"));
hebsuperobject.biblemap.put("b14_10_7", new hebsuperobject(14,10,7,"וידברו אליו לאמר אם תהיה לטוב להעם הזה ורציתם ודברת אלהם דברים טובים והיו לך עבדים כל הימים׃"));
hebsuperobject.biblemap.put("b14_10_8", new hebsuperobject(14,10,8,"ויעזב את עצת הזקנים אשר יעצהו ויועץ את הילדים אשר גדלו אתו העמדים לפניו׃"));
hebsuperobject.biblemap.put("b14_10_9", new hebsuperobject(14,10,9,"ויאמר אלהם מה אתם נועצים ונשיב דבר את העם הזה אשר דברו אלי לאמר הקל מן העל אשר נתן אביך עלינו׃"));
hebsuperobject.biblemap.put("b14_10_10", new hebsuperobject(14,10,10,"וידברו אתו הילדים אשר גדלו אתו לאמר כה תאמר לעם אשר דברו אליך לאמר אביך הכביד את עלנו ואתה הקל מעלינו כה תאמר אלהם קטני עבה ממתני אבי׃"));
hebsuperobject.biblemap.put("b14_10_11", new hebsuperobject(14,10,11,"ועתה אבי העמיס עליכם על כבד ואני אסיף על עלכם אבי יסר אתכם בשוטים ואני בעקרבים׃"));
hebsuperobject.biblemap.put("b14_10_12", new hebsuperobject(14,10,12,"ויבא ירבעם וכל העם אל רחבעם ביום השלשי כאשר דבר המלך לאמר שובו אלי ביום השלשי׃"));
hebsuperobject.biblemap.put("b14_10_13", new hebsuperobject(14,10,13,"ויענם המלך קשה ויעזב המלך רחבעם את עצת הזקנים׃"));
