hebsuperobject.biblemap.put("b14_10_15", new hebsuperobject(14,10,15,"ולא שמע המלך אל העם כי היתה נסבה מעם האלהים למען הקים יהוה את דברו אשר דבר ביד אחיהו השלוני אל ירבעם בן נבט׃"));
hebsuperobject.biblemap.put("b14_10_16", new hebsuperobject(14,10,16,"וכל ישראל כי לא שמע המלך להם וישיבו העם את המלך לאמר מה לנו חלק בדויד ולא נחלה בבן ישי איש לאהליך ישראל עתה ראה ביתך דויד וילך כל ישראל לאהליו׃"));
hebsuperobject.biblemap.put("b14_10_17", new hebsuperobject(14,10,17,"ובני ישראל הישבים בערי יהודה וימלך עליהם רחבעם׃"));
hebsuperobject.biblemap.put("b14_10_18", new hebsuperobject(14,10,18,"וישלח המלך רחבעם את הדרם אשר על המס וירגמו בו בני ישראל אבן וימת והמלך רחבעם התאמץ לעלות במרכבה לנוס ירושלם׃"));
hebsuperobject.biblemap.put("b14_10_19", new hebsuperobject(14,10,19,"ויפשעו ישראל בבית דויד עד היום הזה׃"));
hebsuperobject.biblemap.put("b14_11_1", new hebsuperobject(14,11,1,"ויבא רחבעם ירושלם ויקהל את בית יהודה ובנימן מאה ושמונים אלף בחור עשה מלחמה להלחם עם ישראל להשיב את הממלכה לרחבעם׃"));
hebsuperobject.biblemap.put("b14_11_2", new hebsuperobject(14,11,2,"ויהי דבר יהוה אל שמעיהו איש האלהים לאמר׃"));
