hebsuperobject.biblemap.put("b14_11_4", new hebsuperobject(14,11,4,"כה אמר יהוה לא תעלו ולא תלחמו עם אחיכם שובו איש לביתו כי מאתי נהיה הדבר הזה וישמעו את דברי יהוה וישבו מלכת אל ירבעם׃"));
hebsuperobject.biblemap.put("b14_11_5", new hebsuperobject(14,11,5,"וישב רחבעם בירושלם ויבן ערים למצור ביהודה׃"));
