hebsuperobject.biblemap.put("b14_19_7", new hebsuperobject(14,19,7,"ועתה יהי פחד יהוה עליכם שמרו ועשו כי אין עם יהוה אלהינו עולה ומשא פנים ומקח שחד׃"));
hebsuperobject.biblemap.put("b14_19_8", new hebsuperobject(14,19,8,"וגם בירושלם העמיד יהושפט מן הלוים והכהנים ומראשי האבות לישראל למשפט יהוה ולריב וישבו ירושלם׃"));
hebsuperobject.biblemap.put("b14_19_9", new hebsuperobject(14,19,9,"ויצו עליהם לאמר כה תעשון ביראת יהוה באמונה ובלבב שלם׃"));
