hebsuperobject.biblemap.put("b14_19_11", new hebsuperobject(14,19,11,"והנה אמריהו כהן הראש עליכם לכל דבר יהוה וזבדיהו בן ישמעאל הנגיד לבית יהודה לכל דבר המלך ושטרים הלוים לפניכם חזקו ועשו ויהי יהוה עם הטוב׃"));
hebsuperobject.biblemap.put("b14_20_1", new hebsuperobject(14,20,1,"ויהי אחריכן באו בני מואב ובני עמון ועמהם מהעמונים על יהושפט למלחמה׃"));
hebsuperobject.biblemap.put("b14_20_2", new hebsuperobject(14,20,2,"ויבאו ויגידו ליהושפט לאמר בא עליך המון רב מעבר לים מארם והנם בחצצון תמר היא עין גדי׃"));
hebsuperobject.biblemap.put("b14_20_3", new hebsuperobject(14,20,3,"וירא ויתן יהושפט את פניו לדרוש ליהוה ויקרא צום על כל יהודה׃"));
hebsuperobject.biblemap.put("b14_20_4", new hebsuperobject(14,20,4,"ויקבצו יהודה לבקש מיהוה גם מכל ערי יהודה באו לבקש את יהוה׃"));
