hebsuperobject.biblemap.put("b14_21_7", new hebsuperobject(14,21,7,"ולא אבה יהוה להשחית את בית דויד למען הברית אשר כרת לדויד וכאשר אמר לתת לו ניר ולבניו כל הימים׃"));
hebsuperobject.biblemap.put("b14_21_8", new hebsuperobject(14,21,8,"בימיו פשע אדום מתחת יד יהודה וימליכו עליהם מלך׃"));
