hebsuperobject.biblemap.put("b14_21_19", new hebsuperobject(14,21,19,"ויהי לימים מימים וכעת צאת הקץ לימים שנים יצאו מעיו עם חליו וימת בתחלאים רעים ולא עשו לו עמו שרפה כשרפת אבתיו׃"));
hebsuperobject.biblemap.put("b14_21_20", new hebsuperobject(14,21,20,"בן שלשים ושתים היה במלכו ושמונה שנים מלך בירושלם וילך בלא חמדה ויקברהו בעיר דויד ולא בקברות המלכים׃"));
hebsuperobject.biblemap.put("b14_22_1", new hebsuperobject(14,22,1,"וימליכו יושבי ירושלם את אחזיהו בנו הקטן תחתיו כי כל הראשנים הרג הגדוד הבא בערבים למחנה וימלך אחזיהו בן יהורם מלך יהודה׃"));
hebsuperobject.biblemap.put("b14_22_2", new hebsuperobject(14,22,2,"בן ארבעים ושתים שנה אחזיהו במלכו ושנה אחת מלך בירושלם ושם אמו עתליהו בת עמרי׃"));
