hebsuperobject.biblemap.put("b14_22_6", new hebsuperobject(14,22,6,"וישב להתרפא ביזרעאל כי המכים אשר הכהו ברמה בהלחמו את חזהאל מלך ארם ועזריהו בן יהורם מלך יהודה ירד לראות את יהורם בן אחאב ביזרעאל כי חלה הוא׃"));
hebsuperobject.biblemap.put("b14_22_7", new hebsuperobject(14,22,7,"ומאלהים היתה תבוסת אחזיהו לבוא אל יורם ובבאו יצא עם יהורם אל יהוא בן נמשי אשר משחו יהוה להכרית את בית אחאב׃"));
hebsuperobject.biblemap.put("b14_22_8", new hebsuperobject(14,22,8,"ויהי כהשפט יהוא עם בית אחאב וימצא את שרי יהודה ובני אחי אחזיהו משרתים לאחזיהו ויהרגם׃"));
hebsuperobject.biblemap.put("b14_22_9", new hebsuperobject(14,22,9,"ויבקש את אחזיהו וילכדהו והוא מתחבא בשמרון ויבאהו אל יהוא וימתהו ויקברהו כי אמרו בן יהושפט הוא אשר דרש את יהוה בכל לבבו ואין לבית אחזיהו לעצר כח לממלכה׃"));
hebsuperobject.biblemap.put("b14_22_10", new hebsuperobject(14,22,10,"ועתליהו אם אחזיהו ראתה כי מת בנה ותקם ותדבר את כל זרע הממלכה לבית יהודה׃"));
hebsuperobject.biblemap.put("b14_22_11", new hebsuperobject(14,22,11,"ותקח יהושבעת בת המלך את יואש בן אחזיהו ותגנב אתו מתוך בני המלך המומתים ותתן אתו ואת מינקתו בחדר המטות ותסתירהו יהושבעת בת המלך יהורם אשת יהוידע הכהן כי היא היתה אחות אחזיהו מפני עתליהו ולא המיתתהו׃"));
hebsuperobject.biblemap.put("b14_22_12", new hebsuperobject(14,22,12,"ויהי אתם בבית האלהים מתחבא שש שנים ועתליה מלכת על הארץ׃"));
hebsuperobject.biblemap.put("b14_23_1", new hebsuperobject(14,23,1,"ובשנה השבעית התחזק יהוידע ויקח את שרי המאות לעזריהו בן ירחם ולישמעאל בן יהוחנן ולעזריהו בן עובד ואת מעשיהו בן עדיהו ואת אלישפט בן זכרי עמו בברית׃"));
hebsuperobject.biblemap.put("b14_23_2", new hebsuperobject(14,23,2,"ויסבו ביהודה ויקבצו את הלוים מכל ערי יהודה וראשי האבות לישראל ויבאו אל ירושלם׃"));
hebsuperobject.biblemap.put("b14_23_3", new hebsuperobject(14,23,3,"ויכרת כל הקהל ברית בבית האלהים עם המלך ויאמר להם הנה בן המלך ימלך כאשר דבר יהוה על בני דויד׃"));
hebsuperobject.biblemap.put("b14_23_4", new hebsuperobject(14,23,4,"זה הדבר אשר תעשו השלשית מכם באי השבת לכהנים וללוים לשערי הספים׃"));
hebsuperobject.biblemap.put("b14_23_5", new hebsuperobject(14,23,5,"והשלשית בבית המלך והשלשית בשער היסוד וכל העם בחצרות בית יהוה׃"));
hebsuperobject.biblemap.put("b14_23_6", new hebsuperobject(14,23,6,"ואל יבוא בית יהוה כי אם הכהנים והמשרתים ללוים המה יבאו כי קדש המה וכל העם ישמרו משמרת יהוה׃"));
hebsuperobject.biblemap.put("b14_23_7", new hebsuperobject(14,23,7,"והקיפו הלוים את המלך סביב איש וכליו בידו והבא אל הבית יומת והיו את המלך בבאו ובצאתו׃"));
hebsuperobject.biblemap.put("b14_23_8", new hebsuperobject(14,23,8,"ויעשו הלוים וכל יהודה ככל אשר צוה יהוידע הכהן ויקחו איש את אנשיו באי השבת עם יוצאי השבת כי לא פטר יהוידע הכהן את המחלקות׃"));
hebsuperobject.biblemap.put("b14_23_9", new hebsuperobject(14,23,9,"ויתן יהוידע הכהן לשרי המאות את החניתים ואת המגנות ואת השלטים אשר למלך דויד אשר בית האלהים׃"));
hebsuperobject.biblemap.put("b14_23_10", new hebsuperobject(14,23,10,"ויעמד את כל העם ואיש שלחו בידו מכתף הבית הימנית עד כתף הבית השמאלית למזבח ולבית על המלך סביב׃"));
hebsuperobject.biblemap.put("b14_23_11", new hebsuperobject(14,23,11,"ויוציאו את בן המלך ויתנו עליו את הנזר ואת העדות וימליכו אתו וימשחהו יהוידע ובניו ויאמרו יחי המלך׃"));
hebsuperobject.biblemap.put("b14_23_12", new hebsuperobject(14,23,12,"ותשמע עתליהו את קול העם הרצים והמהללים את המלך ותבוא אל העם בית יהוה׃"));
hebsuperobject.biblemap.put("b14_23_13", new hebsuperobject(14,23,13,"ותרא והנה המלך עומד על עמודו במבוא והשרים והחצצרות על המלך וכל עם הארץ שמח ותוקע בחצצרות והמשוררים בכלי השיר ומודיעים להלל ותקרע עתליהו את בגדיה ותאמר קשר קשר׃"));
hebsuperobject.biblemap.put("b14_23_14", new hebsuperobject(14,23,14,"ויוצא יהוידע הכהן את שרי המאות פקודי החיל ויאמר אלהם הוציאוה אל מבית השדרות והבא אחריה יומת בחרב כי אמר הכהן לא תמיתוה בית יהוה׃"));
hebsuperobject.biblemap.put("b14_23_15", new hebsuperobject(14,23,15,"וישימו לה ידים ותבוא אל מבוא שער הסוסים בית המלך וימיתוה שם׃"));
hebsuperobject.biblemap.put("b14_23_16", new hebsuperobject(14,23,16,"ויכרת יהוידע ברית בינו ובין כל העם ובין המלך להיות לעם ליהוה׃"));
hebsuperobject.biblemap.put("b14_23_17", new hebsuperobject(14,23,17,"ויבאו כל העם בית הבעל ויתצהו ואת מזבחתיו ואת צלמיו שברו ואת מתן כהן הבעל הרגו לפני המזבחות׃"));
hebsuperobject.biblemap.put("b14_23_18", new hebsuperobject(14,23,18,"וישם יהוידע פקדת בית יהוה ביד הכהנים הלוים אשר חלק דויד על בית יהוה להעלות עלות יהוה ככתוב בתורת משה בשמחה ובשיר על ידי דויד׃"));
hebsuperobject.biblemap.put("b14_23_19", new hebsuperobject(14,23,19,"ויעמד השוערים על שערי בית יהוה ולא יבא טמא לכל דבר׃"));
hebsuperobject.biblemap.put("b14_23_20", new hebsuperobject(14,23,20,"ויקח את שרי המאות ואת האדירים ואת המושלים בעם ואת כל עם הארץ ויורד את המלך מבית יהוה ויבאו בתוך שער העליון בית המלך ויושיבו את המלך על כסא הממלכה׃"));
hebsuperobject.biblemap.put("b14_23_21", new hebsuperobject(14,23,21,"וישמחו כל עם הארץ והעיר שקטה ואת עתליהו המיתו בחרב׃"));
hebsuperobject.biblemap.put("b14_24_1", new hebsuperobject(14,24,1,"בן שבע שנים יאש במלכו וארבעים שנה מלך בירושלם ושם אמו צביה מבאר שבע׃"));
hebsuperobject.biblemap.put("b14_24_2", new hebsuperobject(14,24,2,"ויעש יואש הישר בעיני יהוה כל ימי יהוידע הכהן׃"));
hebsuperobject.biblemap.put("b14_24_3", new hebsuperobject(14,24,3,"וישא לו יהוידע נשים שתים ויולד בנים ובנות׃"));
hebsuperobject.biblemap.put("b14_24_4", new hebsuperobject(14,24,4,"ויהי אחריכן היה עם לב יואש לחדש את בית יהוה׃"));
hebsuperobject.biblemap.put("b14_24_5", new hebsuperobject(14,24,5,"ויקבץ את הכהנים והלוים ויאמר להם צאו לערי יהודה וקבצו מכל ישראל כסף לחזק את בית אלהיכם מדי שנה בשנה ואתם תמהרו לדבר ולא מהרו הלוים׃"));
hebsuperobject.biblemap.put("b14_24_6", new hebsuperobject(14,24,6,"ויקרא המלך ליהוידע הראש ויאמר לו מדוע לא דרשת על הלוים להביא מיהודה ומירושלם את משאת משה עבד יהוה והקהל לישראל לאהל העדות׃"));
hebsuperobject.biblemap.put("b14_24_7", new hebsuperobject(14,24,7,"כי עתליהו המרשעת בניה פרצו את בית האלהים וגם כל קדשי בית יהוה עשו לבעלים׃"));
hebsuperobject.biblemap.put("b14_24_8", new hebsuperobject(14,24,8,"ויאמר המלך ויעשו ארון אחד ויתנהו בשער בית יהוה חוצה׃"));
hebsuperobject.biblemap.put("b14_24_9", new hebsuperobject(14,24,9,"ויתנו קול ביהודה ובירושלם להביא ליהוה משאת משה עבד האלהים על ישראל במדבר׃"));
hebsuperobject.biblemap.put("b14_24_10", new hebsuperobject(14,24,10,"וישמחו כל השרים וכל העם ויביאו וישליכו לארון עד לכלה׃"));
hebsuperobject.biblemap.put("b14_24_11", new hebsuperobject(14,24,11,"ויהי בעת יביא את הארון אל פקדת המלך ביד הלוים וכראותם כי רב הכסף ובא סופר המלך ופקיד כהן הראש ויערו את הארון וישאהו וישיבהו אל מקמו כה עשו ליום ביום ויאספו כסף לרב׃"));
hebsuperobject.biblemap.put("b14_24_12", new hebsuperobject(14,24,12,"ויתנהו המלך ויהוידע אל עושה מלאכת עבודת בית יהוה ויהיו שכרים חצבים וחרשים לחדש בית יהוה וגם לחרשי ברזל ונחשת לחזק את בית יהוה׃"));
hebsuperobject.biblemap.put("b14_24_13", new hebsuperobject(14,24,13,"ויעשו עשי המלאכה ותעל ארוכה למלאכה בידם ויעמידו את בית האלהים על מתכנתו ויאמצהו׃"));
hebsuperobject.biblemap.put("b14_24_14", new hebsuperobject(14,24,14,"וככלותם הביאו לפני המלך ויהוידע את שאר הכסף ויעשהו כלים לבית יהוה כלי שרת והעלות וכפות וכלי זהב וכסף ויהיו מעלים עלות בבית יהוה תמיד כל ימי יהוידע׃"));
hebsuperobject.biblemap.put("b14_24_15", new hebsuperobject(14,24,15,"ויזקן יהוידע וישבע ימים וימת בן מאה ושלשים שנה במותו׃"));
hebsuperobject.biblemap.put("b14_24_16", new hebsuperobject(14,24,16,"ויקברהו בעיר דויד עם המלכים כי עשה טובה בישראל ועם האלהים וביתו׃"));
hebsuperobject.biblemap.put("b14_24_17", new hebsuperobject(14,24,17,"ואחרי מות יהוידע באו שרי יהודה וישתחוו למלך אז שמע המלך אליהם׃"));
hebsuperobject.biblemap.put("b14_24_18", new hebsuperobject(14,24,18,"ויעזבו את בית יהוה אלהי אבותיהם ויעבדו את האשרים ואת העצבים ויהי קצף על יהודה וירושלם באשמתם זאת׃"));
hebsuperobject.biblemap.put("b14_24_19", new hebsuperobject(14,24,19,"וישלח בהם נבאים להשיבם אל יהוה ויעידו בם ולא האזינו׃"));
hebsuperobject.biblemap.put("b14_24_20", new hebsuperobject(14,24,20,"ורוח אלהים לבשה את זכריה בן יהוידע הכהן ויעמד מעל לעם ויאמר להם כה אמר האלהים למה אתם עברים את מצות יהוה ולא תצליחו כי עזבתם את יהוה ויעזב אתכם׃"));
hebsuperobject.biblemap.put("b14_24_21", new hebsuperobject(14,24,21,"ויקשרו עליו וירגמהו אבן במצות המלך בחצר בית יהוה׃"));
hebsuperobject.biblemap.put("b14_24_22", new hebsuperobject(14,24,22,"ולא זכר יואש המלך החסד אשר עשה יהוידע אביו עמו ויהרג את בנו וכמותו אמר ירא יהוה וידרש׃"));
hebsuperobject.biblemap.put("b14_24_23", new hebsuperobject(14,24,23,"ויהי לתקופת השנה עלה עליו חיל ארם ויבאו אל יהודה וירושלם וישחיתו את כל שרי העם מעם וכל שללם שלחו למלך דרמשק׃"));
hebsuperobject.biblemap.put("b14_24_24", new hebsuperobject(14,24,24,"כי במצער אנשים באו חיל ארם ויהוה נתן בידם חיל לרב מאד כי עזבו את יהוה אלהי אבותיהם ואת יואש עשו שפטים׃"));
hebsuperobject.biblemap.put("b14_24_25", new hebsuperobject(14,24,25,"ובלכתם ממנו כי עזבו אתו במחליים רבים התקשרו עליו עבדיו בדמי בני יהוידע הכהן ויהרגהו על מטתו וימת ויקברהו בעיר דויד ולא קברהו בקברות המלכים׃"));
hebsuperobject.biblemap.put("b14_24_26", new hebsuperobject(14,24,26,"ואלה המתקשרים עליו זבד בן שמעת העמונית ויהוזבד בן שמרית המואבית׃"));
hebsuperobject.biblemap.put("b14_24_27", new hebsuperobject(14,24,27,"ובניו ורב המשא עליו ויסוד בית האלהים הנם כתובים על מדרש ספר המלכים וימלך אמציהו בנו תחתיו׃"));
hebsuperobject.biblemap.put("b14_25_1", new hebsuperobject(14,25,1,"בן עשרים וחמש שנה מלך אמציהו ועשרים ותשע שנה מלך בירושלם ושם אמו יהועדן מירושלים׃"));
hebsuperobject.biblemap.put("b14_25_2", new hebsuperobject(14,25,2,"ויעש הישר בעיני יהוה רק לא בלבב שלם׃"));
hebsuperobject.biblemap.put("b14_25_3", new hebsuperobject(14,25,3,"ויהי כאשר חזקה הממלכה עליו ויהרג את עבדיו המכים את המלך אביו׃"));
hebsuperobject.biblemap.put("b14_25_4", new hebsuperobject(14,25,4,"ואת בניהם לא המית כי ככתוב בתורה בספר משה אשר צוה יהוה לאמר לא ימותו אבות על בנים ובנים לא ימותו על אבות כי איש בחטאו ימותו׃"));
hebsuperobject.biblemap.put("b14_25_5", new hebsuperobject(14,25,5,"ויקבץ אמציהו את יהודה ויעמידם לבית אבות לשרי האלפים ולשרי המאות לכל יהודה ובנימן ויפקדם למבן עשרים שנה ומעלה וימצאם שלש מאות אלף בחור יוצא צבא אחז רמח וצנה׃"));
hebsuperobject.biblemap.put("b14_25_6", new hebsuperobject(14,25,6,"וישכר מישראל מאה אלף גבור חיל במאה ככר כסף׃"));
hebsuperobject.biblemap.put("b14_25_7", new hebsuperobject(14,25,7,"ואיש האלהים בא אליו לאמר המלך אל יבא עמך צבא ישראל כי אין יהוה עם ישראל כל בני אפרים׃"));
hebsuperobject.biblemap.put("b14_25_8", new hebsuperobject(14,25,8,"כי אם בא אתה עשה חזק למלחמה יכשילך האלהים לפני אויב כי יש כח באלהים לעזור ולהכשיל׃"));
hebsuperobject.biblemap.put("b14_25_9", new hebsuperobject(14,25,9,"ויאמר אמציהו לאיש האלהים ומה לעשות למאת הככר אשר נתתי לגדוד ישראל ויאמר איש האלהים יש ליהוה לתת לך הרבה מזה׃"));
hebsuperobject.biblemap.put("b14_25_10", new hebsuperobject(14,25,10,"ויבדילם אמציהו להגדוד אשר בא אליו מאפרים ללכת למקומם ויחר אפם מאד ביהודה וישובו למקומם בחרי אף׃"));
hebsuperobject.biblemap.put("b14_25_11", new hebsuperobject(14,25,11,"ואמציהו התחזק וינהג את עמו וילך גיא המלח ויך את בני שעיר עשרת אלפים׃"));
hebsuperobject.biblemap.put("b14_25_12", new hebsuperobject(14,25,12,"ועשרת אלפים חיים שבו בני יהודה ויביאום לראש הסלע וישליכום מראש הסלע וכלם נבקעו׃"));
hebsuperobject.biblemap.put("b14_25_13", new hebsuperobject(14,25,13,"ובני הגדוד אשר השיב אמציהו מלכת עמו למלחמה ויפשטו בערי יהודה משמרון ועד בית חורון ויכו מהם שלשת אלפים ויבזו בזה רבה׃"));
hebsuperobject.biblemap.put("b14_25_14", new hebsuperobject(14,25,14,"ויהי אחרי בוא אמציהו מהכות את אדומים ויבא את אלהי בני שעיר ויעמידם לו לאלהים ולפניהם ישתחוה ולהם יקטר׃"));
