hebsuperobject.biblemap.put("b17_9_10", new hebsuperobject(17,9,10,"עשרת בני המן בן המדתא צרר היהודים הרגו ובבזה לא שלחו את ידם׃"));
hebsuperobject.biblemap.put("b17_9_11", new hebsuperobject(17,9,11,"ביום ההוא בא מספר ההרוגים בשושן הבירה לפני המלך׃"));
hebsuperobject.biblemap.put("b17_9_12", new hebsuperobject(17,9,12,"ויאמר המלך לאסתר המלכה בשושן הבירה הרגו היהודים ואבד חמש מאות איש ואת עשרת בני המן בשאר מדינות המלך מה עשו ומה שאלתך וינתן לך ומה בקשתך עוד ותעש׃"));
hebsuperobject.biblemap.put("b17_9_13", new hebsuperobject(17,9,13,"ותאמר אסתר אם על המלך טוב ינתן גם מחר ליהודים אשר בשושן לעשות כדת היום ואת עשרת בני המן יתלו על העץ׃"));
hebsuperobject.biblemap.put("b17_9_14", new hebsuperobject(17,9,14,"ויאמר המלך להעשות כן ותנתן דת בשושן ואת עשרת בני המן תלו׃"));
hebsuperobject.biblemap.put("b17_9_15", new hebsuperobject(17,9,15,"ויקהלו היהודיים אשר בשושן גם ביום ארבעה עשר לחדש אדר ויהרגו בשושן שלש מאות איש ובבזה לא שלחו את ידם׃"));
hebsuperobject.biblemap.put("b17_9_16", new hebsuperobject(17,9,16,"ושאר היהודים אשר במדינות המלך נקהלו ועמד על נפשם ונוח מאיביהם והרג בשנאיהם חמשה ושבעים אלף ובבזה לא שלחו את ידם׃"));
hebsuperobject.biblemap.put("b17_9_17", new hebsuperobject(17,9,17,"ביום שלשה עשר לחדש אדר ונוח בארבעה עשר בו ועשה אתו יום משתה ושמחה׃"));
hebsuperobject.biblemap.put("b17_9_18", new hebsuperobject(17,9,18,"והיהודיים אשר בשושן נקהלו בשלשה עשר בו ובארבעה עשר בו ונוח בחמשה עשר בו ועשה אתו יום משתה ושמחה׃"));
hebsuperobject.biblemap.put("b17_9_19", new hebsuperobject(17,9,19,"על כן היהודים הפרוזים הישבים בערי הפרזות עשים את יום ארבעה עשר לחדש אדר שמחה ומשתה ויום טוב ומשלוח מנות איש לרעהו׃"));
hebsuperobject.biblemap.put("b17_9_20", new hebsuperobject(17,9,20,"ויכתב מרדכי את הדברים האלה וישלח ספרים אל כל היהודים אשר בכל מדינות המלך אחשורוש הקרובים והרחוקים׃"));
hebsuperobject.biblemap.put("b17_9_21", new hebsuperobject(17,9,21,"לקים עליהם להיות עשים את יום ארבעה עשר לחדש אדר ואת יום חמשה עשר בו בכל שנה ושנה׃"));
hebsuperobject.biblemap.put("b17_9_22", new hebsuperobject(17,9,22,"כימים אשר נחו בהם היהודים מאויביהם והחדש אשר נהפך להם מיגון לשמחה ומאבל ליום טוב לעשות אותם ימי משתה ושמחה ומשלוח מנות איש לרעהו ומתנות לאביונים׃"));
hebsuperobject.biblemap.put("b17_9_23", new hebsuperobject(17,9,23,"וקבל היהודים את אשר החלו לעשות ואת אשר כתב מרדכי אליהם׃"));
