hebsuperobject.biblemap.put("b17_9_25", new hebsuperobject(17,9,25,"ובבאה לפני המלך אמר עם הספר ישוב מחשבתו הרעה אשר חשב על היהודים על ראשו ותלו אתו ואת בניו על העץ׃"));
hebsuperobject.biblemap.put("b17_9_26", new hebsuperobject(17,9,26,"על כן קראו לימים האלה פורים על שם הפור על כן על כל דברי האגרת הזאת ומה ראו על ככה ומה הגיע אליהם׃"));
hebsuperobject.biblemap.put("b17_9_27", new hebsuperobject(17,9,27,"קימו וקבל היהודים עליהם ועל זרעם ועל כל הנלוים עליהם ולא יעבור להיות עשים את שני הימים האלה ככתבם וכזמנם בכל שנה ושנה׃"));
hebsuperobject.biblemap.put("b17_9_28", new hebsuperobject(17,9,28,"והימים האלה נזכרים ונעשים בכל דור ודור משפחה ומשפחה מדינה ומדינה ועיר ועיר וימי הפורים האלה לא יעברו מתוך היהודים וזכרם לא יסוף מזרעם׃"));
hebsuperobject.biblemap.put("b17_9_29", new hebsuperobject(17,9,29,"ותכתב אסתר המלכה בת אביחיל ומרדכי היהודי את כל תקף לקים את אגרת הפורים הזאת השנית׃"));
hebsuperobject.biblemap.put("b17_9_30", new hebsuperobject(17,9,30,"וישלח ספרים אל כל היהודים אל שבע ועשרים ומאה מדינה מלכות אחשורוש דברי שלום ואמת׃"));
hebsuperobject.biblemap.put("b17_9_31", new hebsuperobject(17,9,31,"לקים את ימי הפרים האלה בזמניהם כאשר קים עליהם מרדכי היהודי ואסתר המלכה וכאשר קימו על נפשם ועל זרעם דברי הצמות וזעקתם׃"));
hebsuperobject.biblemap.put("b17_9_32", new hebsuperobject(17,9,32,"ומאמר אסתר קים דברי הפרים האלה ונכתב בספר׃"));
