hebsuperobject.biblemap.put("b23_28_22", new hebsuperobject(23,28,22,"ועתה אל תתלוצצו פן יחזקו מוסריכם כי כלה ונחרצה שמעתי מאת אדני יהוה צבאות על כל הארץ׃"));
hebsuperobject.biblemap.put("b23_28_23", new hebsuperobject(23,28,23,"האזינו ושמעו קולי הקשיבו ושמעו אמרתי׃"));
hebsuperobject.biblemap.put("b23_28_24", new hebsuperobject(23,28,24,"הכל היום יחרש החרש לזרע יפתח וישדד אדמתו׃"));
hebsuperobject.biblemap.put("b23_28_25", new hebsuperobject(23,28,25,"הלוא אם שוה פניה והפיץ קצח וכמן יזרק ושם חטה שורה ושערה נסמן וכסמת גבלתו׃"));
hebsuperobject.biblemap.put("b23_28_26", new hebsuperobject(23,28,26,"ויסרו למשפט אלהיו יורנו׃"));
hebsuperobject.biblemap.put("b23_28_27", new hebsuperobject(23,28,27,"כי לא בחרוץ יודש קצח ואופן עגלה על כמן יוסב כי במטה יחבט קצח וכמן בשבט׃"));
hebsuperobject.biblemap.put("b23_28_28", new hebsuperobject(23,28,28,"לחם יודק כי לא לנצח אדוש ידושנו והמם גלגל עגלתו ופרשיו לא ידקנו׃"));
hebsuperobject.biblemap.put("b23_28_29", new hebsuperobject(23,28,29,"גם זאת מעם יהוה צבאות יצאה הפליא עצה הגדיל תושיה׃"));
hebsuperobject.biblemap.put("b23_29_1", new hebsuperobject(23,29,1,"הוי אריאל אריאל קרית חנה דוד ספו שנה על שנה חגים ינקפו׃"));
hebsuperobject.biblemap.put("b23_29_2", new hebsuperobject(23,29,2,"והציקותי לאריאל והיתה תאניה ואניה והיתה לי כאריאל׃"));
hebsuperobject.biblemap.put("b23_29_3", new hebsuperobject(23,29,3,"וחניתי כדור עליך וצרתי עליך מצב והקימתי עליך מצרת׃"));
hebsuperobject.biblemap.put("b23_29_4", new hebsuperobject(23,29,4,"ושפלת מארץ תדברי ומעפר תשח אמרתך והיה כאוב מארץ קולך ומעפר אמרתך תצפצף׃"));
hebsuperobject.biblemap.put("b23_29_5", new hebsuperobject(23,29,5,"והיה כאבק דק המון זריך וכמץ עבר המון עריצים והיה לפתע פתאם׃"));
hebsuperobject.biblemap.put("b23_29_6", new hebsuperobject(23,29,6,"מעם יהוה צבאות תפקד ברעם וברעש וקול גדול סופה וסערה ולהב אש אוכלה׃"));
hebsuperobject.biblemap.put("b23_29_7", new hebsuperobject(23,29,7,"והיה כחלום חזון לילה המון כל הגוים הצבאים על אריאל וכל צביה ומצדתה והמציקים לה׃"));
hebsuperobject.biblemap.put("b23_29_8", new hebsuperobject(23,29,8,"והיה כאשר יחלם הרעב והנה אוכל והקיץ וריקה נפשו וכאשר יחלם הצמא והנה שתה והקיץ והנה עיף ונפשו שוקקה כן יהיה המון כל הגוים הצבאים על הר ציון׃"));
hebsuperobject.biblemap.put("b23_29_9", new hebsuperobject(23,29,9,"התמהמהו ותמהו השתעשעו ושעו שכרו ולא יין נעו ולא שכר׃"));
hebsuperobject.biblemap.put("b23_29_10", new hebsuperobject(23,29,10,"כי נסך עליכם יהוה רוח תרדמה ויעצם את עיניכם את הנביאים ואת ראשיכם החזים כסה׃"));
hebsuperobject.biblemap.put("b23_29_11", new hebsuperobject(23,29,11,"ותהי לכם חזות הכל כדברי הספר החתום אשר יתנו אתו אל יודע הספר לאמר קרא נא זה ואמר לא אוכל כי חתום הוא׃"));
hebsuperobject.biblemap.put("b23_29_12", new hebsuperobject(23,29,12,"ונתן הספר על אשר לא ידע ספר לאמר קרא נא זה ואמר לא ידעתי ספר׃"));
hebsuperobject.biblemap.put("b23_29_13", new hebsuperobject(23,29,13,"ויאמר אדני יען כי נגש העם הזה בפיו ובשפתיו כבדוני ולבו רחק ממני ותהי יראתם אתי מצות אנשים מלמדה׃"));
hebsuperobject.biblemap.put("b23_29_14", new hebsuperobject(23,29,14,"לכן הנני יוסף להפליא את העם הזה הפלא ופלא ואבדה חכמת חכמיו ובינת נבניו תסתתר׃"));
hebsuperobject.biblemap.put("b23_29_15", new hebsuperobject(23,29,15,"הוי המעמיקים מיהוה לסתר עצה והיה במחשך מעשיהם ויאמרו מי ראנו ומי יודענו׃"));
hebsuperobject.biblemap.put("b23_29_16", new hebsuperobject(23,29,16,"הפככם אם כחמר היצר יחשב כי יאמר מעשה לעשהו לא עשני ויצר אמר ליוצרו לא הבין׃"));
hebsuperobject.biblemap.put("b23_29_17", new hebsuperobject(23,29,17,"הלוא עוד מעט מזער ושב לבנון לכרמל והכרמל ליער יחשב׃"));
hebsuperobject.biblemap.put("b23_29_18", new hebsuperobject(23,29,18,"ושמעו ביום ההוא החרשים דברי ספר ומאפל ומחשך עיני עורים תראינה׃"));
hebsuperobject.biblemap.put("b23_29_19", new hebsuperobject(23,29,19,"ויספו ענוים ביהוה שמחה ואביוני אדם בקדוש ישראל יגילו׃"));
hebsuperobject.biblemap.put("b23_29_20", new hebsuperobject(23,29,20,"כי אפס עריץ וכלה לץ ונכרתו כל שקדי און׃"));
hebsuperobject.biblemap.put("b23_29_21", new hebsuperobject(23,29,21,"מחטיאי אדם בדבר ולמוכיח בשער יקשון ויטו בתהו צדיק׃"));
hebsuperobject.biblemap.put("b23_29_22", new hebsuperobject(23,29,22,"לכן כה אמר יהוה אל בית יעקב אשר פדה את אברהם לא עתה יבוש יעקב ולא עתה פניו יחורו׃"));
hebsuperobject.biblemap.put("b23_29_23", new hebsuperobject(23,29,23,"כי בראתו ילדיו מעשה ידי בקרבו יקדישו שמי והקדישו את קדוש יעקב ואת אלהי ישראל יעריצו׃"));
hebsuperobject.biblemap.put("b23_29_24", new hebsuperobject(23,29,24,"וידעו תעי רוח בינה ורוגנים ילמדו לקח׃"));
hebsuperobject.biblemap.put("b23_30_1", new hebsuperobject(23,30,1,"הוי בנים סוררים נאם יהוה לעשות עצה ולא מני ולנסך מסכה ולא רוחי למען ספות חטאת על חטאת׃"));
hebsuperobject.biblemap.put("b23_30_2", new hebsuperobject(23,30,2,"ההלכים לרדת מצרים ופי לא שאלו לעוז במעוז פרעה ולחסות בצל מצרים׃"));
hebsuperobject.biblemap.put("b23_30_3", new hebsuperobject(23,30,3,"והיה לכם מעוז פרעה לבשת והחסות בצל מצרים לכלמה׃"));
hebsuperobject.biblemap.put("b23_30_4", new hebsuperobject(23,30,4,"כי היו בצען שריו ומלאכיו חנס יגיעו׃"));
hebsuperobject.biblemap.put("b23_30_5", new hebsuperobject(23,30,5,"כל הבאיש על עם לא יועילו למו לא לעזר ולא להועיל כי לבשת וגם לחרפה׃"));
hebsuperobject.biblemap.put("b23_30_6", new hebsuperobject(23,30,6,"משא בהמות נגב בארץ צרה וצוקה לביא וליש מהם אפעה ושרף מעופף ישאו על כתף עירים חילהם ועל דבשת גמלים אוצרתם על עם לא יועילו׃"));
hebsuperobject.biblemap.put("b23_30_7", new hebsuperobject(23,30,7,"ומצרים הבל וריק יעזרו לכן קראתי לזאת רהב הם שבת׃"));
hebsuperobject.biblemap.put("b23_30_8", new hebsuperobject(23,30,8,"עתה בוא כתבה על לוח אתם ועל ספר חקה ותהי ליום אחרון לעד עד עולם׃"));
hebsuperobject.biblemap.put("b23_30_9", new hebsuperobject(23,30,9,"כי עם מרי הוא בנים כחשים בנים לא אבו שמוע תורת יהוה׃"));
hebsuperobject.biblemap.put("b23_30_10", new hebsuperobject(23,30,10,"אשר אמרו לראים לא תראו ולחזים לא תחזו לנו נכחות דברו לנו חלקות חזו מהתלות׃"));
hebsuperobject.biblemap.put("b23_30_11", new hebsuperobject(23,30,11,"סורו מני דרך הטו מני ארח השביתו מפנינו את קדוש ישראל׃"));
hebsuperobject.biblemap.put("b23_30_12", new hebsuperobject(23,30,12,"לכן כה אמר קדוש ישראל יען מאסכם בדבר הזה ותבטחו בעשק ונלוז ותשענו עליו׃"));
hebsuperobject.biblemap.put("b23_30_13", new hebsuperobject(23,30,13,"לכן יהיה לכם העון הזה כפרץ נפל נבעה בחומה נשגבה אשר פתאם לפתע יבוא שברה׃"));
hebsuperobject.biblemap.put("b23_30_14", new hebsuperobject(23,30,14,"ושברה כשבר נבל יוצרים כתות לא יחמל ולא ימצא במכתתו חרש לחתות אש מיקוד ולחשף מים מגבא׃"));
hebsuperobject.biblemap.put("b23_30_15", new hebsuperobject(23,30,15,"כי כה אמר אדני יהוה קדוש ישראל בשובה ונחת תושעון בהשקט ובבטחה תהיה גבורתכם ולא אביתם׃"));
hebsuperobject.biblemap.put("b23_30_16", new hebsuperobject(23,30,16,"ותאמרו לא כי על סוס ננוס על כן תנוסון ועל קל נרכב על כן יקלו רדפיכם׃"));
hebsuperobject.biblemap.put("b23_30_17", new hebsuperobject(23,30,17,"אלף אחד מפני גערת אחד מפני גערת חמשה תנסו עד אם נותרתם כתרן על ראש ההר וכנס על הגבעה׃"));
hebsuperobject.biblemap.put("b23_30_18", new hebsuperobject(23,30,18,"ולכן יחכה יהוה לחננכם ולכן ירום לרחמכם כי אלהי משפט יהוה אשרי כל חוכי לו׃"));
hebsuperobject.biblemap.put("b23_30_19", new hebsuperobject(23,30,19,"כי עם בציון ישב בירושלם בכו לא תבכה חנון יחנך לקול זעקך כשמעתו ענך׃"));
hebsuperobject.biblemap.put("b23_30_20", new hebsuperobject(23,30,20,"ונתן לכם אדני לחם צר ומים לחץ ולא יכנף עוד מוריך והיו עיניך ראות את מוריך׃"));
hebsuperobject.biblemap.put("b23_30_21", new hebsuperobject(23,30,21,"ואזניך תשמענה דבר מאחריך לאמר זה הדרך לכו בו כי תאמינו וכי תשמאילו׃"));
hebsuperobject.biblemap.put("b23_30_22", new hebsuperobject(23,30,22,"וטמאתם את צפוי פסילי כספך ואת אפדת מסכת זהבך תזרם כמו דוה צא תאמר לו׃"));
hebsuperobject.biblemap.put("b23_30_23", new hebsuperobject(23,30,23,"ונתן מטר זרעך אשר תזרע את האדמה ולחם תבואת האדמה והיה דשן ושמן ירעה מקניך ביום ההוא כר נרחב׃"));
hebsuperobject.biblemap.put("b23_30_24", new hebsuperobject(23,30,24,"והאלפים והעירים עבדי האדמה בליל חמיץ יאכלו אשר זרה ברחת ובמזרה׃"));
hebsuperobject.biblemap.put("b23_30_25", new hebsuperobject(23,30,25,"והיה על כל הר גבה ועל כל גבעה נשאה פלגים יבלי מים ביום הרג רב בנפל מגדלים׃"));
hebsuperobject.biblemap.put("b23_30_26", new hebsuperobject(23,30,26,"והיה אור הלבנה כאור החמה ואור החמה יהיה שבעתים כאור שבעת הימים ביום חבש יהוה את שבר עמו ומחץ מכתו ירפא׃"));
hebsuperobject.biblemap.put("b23_30_27", new hebsuperobject(23,30,27,"הנה שם יהוה בא ממרחק בער אפו וכבד משאה שפתיו מלאו זעם ולשונו כאש אכלת׃"));
hebsuperobject.biblemap.put("b23_30_28", new hebsuperobject(23,30,28,"ורוחו כנחל שוטף עד צואר יחצה להנפה גוים בנפת שוא ורסן מתעה על לחיי עמים׃"));
hebsuperobject.biblemap.put("b23_30_29", new hebsuperobject(23,30,29,"השיר יהיה לכם כליל התקדש חג ושמחת לבב כהולך בחליל לבוא בהר יהוה אל צור ישראל׃"));
hebsuperobject.biblemap.put("b23_30_30", new hebsuperobject(23,30,30,"והשמיע יהוה את הוד קולו ונחת זרועו יראה בזעף אף ולהב אש אוכלה נפץ וזרם ואבן ברד׃"));
hebsuperobject.biblemap.put("b23_30_31", new hebsuperobject(23,30,31,"כי מקול יהוה יחת אשור בשבט יכה׃"));
hebsuperobject.biblemap.put("b23_30_32", new hebsuperobject(23,30,32,"והיה כל מעבר מטה מוסדה אשר יניח יהוה עליו בתפים ובכנרות ובמלחמות תנופה נלחם בה׃"));
hebsuperobject.biblemap.put("b23_30_33", new hebsuperobject(23,30,33,"כי ערוך מאתמול תפתה גם הוא למלך הוכן העמיק הרחב מדרתה אש ועצים הרבה נשמת יהוה כנחל גפרית בערה בה׃"));
hebsuperobject.biblemap.put("b23_31_1", new hebsuperobject(23,31,1,"הוי הירדים מצרים לעזרה על סוסים ישענו ויבטחו על רכב כי רב ועל פרשים כי עצמו מאד ולא שעו על קדוש ישראל ואת יהוה לא דרשו׃"));
hebsuperobject.biblemap.put("b23_31_2", new hebsuperobject(23,31,2,"וגם הוא חכם ויבא רע ואת דבריו לא הסיר וקם על בית מרעים ועל עזרת פעלי און׃"));
hebsuperobject.biblemap.put("b23_31_3", new hebsuperobject(23,31,3,"ומצרים אדם ולא אל וסוסיהם בשר ולא רוח ויהוה יטה ידו וכשל עוזר ונפל עזר ויחדו כלם יכליון׃"));
hebsuperobject.biblemap.put("b23_31_4", new hebsuperobject(23,31,4,"כי כה אמר יהוה אלי כאשר יהגה האריה והכפיר על טרפו אשר יקרא עליו מלא רעים מקולם לא יחת ומהמונם לא יענה כן ירד יהוה צבאות לצבא על הר ציון ועל גבעתה׃"));
hebsuperobject.biblemap.put("b23_31_5", new hebsuperobject(23,31,5,"כצפרים עפות כן יגן יהוה צבאות על ירושלם גנון והציל פסח והמליט׃"));
hebsuperobject.biblemap.put("b23_31_6", new hebsuperobject(23,31,6,"שובו לאשר העמיקו סרה בני ישראל׃"));
hebsuperobject.biblemap.put("b23_31_7", new hebsuperobject(23,31,7,"כי ביום ההוא ימאסון איש אלילי כספו ואלילי זהבו אשר עשו לכם ידיכם חטא׃"));
hebsuperobject.biblemap.put("b23_31_8", new hebsuperobject(23,31,8,"ונפל אשור בחרב לא איש וחרב לא אדם תאכלנו ונס לו מפני חרב ובחוריו למס יהיו׃"));
hebsuperobject.biblemap.put("b23_31_9", new hebsuperobject(23,31,9,"וסלעו ממגור יעבור וחתו מנס שריו נאם יהוה אשר אור לו בציון ותנור לו בירושלם׃"));
hebsuperobject.biblemap.put("b23_32_1", new hebsuperobject(23,32,1,"הן לצדק ימלך מלך ולשרים למשפט ישרו׃"));
hebsuperobject.biblemap.put("b23_32_2", new hebsuperobject(23,32,2,"והיה איש כמחבא רוח וסתר זרם כפלגי מים בציון כצל סלע כבד בארץ עיפה׃"));
hebsuperobject.biblemap.put("b23_32_3", new hebsuperobject(23,32,3,"ולא תשעינה עיני ראים ואזני שמעים תקשבנה׃"));
hebsuperobject.biblemap.put("b23_32_4", new hebsuperobject(23,32,4,"ולבב נמהרים יבין לדעת ולשון עלגים תמהר לדבר צחות׃"));
hebsuperobject.biblemap.put("b23_32_5", new hebsuperobject(23,32,5,"לא יקרא עוד לנבל נדיב ולכילי לא יאמר שוע׃"));
hebsuperobject.biblemap.put("b23_32_6", new hebsuperobject(23,32,6,"כי נבל נבלה ידבר ולבו יעשה און לעשות חנף ולדבר אל יהוה תועה להריק נפש רעב ומשקה צמא יחסיר׃"));
hebsuperobject.biblemap.put("b23_32_7", new hebsuperobject(23,32,7,"וכלי כליו רעים הוא זמות יעץ לחבל ענוים באמרי שקר ובדבר אביון משפט׃"));
