hebsuperobject.biblemap.put("b23_44_2", new hebsuperobject(23,44,2,"כה אמר יהוה עשך ויצרך מבטן יעזרך אל תירא עבדי יעקב וישרון בחרתי בו׃"));
hebsuperobject.biblemap.put("b23_44_3", new hebsuperobject(23,44,3,"כי אצק מים על צמא ונזלים על יבשה אצק רוחי על זרעך וברכתי על צאצאיך׃"));
hebsuperobject.biblemap.put("b23_44_4", new hebsuperobject(23,44,4,"וצמחו בבין חציר כערבים על יבלי מים׃"));
