hebsuperobject.biblemap.put("b23_44_8", new hebsuperobject(23,44,8,"אל תפחדו ואל תרהו הלא מאז השמעתיך והגדתי ואתם עדי היש אלוה מבלעדי ואין צור בל ידעתי׃"));
hebsuperobject.biblemap.put("b23_44_9", new hebsuperobject(23,44,9,"יצרי פסל כלם תהו וחמודיהם בל יועילו ועדיהם המה בל יראו ובל ידעו למען יבשו׃"));
