hebsuperobject.biblemap.put("b23_51_22", new hebsuperobject(23,51,22,"כה אמר אדניך יהוה ואלהיך יריב עמו הנה לקחתי מידך את כוס התרעלה את קבעת כוס חמתי לא תוסיפי לשתותה עוד׃"));
hebsuperobject.biblemap.put("b23_51_23", new hebsuperobject(23,51,23,"ושמתיה ביד מוגיך אשר אמרו לנפשך שחי ונעברה ותשימי כארץ גוך וכחוץ לעברים׃"));
hebsuperobject.biblemap.put("b23_52_1", new hebsuperobject(23,52,1,"עורי עורי לבשי עזך ציון לבשי בגדי תפארתך ירושלם עיר הקדש כי לא יוסיף יבא בך עוד ערל וטמא׃"));
hebsuperobject.biblemap.put("b23_52_2", new hebsuperobject(23,52,2,"התנערי מעפר קומי שבי ירושלם התפתחו מוסרי צוארך שביה בת ציון׃"));
hebsuperobject.biblemap.put("b23_52_3", new hebsuperobject(23,52,3,"כי כה אמר יהוה חנם נמכרתם ולא בכסף תגאלו׃"));
hebsuperobject.biblemap.put("b23_52_4", new hebsuperobject(23,52,4,"כי כה אמר אדני יהוה מצרים ירד עמי בראשנה לגור שם ואשור באפס עשקו׃"));
