hebsuperobject.biblemap.put("b23_52_6", new hebsuperobject(23,52,6,"לכן ידע עמי שמי לכן ביום ההוא כי אני הוא המדבר הנני׃"));
hebsuperobject.biblemap.put("b23_52_7", new hebsuperobject(23,52,7,"מה נאוו על ההרים רגלי מבשר משמיע שלום מבשר טוב משמיע ישועה אמר לציון מלך אלהיך׃"));
hebsuperobject.biblemap.put("b23_52_8", new hebsuperobject(23,52,8,"קול צפיך נשאו קול יחדו ירננו כי עין בעין יראו בשוב יהוה ציון׃"));
hebsuperobject.biblemap.put("b23_52_9", new hebsuperobject(23,52,9,"פצחו רננו יחדו חרבות ירושלם כי נחם יהוה עמו גאל ירושלם׃"));
hebsuperobject.biblemap.put("b23_52_10", new hebsuperobject(23,52,10,"חשף יהוה את זרוע קדשו לעיני כל הגוים וראו כל אפסי ארץ את ישועת אלהינו׃"));
hebsuperobject.biblemap.put("b23_52_11", new hebsuperobject(23,52,11,"סורו סורו צאו משם טמא אל תגעו צאו מתוכה הברו נשאי כלי יהוה׃"));
hebsuperobject.biblemap.put("b23_52_12", new hebsuperobject(23,52,12,"כי לא בחפזון תצאו ובמנוסה לא תלכון כי הלך לפניכם יהוה ומאספכם אלהי ישראל׃"));
hebsuperobject.biblemap.put("b23_52_13", new hebsuperobject(23,52,13,"הנה ישכיל עבדי ירום ונשא וגבה מאד׃"));
hebsuperobject.biblemap.put("b23_52_14", new hebsuperobject(23,52,14,"כאשר שממו עליך רבים כן משחת מאיש מראהו ותארו מבני אדם׃"));
hebsuperobject.biblemap.put("b23_52_15", new hebsuperobject(23,52,15,"כן יזה גוים רבים עליו יקפצו מלכים פיהם כי אשר לא ספר להם ראו ואשר לא שמעו התבוננו׃"));
hebsuperobject.biblemap.put("b23_53_1", new hebsuperobject(23,53,1,"מי האמין לשמעתנו וזרוע יהוה על מי נגלתה׃"));
hebsuperobject.biblemap.put("b23_53_2", new hebsuperobject(23,53,2,"ויעל כיונק לפניו וכשרש מארץ ציה לא תאר לו ולא הדר ונראהו ולא מראה ונחמדהו׃"));
hebsuperobject.biblemap.put("b23_53_3", new hebsuperobject(23,53,3,"נבזה וחדל אישים איש מכאבות וידוע חלי וכמסתר פנים ממנו נבזה ולא חשבנהו׃"));
hebsuperobject.biblemap.put("b23_53_4", new hebsuperobject(23,53,4,"אכן חלינו הוא נשא ומכאבינו סבלם ואנחנו חשבנהו נגוע מכה אלהים ומענה׃"));
hebsuperobject.biblemap.put("b23_53_5", new hebsuperobject(23,53,5,"והוא מחלל מפשענו מדכא מעונתינו מוסר שלומנו עליו ובחברתו נרפא לנו׃"));
