hebsuperobject.biblemap.put("b23_53_7", new hebsuperobject(23,53,7,"נגש והוא נענה ולא יפתח פיו כשה לטבח יובל וכרחל לפני גזזיה נאלמה ולא יפתח פיו׃"));
hebsuperobject.biblemap.put("b23_53_8", new hebsuperobject(23,53,8,"מעצר וממשפט לקח ואת דורו מי ישוחח כי נגזר מארץ חיים מפשע עמי נגע למו׃"));
hebsuperobject.biblemap.put("b23_53_9", new hebsuperobject(23,53,9,"ויתן את רשעים קברו ואת עשיר במתיו על לא חמס עשה ולא מרמה בפיו׃"));
hebsuperobject.biblemap.put("b23_53_10", new hebsuperobject(23,53,10,"ויהוה חפץ דכאו החלי אם תשים אשם נפשו יראה זרע יאריך ימים וחפץ יהוה בידו יצלח׃"));
hebsuperobject.biblemap.put("b23_53_11", new hebsuperobject(23,53,11,"מעמל נפשו יראה ישבע בדעתו יצדיק צדיק עבדי לרבים ועונתם הוא יסבל׃"));
hebsuperobject.biblemap.put("b23_53_12", new hebsuperobject(23,53,12,"לכן אחלק לו ברבים ואת עצומים יחלק שלל תחת אשר הערה למות נפשו ואת פשעים נמנה והוא חטא רבים נשא ולפשעים יפגיע׃"));
hebsuperobject.biblemap.put("b23_54_1", new hebsuperobject(23,54,1,"רני עקרה לא ילדה פצחי רנה וצהלי לא חלה כי רבים בני שוממה מבני בעולה אמר יהוה׃"));
hebsuperobject.biblemap.put("b23_54_2", new hebsuperobject(23,54,2,"הרחיבי מקום אהלך ויריעות משכנותיך יטו אל תחשכי האריכי מיתריך ויתדתיך חזקי׃"));
hebsuperobject.biblemap.put("b23_54_3", new hebsuperobject(23,54,3,"כי ימין ושמאול תפרצי וזרעך גוים יירש וערים נשמות יושיבו׃"));
