hebsuperobject.biblemap.put("b23_54_5", new hebsuperobject(23,54,5,"כי בעליך עשיך יהוה צבאות שמו וגאלך קדוש ישראל אלהי כל הארץ יקרא׃"));
hebsuperobject.biblemap.put("b23_54_6", new hebsuperobject(23,54,6,"כי כאשה עזובה ועצובת רוח קראך יהוה ואשת נעורים כי תמאס אמר אלהיך׃"));
hebsuperobject.biblemap.put("b23_54_7", new hebsuperobject(23,54,7,"ברגע קטן עזבתיך וברחמים גדלים אקבצך׃"));
