hebsuperobject.biblemap.put("b23_54_9", new hebsuperobject(23,54,9,"כי מי נח זאת לי אשר נשבעתי מעבר מי נח עוד על הארץ כן נשבעתי מקצף עליך ומגער בך׃"));
hebsuperobject.biblemap.put("b23_54_10", new hebsuperobject(23,54,10,"כי ההרים ימושו והגבעות תמוטנה וחסדי מאתך לא ימוש וברית שלומי לא תמוט אמר מרחמך יהוה׃"));
hebsuperobject.biblemap.put("b23_54_11", new hebsuperobject(23,54,11,"עניה סערה לא נחמה הנה אנכי מרביץ בפוך אבניך ויסדתיך בספירים׃"));
