hebsuperobject.biblemap.put("b23_55_6", new hebsuperobject(23,55,6,"דרשו יהוה בהמצאו קראהו בהיותו קרוב׃"));
hebsuperobject.biblemap.put("b23_55_7", new hebsuperobject(23,55,7,"יעזב רשע דרכו ואיש און מחשבתיו וישב אל יהוה וירחמהו ואל אלהינו כי ירבה לסלוח׃"));
hebsuperobject.biblemap.put("b23_55_8", new hebsuperobject(23,55,8,"כי לא מחשבותי מחשבותיכם ולא דרכיכם דרכי נאם יהוה׃"));
hebsuperobject.biblemap.put("b23_55_9", new hebsuperobject(23,55,9,"כי גבהו שמים מארץ כן גבהו דרכי מדרכיכם ומחשבתי ממחשבתיכם׃"));
