hebsuperobject.biblemap.put("b23_55_11", new hebsuperobject(23,55,11,"כן יהיה דברי אשר יצא מפי לא ישוב אלי ריקם כי אם עשה את אשר חפצתי והצליח אשר שלחתיו׃"));
hebsuperobject.biblemap.put("b23_55_12", new hebsuperobject(23,55,12,"כי בשמחה תצאו ובשלום תובלון ההרים והגבעות יפצחו לפניכם רנה וכל עצי השדה ימחאו כף׃"));
hebsuperobject.biblemap.put("b23_55_13", new hebsuperobject(23,55,13,"תחת הנעצוץ יעלה ברוש תחת הסרפד יעלה הדס והיה ליהוה לשם לאות עולם לא יכרת׃"));
hebsuperobject.biblemap.put("b23_56_1", new hebsuperobject(23,56,1,"כה אמר יהוה שמרו משפט ועשו צדקה כי קרובה ישועתי לבוא וצדקתי להגלות׃"));
hebsuperobject.biblemap.put("b23_56_2", new hebsuperobject(23,56,2,"אשרי אנוש יעשה זאת ובן אדם יחזיק בה שמר שבת מחללו ושמר ידו מעשות כל רע׃"));
hebsuperobject.biblemap.put("b23_56_3", new hebsuperobject(23,56,3,"ואל יאמר בן הנכר הנלוה אל יהוה לאמר הבדל יבדילני יהוה מעל עמו ואל יאמר הסריס הן אני עץ יבש׃"));
hebsuperobject.biblemap.put("b23_56_4", new hebsuperobject(23,56,4,"כי כה אמר יהוה לסריסים אשר ישמרו את שבתותי ובחרו באשר חפצתי ומחזיקים בבריתי׃"));
hebsuperobject.biblemap.put("b23_56_5", new hebsuperobject(23,56,5,"ונתתי להם בביתי ובחומתי יד ושם טוב מבנים ומבנות שם עולם אתן לו אשר לא יכרת׃"));
hebsuperobject.biblemap.put("b23_56_6", new hebsuperobject(23,56,6,"ובני הנכר הנלוים על יהוה לשרתו ולאהבה את שם יהוה להיות לו לעבדים כל שמר שבת מחללו ומחזיקים בבריתי׃"));
hebsuperobject.biblemap.put("b23_56_7", new hebsuperobject(23,56,7,"והביאותים אל הר קדשי ושמחתים בבית תפלתי עולתיהם וזבחיהם לרצון על מזבחי כי ביתי בית תפלה יקרא לכל העמים׃"));
hebsuperobject.biblemap.put("b23_56_8", new hebsuperobject(23,56,8,"נאם אדני יהוה מקבץ נדחי ישראל עוד אקבץ עליו לנקבציו׃"));
