hebsuperobject.biblemap.put("b23_56_10", new hebsuperobject(23,56,10,"צפו עורים כלם לא ידעו כלם כלבים אלמים לא יוכלו לנבח הזים שכבים אהבי לנום׃"));
hebsuperobject.biblemap.put("b23_56_11", new hebsuperobject(23,56,11,"והכלבים עזי נפש לא ידעו שבעה והמה רעים לא ידעו הבין כלם לדרכם פנו איש לבצעו מקצהו׃"));
hebsuperobject.biblemap.put("b23_56_12", new hebsuperobject(23,56,12,"אתיו אקחה יין ונסבאה שכר והיה כזה יום מחר גדול יתר מאד׃"));
hebsuperobject.biblemap.put("b23_57_1", new hebsuperobject(23,57,1,"הצדיק אבד ואין איש שם על לב ואנשי חסד נאספים באין מבין כי מפני הרעה נאסף הצדיק׃"));
hebsuperobject.biblemap.put("b23_57_2", new hebsuperobject(23,57,2,"יבוא שלום ינוחו על משכבותם הלך נכחו׃"));
