hebsuperobject.biblemap.put("b23_57_4", new hebsuperobject(23,57,4,"על מי תתענגו על מי תרחיבו פה תאריכו לשון הלוא אתם ילדי פשע זרע שקר׃"));
hebsuperobject.biblemap.put("b23_57_5", new hebsuperobject(23,57,5,"הנחמים באלים תחת כל עץ רענן שחטי הילדים בנחלים תחת סעפי הסלעים׃"));
hebsuperobject.biblemap.put("b23_57_6", new hebsuperobject(23,57,6,"בחלקי נחל חלקך הם הם גורלך גם להם שפכת נסך העלית מנחה העל אלה אנחם׃"));
hebsuperobject.biblemap.put("b23_57_7", new hebsuperobject(23,57,7,"על הר גבה ונשא שמת משכבך גם שם עלית לזבח זבח׃"));
